import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';
import { FaUser, FaSignOutAlt } from 'react-icons/fa';
import { toast } from 'react-toastify';

function Room({ nickname }) {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [players, setPlayers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const socketRef = useRef();
  const chatMessagesRef = useRef();
  const isLeaving = useRef(false);

  useEffect(() => {
    socketRef.current = io('http://localhost:3001');

    // 소켓 이벤트 리스너 설정
    socketRef.current.on('roomInfo', (roomData) => {
      setRoom(roomData);
      setPlayers(roomData.players);
    });

    socketRef.current.on('error', ({ message }) => {
      toast.error(message);
      navigate('/');
    });

    socketRef.current.on('chatMessage', (message) => {
      if (message.roomId === roomId) {
        setMessages(prev => [...prev, message]);
      }
    });

    socketRef.current.on('playerJoined', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('playerLeft', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    // 방 참가 요청
    socketRef.current.emit('joinRoom', { roomId, nickname });

    // 클린업
    return () => {
      if (isLeaving.current) {
        socketRef.current.emit('leaveRoom', { roomId, nickname });
      }
      socketRef.current.disconnect();
    };
  }, [roomId, nickname, navigate]);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  const handleLeave = () => {
    isLeaving.current = true;
    navigate('/');
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      socketRef.current.emit('sendMessage', {
        roomId,
        nickname,
        message: inputMessage
      });
      setInputMessage('');
    }
  };

  if (!room) {
    return (
      <div className="container py-4">
        <div className="d-flex justify-content-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">로딩중...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <div className="d-flex align-items-center">
            <h2 className="mb-0" style={{ fontSize: '1.5rem' }}>
              {room.title}
            </h2>
          </div>
          <div className="d-flex gap-2">
            <button 
              className="btn btn-outline-danger"
              onClick={handleLeave}
            >
              <FaSignOutAlt className="me-2" />
              나가기
            </button>
          </div>
        </div>
        
        <div className="card-body">
          <div className="row">
            {/* 플레이어 목록 */}
            <div className="col-md-3">
              <div className="card mb-3">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">참가자 목록</h5>
                  <span className="badge bg-secondary">
                    {players.length}/{room.maxPlayers}명
                  </span>
                </div>
                <div className="card-body p-0">
                  <ul className="list-group list-group-flush">
                    {players.map(player => (
                      <li key={player.nickname} className="list-group-item d-flex align-items-center justify-content-between">
                        <div className="d-flex align-items-center">
                          <FaUser className="me-2 text-secondary" />
                          <span>{player.nickname}</span>
                          {player.nickname === players[0].nickname && (
                            <span className="badge bg-primary ms-2">방장</span>
                          )}
                        </div>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            {/* 채팅 영역 */}
            <div className="col-md-9">
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0">채팅</h5>
                </div>
                <div className="card-body p-0 d-flex flex-column" style={{ height: '500px' }}>
                  <div 
                    className="chat-messages p-3 flex-grow-1 overflow-auto"
                    ref={chatMessagesRef}
                  >
                    {messages.map((msg, index) => (
                      <div 
                        key={index} 
                        className={`mb-2 ${msg.nickname !== 'SYSTEM' && msg.nickname === nickname ? 'text-end' : ''}`}
                      >
                        {msg.type === 'system' ? (
                          <div className="text-center text-muted small my-2">
                            {msg.message}
                          </div>
                        ) : (
                          <div
                            className={`d-inline-block p-2 rounded-3 ${
                              msg.nickname === nickname 
                                ? 'bg-primary text-white' 
                                : 'bg-light'
                            }`}
                            style={{ maxWidth: '75%' }}
                          >
                            <div className="small fw-bold mb-1">
                              {msg.nickname}
                            </div>
                            <div style={{ wordBreak: 'break-word' }}>
                              {msg.message}
                            </div>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                  <form 
                    onSubmit={sendMessage} 
                    className="border-top p-2 mt-auto"
                  >
                    <div className="input-group">
                      <input
                        type="text"
                        className="form-control"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        placeholder="메시지를 입력하세요"
                      />
                      <button type="submit" className="btn btn-primary">
                        전송
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Room;
