import { useState, useEffect, useRef, useCallback } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import '../styles/ZombieDice.css';

// ÏäπÎ¶¨ Ï†êÏàò
const WINNING_SCORE = 13;

// Ï£ºÏÇ¨ÏúÑ Î©¥ Ï†ïÏùò
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'BRAIN' },      // 1Î©¥
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2Î©¥
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 3Î©¥
      { image: redShotgun, type: 'SHOTGUN' },   // 4Î©¥
      { image: redShotgun, type: 'SHOTGUN' },   // 5Î©¥
      { image: redShotgun, type: 'SHOTGUN' }    // 6Î©¥
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1Î©¥
      { image: yellowBrain, type: 'BRAIN' },     // 2Î©¥
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 3Î©¥
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 4Î©¥
      { image: yellowShotgun, type: 'SHOTGUN' },  // 5Î©¥
      { image: yellowShotgun, type: 'SHOTGUN' }   // 6Î©¥
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1Î©¥
      { image: greenBrain, type: 'BRAIN' },     // 2Î©¥
      { image: greenBrain, type: 'BRAIN' },     // 3Î©¥
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 4Î©¥
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5Î©¥
      { image: greenShotgun, type: 'SHOTGUN' }   // 6Î©¥
    ]
  }
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  const [dicePool, setDicePool] = useState([
    ...Array(3).fill('RED'),
    ...Array(4).fill('YELLOW'),
    ...Array(6).fill('GREEN')
  ]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [keptDice, setKeptDice] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const diceContainerRef = useRef(null);

  // Ï£ºÏÇ¨ÏúÑ 3Í∞ú ÎûúÎç§ ÏÑ†ÌÉù
  const pickDice = () => {
    const newDice = [];
    const tempPool = [...dicePool];
    
    for (let i = 0; i < 3; i++) {
      if (tempPool.length === 0) break;
      const randomIndex = Math.floor(Math.random() * tempPool.length);
      newDice.push(tempPool[randomIndex]);
      tempPool.splice(randomIndex, 1);
    }
    
    setDicePool(tempPool);
    setSelectedDice(newDice);
  };

  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞
  const handleRoll = () => {
    setIsRolling(true);
    
    // ÏÑúÎ≤ÑÏóê Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠
    if (window.socket) {
      // Room Ïª¥Ìè¨ÎÑåÌä∏ÏóêÏÑú roomId Í∞ÄÏ†∏Ïò§Í∏∞
      const roomId = window.location.pathname.split('/')[2]; // URLÏóêÏÑú roomId Ï∂îÏ∂ú
      
      console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ Ï†ÑÏÜ°:', {
        action: 'rollDice',
        roomId,
        selectedDice,
        dicePool,
        currentBrains,
        currentShotguns
      });
      
      window.socket.emit('gameAction', {
        action: 'rollDice',
        roomId, // roomId Ï∂îÍ∞Ä
        selectedDice: selectedDice,
        dicePool: dicePool,
        currentBrains: currentBrains,
        currentShotguns: currentShotguns
      });
    } else {
      console.error('ÏÜåÏºì Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§!');
      setIsRolling(false);
    }
    
    // ÏÑúÎ≤Ñ ÏùëÎãµÏù¥ ÏóÜÏùÑ Í≤ΩÏö∞Î•º ÎåÄÎπÑÌïú ÌÉÄÏûÑÏïÑÏõÉ
    setTimeout(() => {
      if (isRolling) {
        console.error('ÏÑúÎ≤Ñ ÏùëÎãµ ÌÉÄÏûÑÏïÑÏõÉ');
        setIsRolling(false);
      }
    }, 5000);
  };

  // Í≤∞Í≥º Ï≤òÎ¶¨ Ìï®Ïàò Î∂ÑÎ¶¨
  const processResults = (results) => {
    // Í≤∞Í≥º Í≥ÑÏÇ∞
    let newBrains = currentBrains;
    let newShotguns = currentShotguns;
    let newDicePool = [...dicePool];
    
    results.forEach(({ result, type }) => {
      if (result === 'BRAIN') newBrains++;
      if (result === 'SHOTGUN') newShotguns++;
      if (result === 'FOOTSTEPS') {
        newDicePool.push(type);
      }
    });

    setCurrentBrains(newBrains);
    setCurrentShotguns(newShotguns);
    setDicePool(newDicePool);

    if (newShotguns >= 3) {
      setTimeout(() => {
        onTurnEnd(0);
      }, 1000);
    }
  };

  // Í≥ÑÏÜç ÏßÑÌñâ
  const handleContinue = () => {
    setRolledResults([]);
    // ÎèÑÎßùÍ∞Ñ Ï£ºÏÇ¨ÏúÑÎ•º Ï†úÏô∏Ìïú ÎÇòÎ®∏ÏßÄÎäî Î≥¥Í¥Ä
    const keptDice = rolledResults
      .filter(result => result.result !== 'FOOTSTEPS')
      .map(result => result.type);
    
    // ÏÉàÎ°úÏö¥ Ï£ºÏÇ¨ÏúÑ ÏÑ†ÌÉù
    pickDice();
  };

  // ÌÑ¥ Ï¢ÖÎ£å
  const handleStop = () => {
    const finalScore = currentBrains;
    // Ï£ºÏÇ¨ÏúÑ ÌíÄ Ï¥àÍ∏∞Ìôî
    setDicePool([
      ...Array(3).fill('RED'),
      ...Array(4).fill('YELLOW'),
      ...Array(6).fill('GREEN')
    ]);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    setSelectedDice([]);
    onTurnEnd(finalScore);
  };

  // Ï†êÏàòÌåê Î†åÎçîÎßÅ
  const renderScoreBoard = () => (
    <div className="score-board">
      <table>
        <thead>
          <tr>
            <th>ÌîåÎ†àÏù¥Ïñ¥</th>
            <th>Ï†êÏàò</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => (
            <tr 
              key={player.nickname}
              className={index === currentTurn ? 'current-player' : ''}
            >
              <td>{player.nickname}</td>
              <td>{player.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  // ÏäπÏûêÍ∞Ä ÏûàÎäîÏßÄ ÌôïÏù∏
  const winner = players.find(player => player.score >= WINNING_SCORE);

  useEffect(() => {
    if (isMyTurn) {
      pickDice();
    }
  }, [isMyTurn]);

  // Three.jsÎ•º ÏÇ¨Ïö©Ìïú Ï£ºÏÇ¨ÏúÑ Ïª¥Ìè¨ÎÑåÌä∏
  function Dice({ type, result, isRolling }) {
    const diceRef = useRef(null);
    const animationRef = useRef(null);

    useEffect(() => {
      // Ïù¥Ï†Ñ Ï∫îÎ≤ÑÏä§ Ï†úÍ±∞
      while (diceRef.current && diceRef.current.firstChild) {
        diceRef.current.removeChild(diceRef.current.firstChild);
      }

      // Ïî¨ ÏÑ§Ï†ï
      const scene = new THREE.Scene();
      scene.background = new THREE.Color(0xf0f0f0);
      
      // Ïπ¥Î©îÎùº ÏÑ§Ï†ï
      const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
      camera.position.z = 2;
      
      // Î†åÎçîÎü¨ ÏÑ§Ï†ï
      const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
      renderer.setSize(150, 150);
      renderer.setPixelRatio(window.devicePixelRatio);
      if (diceRef.current) {
        diceRef.current.appendChild(renderer.domElement);
      }

      // Ï°∞Î™Ö Ï∂îÍ∞Ä
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
      scene.add(ambientLight);
      
      const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
      directionalLight.position.set(1, 1, 1);
      scene.add(directionalLight);

      // ÌÖçÏä§Ï≤ò Î°úÎçî ÏÉùÏÑ±
      const textureLoader = new THREE.TextureLoader();
      
      // Í≤∞Í≥º ÌÉÄÏûÖÏóê Îî∞Î•∏ Î©¥ Îß§Ìïë
      const resultIndex = DICE_FACES[type].faces.findIndex(face => face.type === result);
      
      // Ï£ºÏÇ¨ÏúÑ Î©¥ ÏàúÏÑú: +x, -x, +y, -y, +z, -z
      // Í∞Å Î©¥Ïóê ÎßûÎäî ÌÖçÏä§Ï≤ò Îß§Ìïë
      const materials = [];
      
      // Ï£ºÏÇ¨ÏúÑ ÌÉÄÏûÖÏóê Îî∞Î•∏ Î©¥ Ïù¥ÎØ∏ÏßÄ ÏÑ§Ï†ï
      let brainImage, footstepsImage, shotgunImage;
      
      switch(type) {
        case 'RED':
          brainImage = redBrain;
          footstepsImage = redFootsteps;
          shotgunImage = redShotgun;
          break;
        case 'YELLOW':
          brainImage = yellowBrain;
          footstepsImage = yellowFootsteps;
          shotgunImage = yellowShotgun;
          break;
        case 'GREEN':
          brainImage = greenBrain;
          footstepsImage = greenFootsteps;
          shotgunImage = greenShotgun;
          break;
      }
      
      // Í≤∞Í≥ºÏóê Îî∞Îùº ÏúÑÏ™Ω Î©¥ ÏÑ§Ï†ï
      let topFaceImage;
      switch(result) {
        case 'BRAIN':
          topFaceImage = brainImage;
          break;
        case 'FOOTSTEPS':
          topFaceImage = footstepsImage;
          break;
        case 'SHOTGUN':
          topFaceImage = shotgunImage;
          break;
      }
      
      // Í∞Å Î©¥Ïóê ÌÖçÏä§Ï≤ò Ï†ÅÏö©
      // ÏàúÏÑú: +x, -x, +y, -y, +z(ÏúÑ), -z(ÏïÑÎûò)
      const faceImages = [
        brainImage,       // +x
        footstepsImage,   // -x
        shotgunImage,     // +y
        brainImage,       // -y
        topFaceImage,     // +z (ÏúÑÏ™Ω Î©¥ - Í≤∞Í≥ºÏóê Îî∞Îùº Î≥ÄÍ≤Ω)
        footstepsImage    // -z
      ];
      
      // Í∞Å Î©¥Ïóê ÌÖçÏä§Ï≤ò Ï†ÅÏö©
      faceImages.forEach(image => {
        const texture = textureLoader.load(image);
        texture.colorSpace = THREE.SRGBColorSpace;
        materials.push(new THREE.MeshStandardMaterial({ 
          map: texture,
          roughness: 0.3,
          metalness: 0.2
        }));
      });
      
      // Ï£ºÏÇ¨ÏúÑ ÏßÄÏò§Î©îÌä∏Î¶¨ ÏÉùÏÑ±
      const geometry = new THREE.BoxGeometry(1, 1, 1);
      const dice = new THREE.Mesh(geometry, materials);
      scene.add(dice);
      
      // Ï¥àÍ∏∞ ÌöåÏ†Ñ ÏÑ§Ï†ï
      dice.rotation.set(
        Math.random() * Math.PI,
        Math.random() * Math.PI,
        Math.random() * Math.PI
      );
      
      // Ïï†ÎãàÎ©îÏù¥ÏÖò Ìï®Ïàò
      const animate = () => {
        animationRef.current = requestAnimationFrame(animate);
        
        if (isRolling) {
          // Íµ¥Îü¨Í∞ÄÎäî Ïï†ÎãàÎ©îÏù¥ÏÖò
          dice.rotation.x += 0.1;
          dice.rotation.y += 0.15;
          dice.rotation.z += 0.05;
        } else {
          // Í≤∞Í≥ºÏóê Îî∞Î•∏ ÌöåÏ†Ñ ÏÑ§Ï†ï - Ìï≠ÏÉÅ +z Î©¥Ïù¥ ÏúÑÎ°ú Ïò§ÎèÑÎ°ù
          const targetX = 0;
          const targetY = 0;
          const targetZ = 0;
          
          // Î∂ÄÎìúÎü¨Ïö¥ ÌöåÏ†Ñ Ïï†ÎãàÎ©îÏù¥ÏÖò
          dice.rotation.x += (targetX - dice.rotation.x) * 0.1;
          dice.rotation.y += (targetY - dice.rotation.y) * 0.1;
          dice.rotation.z += (targetZ - dice.rotation.z) * 0.1;
        }
        
        renderer.render(scene, camera);
      };
      
      animate();
      
      // ÌÅ¥Î¶∞ÏóÖ
      return () => {
        cancelAnimationFrame(animationRef.current);
        if (diceRef.current && renderer.domElement) {
          diceRef.current.removeChild(renderer.domElement);
        }
        geometry.dispose();
        materials.forEach(material => material.dispose());
        renderer.dispose();
      };
    }, [isRolling, result, type]);
    
    return (
      <div className="dice-result-container">
        <div ref={diceRef} className="dice-3d-container"></div>
        <div className="dice-result-label">
          {result === 'BRAIN' && <span className="result-brain">Îáå</span>}
          {result === 'SHOTGUN' && <span className="result-shotgun">Ï¥ù</span>}
          {result === 'FOOTSTEPS' && <span className="result-footsteps">Î∞úÏûêÍµ≠</span>}
        </div>
      </div>
    );
  }

  // ÏÜåÏºì Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà ÏàòÏ†ï
  useEffect(() => {
    if (window.socket) {
      console.log('ÏÜåÏºì Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù');
      
      // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º ÏàòÏã†
      const handleGameAction = (data) => {
        console.log('Í≤åÏûÑ Ïï°ÏÖò ÏàòÏã†:', data);
        
        if (data.action === 'rollDice') {
          console.log('Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º ÏàòÏã†:', data.results);
          
          // Í≤∞Í≥º ÌëúÏãú
          setRolledResults(data.results);
          
          // Ïï†ÎãàÎ©îÏù¥ÏÖò ÌõÑ Í≤∞Í≥º Ï≤òÎ¶¨
          setTimeout(() => {
            processResults(data.results);
            setIsRolling(false);
          }, 2500);
        }
      };
      
      window.socket.on('gameAction', handleGameAction);
      
      return () => {
        console.log('ÏÜåÏºì Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞');
        window.socket.off('gameAction', handleGameAction);
      };
    }
  }, []);

  if (!isMyTurn) {
    return (
      <div className="zombie-dice">
        {renderScoreBoard()}
        <div className="text-center mb-3">
          <h3>{players[currentTurn].nickname}ÎãòÏùò Ï∞®Î°ÄÏûÖÎãàÎã§</h3>
        </div>
        
        {rolledResults.length > 0 && (
          <>
            <div className="current-status mb-3">
              <div className="d-flex justify-content-around">
                <div>ÌöçÎìùÌïú Îáå: {currentBrains}</div>
                <div>ÏÉ∑Í±¥ ÌöüÏàò: {currentShotguns}/3</div>
              </div>
            </div>
            
            <div className="dice-area mb-3">
              {rolledResults.map((result, index) => (
                <Dice 
                  key={index}
                  type={result.type}
                  result={result.result}
                  isRolling={isRolling}
                />
              ))}
            </div>
          </>
        )}
      </div>
    );
  }

  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>üèÜ {winner.nickname}ÎãòÏù¥ ÏäπÎ¶¨ÌïòÏÖ®ÏäµÎãàÎã§! üèÜ</h3>
        </div>
      ) : (
        <>
          <div className="current-status mb-3">
            <div className="d-flex justify-content-around">
              <div>ÌöçÎìùÌïú Îáå: {currentBrains}</div>
              <div>ÏÉ∑Í±¥ ÌöüÏàò: {currentShotguns}/3</div>
            </div>
          </div>

          <div className="dice-area mb-3">
            {selectedDice.length > 0 && !rolledResults.length ? (
              <button 
                className="btn btn-primary"
                onClick={handleRoll}
                disabled={isRolling}
              >
                {isRolling ? 'Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Îäî Ï§ë...' : 'Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞'}
              </button>
            ) : (
              rolledResults.map((result, index) => (
                <Dice 
                  key={index}
                  type={result.type}
                  result={result.result}
                  isRolling={isRolling}
                />
              ))
            )}
          </div>

          {rolledResults.length > 0 && !winner && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                Í≥ÑÏÜçÌïòÍ∏∞
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                Î©àÏ∂îÍ∏∞
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default ZombieDice;
