import { useState, useEffect, useRef } from 'react';
import '../styles/ZombieDice.css';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';

// ìŠ¹ë¦¬ ì ìˆ˜
const WINNING_SCORE = 13;

// ì£¼ì‚¬ìœ„ ë©´ ì •ì˜
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'SHOTGUN' },      // 1ë©´
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2ë©´
      { image: redFootsteps, type: 'SHOTGUN' }, // 3ë©´
      { image: redShotgun, type: 'BRAIN' },   // 4ë©´
      { image: redShotgun, type: 'FOOTSTEPS' },   // 5ë©´
      { image: redShotgun, type: 'SHOTGUN' }    // 6ë©´
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1ë©´
      { image: yellowBrain, type: 'FOOTSTEPS' },     // 2ë©´
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 3ë©´
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 4ë©´
      { image: yellowShotgun, type: 'FOOTSTEPS' },  // 5ë©´
      { image: yellowShotgun, type: 'BRAIN' }   // 6ë©´
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1ë©´
      { image: greenBrain, type: 'FOOTSTEPS' },     // 2ë©´
      { image: greenBrain, type: 'BRAIN' },     // 3ë©´
      { image: greenFootsteps, type: 'SHOTGUN' }, // 4ë©´
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5ë©´
      { image: greenShotgun, type: 'SHOTGUN' }   // 6ë©´
    ]
  }
};

// ê°„ë‹¨í•œ 2D ì£¼ì‚¬ìœ„ ì»´í¬ë„ŒíŠ¸
const SimpleDice = ({ type, isRolling, result }) => {
  const diceRef = useRef(null);
  
  // ì£¼ì‚¬ìœ„ ìƒ‰ìƒ ì„¤ì •
  const getDiceColor = () => {
    if (type === 'RED') return '#ff5252';
    if (type === 'YELLOW') return '#ffeb3b';
    if (type === 'GREEN') return '#4caf50';
    return '#ffffff';
  };
  
  // ì•„ì´ì½˜ ì„¤ì •
  const getResultIcon = () => {
    if (!result) return '';
    
    if (result === 'BRAIN') return 'ğŸ§ ';
    if (result === 'SHOTGUN') return 'ğŸ”«';
    if (result === 'FOOTSTEPS') return 'ğŸ‘£';
    return '';
  };
  
  // ì• ë‹ˆë©”ì´ì…˜ íš¨ê³¼
  useEffect(() => {
    if (isRolling && diceRef.current) {
      // ëœë¤í•œ íšŒì „ ì• ë‹ˆë©”ì´ì…˜ ì ìš©
      const randomRotateX = Math.floor(Math.random() * 5 + 3) * 360;
      const randomRotateY = Math.floor(Math.random() * 5 + 3) * 360;
      const randomRotateZ = Math.floor(Math.random() * 5 + 3) * 360;
      
      diceRef.current.style.transition = 'transform 2s cubic-bezier(0.17, 0.84, 0.44, 1)';
      diceRef.current.style.transform = `rotateX(${randomRotateX}deg) rotateY(${randomRotateY}deg) rotateZ(${randomRotateZ}deg)`;
    }
  }, [isRolling]);
  
  return (
    <div 
      ref={diceRef} 
      className={`simple-dice ${isRolling ? 'rolling' : ''}`}
      style={{ 
        backgroundColor: getDiceColor(),
        transform: 'rotateX(0deg) rotateY(0deg) rotateZ(0deg)'
      }}
    >
      {!isRolling && result && (
        <div className="dice-result-icon">
          {getResultIcon()}
        </div>
      )}
    </div>
  );
};

// ì£¼ì‚¬ìœ„ ì• ë‹ˆë©”ì´ì…˜ ì˜ì—­ (ê°„ë‹¨í•œ 2D ë²„ì „)
const DiceAnimationArea = ({ selectedDice, isRolling, onAnimationComplete }) => {
  // ì• ë‹ˆë©”ì´ì…˜ ì™„ë£Œ ì²˜ë¦¬
  useEffect(() => {
    if (isRolling) {
      const timer = setTimeout(() => {
        // ì£¼ì‚¬ìœ„ ê²°ê³¼ ìƒì„±
        const results = selectedDice.map(diceType => {
          // ì£¼ì‚¬ìœ„ íƒ€ì…ì— ë”°ë¥¸ ë©´ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
          const diceFaces = DICE_FACES[diceType].faces;
          
          // ëœë¤í•˜ê²Œ ë©´ ì„ íƒ (0-5)
          const faceIndex = Math.floor(Math.random() * 6);
          const selectedFace = diceFaces[faceIndex];
          
          return {
            type: diceType,
            result: selectedFace.type,
            faceIndex: faceIndex
          };
        });
        
        if (onAnimationComplete) {
          onAnimationComplete(results);
        }
      }, 2500); // 2.5ì´ˆ í›„ ê²°ê³¼ ì²˜ë¦¬
      
      return () => clearTimeout(timer);
    }
  }, [isRolling, selectedDice, onAnimationComplete]);
  
  return (
    <div className="simple-dice-animation-area">
      <div className="dice-container">
        {selectedDice.map((diceType, index) => (
          <SimpleDice 
            key={index}
            type={diceType}
            isRolling={isRolling}
          />
        ))}
      </div>
    </div>
  );
};

// ì£¼ì‚¬ìœ„ í’€ ìƒíƒœ ì»´í¬ë„ŒíŠ¸
const DicePoolStatus = ({ dicePool }) => {
  // ìƒ‰ìƒë³„ ì£¼ì‚¬ìœ„ ì¹´ìš´íŠ¸
  const redCount = dicePool.filter(dice => dice === 'RED').length;
  const yellowCount = dicePool.filter(dice => dice === 'YELLOW').length;
  const greenCount = dicePool.filter(dice => dice === 'GREEN').length;
  
  return (
    <div className="dice-pool-status">
      <h4>ë‚¨ì€ ì£¼ì‚¬ìœ„</h4>
      <div className="dice-counts">
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#ff5252' }}></div>
          <span>{redCount}ê°œ</span>
        </div>
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#ffeb3b' }}></div>
          <span>{yellowCount}ê°œ</span>
        </div>
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#4caf50' }}></div>
          <span>{greenCount}ê°œ</span>
        </div>
      </div>
    </div>
  );
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  // ìƒíƒœ ì •ì˜
  const [dicePool, setDicePool] = useState([]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [isRolling, setIsRolling] = useState(false);
  const [winner, setWinner] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [showDiceAnimation, setShowDiceAnimation] = useState(false);
  
  // ì ìˆ˜íŒ ë Œë”ë§
  const renderScoreBoard = () => {
    if (!players) return null;
    
    return (
      <div className="score-board">
        <table className="table">
          <thead>
            <tr>
              <th>í”Œë ˆì´ì–´</th>
              <th>ì ìˆ˜</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(players).map(([id, player]) => (
              <tr 
                key={id} 
                className={parseInt(id) === currentTurn ? 'current-player' : ''}
              >
                <td>{player.nickname}</td>
                <td>{player.score || 0}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
  
  // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìš”ì²­
  const handleRoll = () => {
    console.log('ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìš”ì²­');
    
    // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìƒíƒœ ì„¤ì •
    setIsRolling(true);
    setShowDiceAnimation(true);
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      // ì£¼ì‚¬ìœ„ ì„ íƒ ìš”ì²­ - ì„œë²„ì— ì „ì†¡í•˜ì—¬ ëª¨ë“  í”Œë ˆì´ì–´ê°€ ë³¼ ìˆ˜ ìˆê²Œ í•¨
      window.socket.emit('gameAction', {
        action: 'selectDice',
        roomId,
        requestRoll: true // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸°ë„ ìš”ì²­
      });
    } else {
      console.error('ì†Œì¼“ ì—°ê²°ì´ ì—†ìŠµë‹ˆë‹¤!');
      setIsRolling(false);
      setShowDiceAnimation(false);
    }
  };
  
  // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ê²°ê³¼ ì²˜ë¦¬
  const handleRollComplete = (results) => {
    console.log('ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ì™„ë£Œ:', results);
    
    // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìƒíƒœ ì¢…ë£Œ
    setIsRolling(false);
    
    // ì• ë‹ˆë©”ì´ì…˜ ì»¨í…Œì´ë„ˆëŠ” ê²°ê³¼ê°€ í‘œì‹œëœ í›„ ì¼ì • ì‹œê°„ ì§€ë‚˜ì„œ ìˆ¨ê¹€
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000);
    
    if (!results || results.length === 0) {
      // ê²°ê³¼ê°€ ì—†ëŠ” ê²½ìš° (ì˜¤ë¥˜)
      return;
    }
    
    // ì£¼ì‚¬ìœ„ ê²°ê³¼ ì €ì¥
    setRolledResults(results);
    
    // ë‡Œì™€ ì´ ê°œìˆ˜ ì§‘ê³„
    let brains = currentBrains;
    let shotguns = currentShotguns;
    
    results.forEach(result => {
      if (result.result === 'BRAIN') {
        brains++;
      } else if (result.result === 'SHOTGUN') {
        shotguns++;
      }
    });
    
    setCurrentBrains(brains);
    setCurrentShotguns(shotguns);
    
    // ê²Œì„ ì•¡ì…˜ ì„œë²„ì— ì „ì†¡
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'rollDiceResult',
        roomId,
        results,
        brains,
        shotguns
      });
    }
  };
  
  // ê³„ì†í•˜ê¸° ì²˜ë¦¬
  const handleContinue = () => {
    if (currentShotguns >= 3) {
      // ì´ 3ê°œ ì´ìƒì´ë©´ ìë™ìœ¼ë¡œ í„´ ì¢…ë£Œ
      handleStop();
      return;
    }
    
    // ëŒë¦´ ìˆ˜ ìˆëŠ” ì£¼ì‚¬ìœ„ê°€ ì—†ìœ¼ë©´ í„´ ì¢…ë£Œ
    if (dicePool.length === 0) {
      handleStop();
      return;
    }
    
    // ë‹¤ì‹œ ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸°
    handleRoll();
  };
  
  // ë©ˆì¶”ê¸° ì²˜ë¦¬
  const handleStop = () => {
    const score = currentShotguns >= 3 ? 0 : currentBrains;
    
    // ì„œë²„ì— í„´ ì¢…ë£Œ ì „ì†¡
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'endTurn',
        roomId,
        score
      });
    }
    
    // í„´ ì¢…ë£Œ ì½œë°± í˜¸ì¶œ
    if (onTurnEnd) {
      onTurnEnd(score);
    }
  };
  
