import { useState, useEffect, useRef } from 'react';
import '../styles/ZombieDice.css';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';

// ÏäπÎ¶¨ Ï†êÏàò
const WINNING_SCORE = 13;

// Ï£ºÏÇ¨ÏúÑ Î©¥ Ï†ïÏùò
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'SHOTGUN' },      // 1Î©¥
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2Î©¥
      { image: redFootsteps, type: 'SHOTGUN' }, // 3Î©¥
      { image: redShotgun, type: 'BRAIN' },   // 4Î©¥
      { image: redShotgun, type: 'FOOTSTEPS' },   // 5Î©¥
      { image: redShotgun, type: 'SHOTGUN' }    // 6Î©¥
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1Î©¥
      { image: yellowBrain, type: 'FOOTSTEPS' },     // 2Î©¥
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 3Î©¥
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 4Î©¥
      { image: yellowShotgun, type: 'FOOTSTEPS' },  // 5Î©¥
      { image: yellowShotgun, type: 'BRAIN' }   // 6Î©¥
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1Î©¥
      { image: greenBrain, type: 'FOOTSTEPS' },     // 2Î©¥
      { image: greenBrain, type: 'BRAIN' },     // 3Î©¥
      { image: greenFootsteps, type: 'SHOTGUN' }, // 4Î©¥
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5Î©¥
      { image: greenShotgun, type: 'SHOTGUN' }   // 6Î©¥
    ]
  }
};

// Í∞ÑÎã®Ìïú 2D Ï£ºÏÇ¨ÏúÑ Ïª¥Ìè¨ÎÑåÌä∏
const SimpleDice = ({ type, isRolling, result }) => {
  const diceRef = useRef(null);
  
  // Ï£ºÏÇ¨ÏúÑ ÏÉâÏÉÅ ÏÑ§Ï†ï
  const getDiceColor = () => {
    if (type === 'RED') return '#ff5252';
    if (type === 'YELLOW') return '#ffeb3b';
    if (type === 'GREEN') return '#4caf50';
    return '#ffffff';
  };
  
  // ÏïÑÏù¥ÏΩò ÏÑ§Ï†ï
  const getResultIcon = () => {
    if (!result) return '';
    
    if (result === 'BRAIN') return 'üß†';
    if (result === 'SHOTGUN') return 'üî´';
    if (result === 'FOOTSTEPS') return 'üë£';
    return '';
  };
  
  // Ïï†ÎãàÎ©îÏù¥ÏÖò Ìö®Í≥º
  useEffect(() => {
    if (isRolling && diceRef.current) {
      // ÎûúÎç§Ìïú ÌöåÏ†Ñ Ïï†ÎãàÎ©îÏù¥ÏÖò Ï†ÅÏö©
      const randomRotateX = Math.floor(Math.random() * 5 + 3) * 360;
      const randomRotateY = Math.floor(Math.random() * 5 + 3) * 360;
      const randomRotateZ = Math.floor(Math.random() * 5 + 3) * 360;
      
      diceRef.current.style.transition = 'transform 2s cubic-bezier(0.17, 0.84, 0.44, 1)';
      diceRef.current.style.transform = `rotateX(${randomRotateX}deg) rotateY(${randomRotateY}deg) rotateZ(${randomRotateZ}deg)`;
    }
  }, [isRolling]);
  
  return (
    <div 
      ref={diceRef} 
      className={`simple-dice ${isRolling ? 'rolling' : ''}`}
      style={{ 
        backgroundColor: getDiceColor(),
        transform: 'rotateX(0deg) rotateY(0deg) rotateZ(0deg)'
      }}
    >
      {!isRolling && result && (
        <div className="dice-result-icon">
          {getResultIcon()}
        </div>
      )}
    </div>
  );
};

// Ï£ºÏÇ¨ÏúÑ Ïï†ÎãàÎ©îÏù¥ÏÖò ÏòÅÏó≠ (Í∞ÑÎã®Ìïú 2D Î≤ÑÏ†Ñ)
const DiceAnimationArea = ({ selectedDice, isRolling, onAnimationComplete }) => {
  // Ïï†ÎãàÎ©îÏù¥ÏÖò ÏôÑÎ£å Ï≤òÎ¶¨
  useEffect(() => {
    if (isRolling) {
      const timer = setTimeout(() => {
        // Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º ÏÉùÏÑ±
        const results = selectedDice.map(diceType => {
          // Ï£ºÏÇ¨ÏúÑ ÌÉÄÏûÖÏóê Îî∞Î•∏ Î©¥ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
          const diceFaces = DICE_FACES[diceType].faces;
          
          // ÎûúÎç§ÌïòÍ≤å Î©¥ ÏÑ†ÌÉù (0-5)
          const faceIndex = Math.floor(Math.random() * 6);
          const selectedFace = diceFaces[faceIndex];
          
          return {
            type: diceType,
            result: selectedFace.type,
            faceIndex: faceIndex
          };
        });
        
        if (onAnimationComplete) {
          onAnimationComplete(results);
        }
      }, 2500); // 2.5Ï¥à ÌõÑ Í≤∞Í≥º Ï≤òÎ¶¨
      
      return () => clearTimeout(timer);
    }
  }, [isRolling, selectedDice, onAnimationComplete]);
  
  return (
    <div className="simple-dice-animation-area">
      <div className="dice-container">
        {selectedDice.map((diceType, index) => (
          <SimpleDice 
            key={index}
            type={diceType}
            isRolling={isRolling}
          />
        ))}
      </div>
    </div>
  );
};

// Ï£ºÏÇ¨ÏúÑ ÌíÄ ÏÉÅÌÉú Ïª¥Ìè¨ÎÑåÌä∏
const DicePoolStatus = ({ dicePool }) => {
  // ÏÉâÏÉÅÎ≥Ñ Ï£ºÏÇ¨ÏúÑ Ïπ¥Ïö¥Ìä∏
  const redCount = dicePool.filter(dice => dice === 'RED').length;
  const yellowCount = dicePool.filter(dice => dice === 'YELLOW').length;
  const greenCount = dicePool.filter(dice => dice === 'GREEN').length;
  
  return (
    <div className="dice-pool-status">
      <h4>ÎÇ®ÏùÄ Ï£ºÏÇ¨ÏúÑ</h4>
      <div className="dice-counts">
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#ff5252' }}></div>
          <span>{redCount}Í∞ú</span>
        </div>
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#ffeb3b' }}></div>
          <span>{yellowCount}Í∞ú</span>
        </div>
        <div className="dice-count">
          <div className="dice-color" style={{ backgroundColor: '#4caf50' }}></div>
          <span>{greenCount}Í∞ú</span>
        </div>
      </div>
    </div>
  );
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  // ÏÉÅÌÉú Ï†ïÏùò
  const [dicePool, setDicePool] = useState([]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [isRolling, setIsRolling] = useState(false);
  const [winner, setWinner] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [showDiceAnimation, setShowDiceAnimation] = useState(false);
  
  // Ï†êÏàòÌåê Î†åÎçîÎßÅ
  const renderScoreBoard = () => {
    if (!players) return null;
    
    return (
      <div className="score-board">
        <table className="table">
          <thead>
            <tr>
              <th>ÌîåÎ†àÏù¥Ïñ¥</th>
              <th>Ï†êÏàò</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(players).map(([id, player]) => (
              <tr 
                key={id} 
                className={parseInt(id) === currentTurn ? 'current-player' : ''}
              >
                <td>{player.nickname}</td>
                <td>{player.score || 0}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
  
  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠
  const handleRoll = () => {
    console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠');
    
    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏÉÅÌÉú ÏÑ§Ï†ï
    setIsRolling(true);
    setShowDiceAnimation(true);
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      // Ï£ºÏÇ¨ÏúÑ ÏÑ†ÌÉù ÏöîÏ≤≠ - ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ°ÌïòÏó¨ Î™®Îì† ÌîåÎ†àÏù¥Ïñ¥Í∞Ä Î≥º Ïàò ÏûàÍ≤å Ìï®
      window.socket.emit('gameAction', {
        action: 'selectDice',
        roomId,
        requestRoll: true // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ÎèÑ ÏöîÏ≤≠
      });
    } else {
      console.error('ÏÜåÏºì Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§!');
      setIsRolling(false);
      setShowDiceAnimation(false);
    }
  };
  
  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º Ï≤òÎ¶¨
  const handleRollComplete = (results) => {
    console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏôÑÎ£å:', results);
    
    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏÉÅÌÉú Ï¢ÖÎ£å
    setIsRolling(false);
    
    // Ïï†ÎãàÎ©îÏù¥ÏÖò Ïª®ÌÖåÏù¥ÎÑàÎäî Í≤∞Í≥ºÍ∞Ä ÌëúÏãúÎêú ÌõÑ ÏùºÏ†ï ÏãúÍ∞Ñ ÏßÄÎÇòÏÑú Ïà®ÍπÄ
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000);
    
    if (!results || results.length === 0) {
      // Í≤∞Í≥ºÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ (Ïò§Î•ò)
      return;
    }
    
    // Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º Ï†ÄÏû•
    setRolledResults(results);
    
    // ÎáåÏôÄ Ï¥ù Í∞úÏàò ÏßëÍ≥Ñ
    let brains = currentBrains;
    let shotguns = currentShotguns;
    
    results.forEach(result => {
      if (result.result === 'BRAIN') {
        brains++;
      } else if (result.result === 'SHOTGUN') {
        shotguns++;
      }
    });
    
    setCurrentBrains(brains);
    setCurrentShotguns(shotguns);
    
    // Í≤åÏûÑ Ïï°ÏÖò ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ°
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'rollDiceResult',
        roomId,
        results,
        brains,
        shotguns
      });
    }
  };
  
  // Í≥ÑÏÜçÌïòÍ∏∞ Ï≤òÎ¶¨
  const handleContinue = () => {
    if (currentShotguns >= 3) {
      // Ï¥ù 3Í∞ú Ïù¥ÏÉÅÏù¥Î©¥ ÏûêÎèôÏúºÎ°ú ÌÑ¥ Ï¢ÖÎ£å
      handleStop();
      return;
    }
    
    // ÎèåÎ¶¥ Ïàò ÏûàÎäî Ï£ºÏÇ¨ÏúÑÍ∞Ä ÏóÜÏúºÎ©¥ ÌÑ¥ Ï¢ÖÎ£å
    if (dicePool.length === 0) {
      handleStop();
      return;
    }
    
    // Îã§Ïãú Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞
    handleRoll();
  };
  
  // Î©àÏ∂îÍ∏∞ Ï≤òÎ¶¨
  const handleStop = () => {
    const score = currentShotguns >= 3 ? 0 : currentBrains;
    
    // ÏÑúÎ≤ÑÏóê ÌÑ¥ Ï¢ÖÎ£å Ï†ÑÏÜ°
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'endTurn',
        roomId,
        score
      });
    }
    
    // ÌÑ¥ Ï¢ÖÎ£å ÏΩúÎ∞± Ìò∏Ï∂ú
    if (onTurnEnd) {
      onTurnEnd(score);
    }
  };
  
  // ÏÜåÏºì Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨
  const handleDiceSelected = (data) => {
    console.log('Ï£ºÏÇ¨ÏúÑ ÏÑ†ÌÉùÎê®:', data);
    const { selectedDice: newSelectedDice, dicePool: newDicePool, requestRoll } = data;
    
    setSelectedDice(newSelectedDice);
    setDicePool(newDicePool);
    
    if (requestRoll) {
      // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Ïï†ÎãàÎ©îÏù¥ÏÖò ÏãúÏûë
      setShowDiceAnimation(true);
      setIsRolling(true);
    }
  };
  
  const handleDiceRolling = (data) => {
    console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Îäî Ï§ë:', data);
    setShowDiceAnimation(true);
    setIsRolling(true);
  };
  
  const handleRollDiceResult = (data) => {
    console.log('Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º ÏàòÏã†:', data);
    
    setIsRolling(false);
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000);
    
    const { results, brains, shotguns } = data;
    
    if (results) {
      setRolledResults(results);
      setCurrentBrains(brains);
      setCurrentShotguns(shotguns);
    }
  };
  
  const handleTurnStarted = (data) => {
    console.log('ÌÑ¥ ÏãúÏûëÎê®:', data);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
  };
  
  const handleTurnEnded = (data) => {
    console.log('ÌÑ¥ Ï¢ÖÎ£åÎê®:', data);
  };
  
  const handleGameEnded = (data) => {
    console.log('Í≤åÏûÑ Ï¢ÖÎ£åÎê®:', data);
    setWinner(data.winner);
  };
  
  // ÏÜåÏºì Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Îì±Î°ù
  useEffect(() => {
    if (window.socket) {
      window.socket.on('diceSelected', handleDiceSelected);
      window.socket.on('diceRolling', handleDiceRolling);
      window.socket.on('rollDiceResult', handleRollDiceResult);
      window.socket.on('turnStarted', handleTurnStarted);
      window.socket.on('turnEnded', handleTurnEnded);
      window.socket.on('gameEnded', handleGameEnded);
      
      return () => {
        window.socket.off('turnStarted', handleTurnStarted);
        window.socket.off('diceSelected', handleDiceSelected);
        window.socket.off('rollDiceResult', handleRollDiceResult);
        window.socket.off('turnEnded', handleTurnEnded);
        window.socket.off('gameEnded', handleGameEnded);
        window.socket.off('diceRolling', handleDiceRolling);
      };
    }
  }, [onTurnEnd, isMyTurn, isRolling]);
  
  // Î©îÏù∏ Î†åÎçîÎßÅ
  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>üèÜ {winner.nickname}ÎãòÏù¥ ÏäπÎ¶¨ÌïòÏÖ®ÏäµÎãàÎã§! üèÜ</h3>
        </div>
      ) : (
        <>
          {isMyTurn ? (
            <>
              <div className="text-center mb-3">
                <h3 className="my-turn-indicator">ÎãπÏã†Ïùò Ï∞®Î°ÄÏûÖÎãàÎã§!</h3>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>ÌöçÎìùÌïú Îáå: {currentBrains}</div>
                  <div>ÏÉ∑Í±¥ ÌöüÏàò: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation ? (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Îäî Ï§ë...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={handleRollComplete}
                  />
                </div>
              ) : (
                <div className="dice-area mb-3">
                  {isRolling ? (
                    <div className="text-center">
                      <p>Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Îäî Ï§ë...</p>
                    </div>
                  ) : (
                    rolledResults.length > 0 ? (
                      <div className="rolled-results">
                        <h4>Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º:</h4>
                        <div className="dice-results-container">
                          {rolledResults.map((result, index) => (
                            <div key={index} className={`dice-result ${result.result.toLowerCase()}`}>
                              <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                              <div className="dice-face">
                                {result.result === 'BRAIN' && 'Îáå'}
                                {result.result === 'SHOTGUN' && 'Ï¥ù'}
                                {result.result === 'FOOTSTEPS' && 'Î∞úÏûêÍµ≠'}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ) : (
                      <button 
                        className="btn btn-primary btn-lg"
                        onClick={handleRoll}
                        disabled={isRolling}
                      >
                        Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞
                      </button>
                    )
                  )}
                </div>
              )}
            </>
          ) : (
            <>
              <div className="text-center mb-3">
                <h3>Îã§Î•∏ ÌîåÎ†àÏù¥Ïñ¥Ïùò Ï∞®Î°ÄÏûÖÎãàÎã§.</h3>
                <p>{players[currentTurn]?.nickname}ÎãòÏù¥ Ï£ºÏÇ¨ÏúÑÎ•º Íµ¥Î¶¨Í≥† ÏûàÏäµÎãàÎã§...</p>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>ÌöçÎìùÌïú Îáå: {currentBrains}</div>
                  <div>ÏÉ∑Í±¥ ÌöüÏàò: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation && (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Îäî Ï§ë...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={null} // Í¥ÄÏ†ÑÏûêÎäî Í≤∞Í≥º Ï≤òÎ¶¨ÌïòÏßÄ ÏïäÏùå
                  />
                </div>
              )}
              
              {rolledResults.length > 0 && !isRolling && !showDiceAnimation && (
                <div className="rolled-results">
                  <h4>Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º:</h4>
                  <div className="dice-results-container">
                    {rolledResults.map((result, index) => (
                      <div key={index} className={`dice-result ${result.result.toLowerCase()}`}>
                        <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                        <div className="dice-face">
                          {result.result === 'BRAIN' && 'Îáå'}
                          {result.result === 'SHOTGUN' && 'Ï¥ù'}
                          {result.result === 'FOOTSTEPS' && 'Î∞úÏûêÍµ≠'}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
          
          {rolledResults.length > 0 && !winner && !isRolling && !showDiceAnimation && isMyTurn && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                Í≥ÑÏÜçÌïòÍ∏∞
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                {currentShotguns >= 3 ? "ÌÑ¥ Ï¢ÖÎ£å (0Ï†ê)" : "Î©àÏ∂îÍ∏∞"}
              </button>
            </div>
          )}
          
          {currentShotguns >= 3 && (
            <div className="alert alert-danger mt-3">
              <strong>Ï¥ùÏù¥ 3Í∞ú Ïù¥ÏÉÅ!</strong> Ïù¥Î≤à ÌÑ¥Ïóê ÌöçÎìùÌïú ÎáåÎ•º Î™®Îëê ÏûÉÏóàÏäµÎãàÎã§. ÌÑ¥ÏùÑ Ï¢ÖÎ£åÌï¥Ï£ºÏÑ∏Ïöî.
            </div>
          )}
          
          <DicePoolStatus dicePool={dicePool} />
        </>
      )}
    </div>
  );
}

export default ZombieDice;
