import { useState, useEffect, useRef, Suspense } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { Physics, useBox, usePlane } from '@react-three/cannon';
import * as THREE from 'three';
import { TextureLoader } from 'three';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import '../styles/ZombieDice.css';

// 승리 점수
const WINNING_SCORE = 13;

// 주사위 면 정의
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'SHOTGUN' },      // 1면
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2면
      { image: redFootsteps, type: 'SHOTGUN' }, // 3면
      { image: redShotgun, type: 'BRAIN' },   // 4면
      { image: redShotgun, type: 'FOOTSTEPS' },   // 5면
      { image: redShotgun, type: 'SHOTGUN' }    // 6면
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1면
      { image: yellowBrain, type: 'FOOTSTEPS' },     // 2면
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 3면
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 4면
      { image: yellowShotgun, type: 'FOOTSTEPS' },  // 5면
      { image: yellowShotgun, type: 'BRAIN' }   // 6면
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1면
      { image: greenBrain, type: 'FOOTSTEPS' },     // 2면
      { image: greenBrain, type: 'BRAIN' },     // 3면
      { image: greenFootsteps, type: 'SHOTGUN' }, // 4면
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5면
      { image: greenShotgun, type: 'SHOTGUN' }   // 6면
    ]
  }
};

// 주사위 색상 가져오기
const getDiceColor = (type) => {
  if (type === 'RED') return 0xff0000;
  if (type === 'YELLOW') return 0xffff00;
  if (type === 'GREEN') return 0x00ff00;
  return 0xffffff;
};

// 주사위 타입에 따른 텍스처 가져오기
const getTexturesForType = (type) => {
  // DICE_FACES에서 해당 타입의 주사위 면 정보 가져오기
  const faces = DICE_FACES[type]?.faces || [];
  
  // 6개 면의 이미지를 배열로 반환
  return faces.map(face => face.image);
};

// 결과에 맞게 주사위 회전 조정
const adjustDiceRotation = (result, faceIndex, api, ref) => {
  // 주사위 면에 따른 회전 설정
  // 주사위 큐브에서 각 면의 인덱스:
  // 0: +x (오른쪽), 1: -x (왼쪽)
  // 2: +y (위), 3: -y (아래)
  // 4: +z (앞), 5: -z (뒤)
  
  let targetRotation = [0, 0, 0];
  
  switch(faceIndex) {
    case 0: // +x (오른쪽) 면이 위로
      targetRotation = [0, 0, -Math.PI/2];
      break;
    case 1: // -x (왼쪽) 면이 위로
      targetRotation = [0, 0, Math.PI/2];
      break;
    case 2: // +y (위) 면이 위로
      targetRotation = [0, 0, 0];
      break;
    case 3: // -y (아래) 면이 위로
      targetRotation = [Math.PI, 0, 0];
      break;
    case 4: // +z (앞) 면이 위로
      targetRotation = [Math.PI/2, 0, 0];
      break;
    case 5: // -z (뒤) 면이 위로
      targetRotation = [-Math.PI/2, 0, 0];
      break;
    default:
      // 기본값 - 위 면이 위로
      targetRotation = [0, 0, 0];
  }
  
  // 주사위 회전 설정
  if (ref.current) {
    ref.current.rotation.x = targetRotation[0];
    ref.current.rotation.y = targetRotation[1];
    ref.current.rotation.z = targetRotation[2];
  }
  
  // 물리 속성 정지
  api.velocity.set(0, 0, 0);
  api.angularVelocity.set(0, 0, 0);
};

// 결과 타입에 따른 클래스 이름 가져오기
const getResultClass = (result) => {
  if (result === 'BRAIN') return 'result-brain';
  if (result === 'SHOTGUN') return 'result-shotgun';
  if (result === 'FOOTSTEPS') return 'result-footsteps';
  return '';
};

// 모달 스타일을 상수로 컴포넌트 외부에 정의
const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '80%',
  maxWidth: '600px',
  backgroundColor: 'white',
  borderRadius: '8px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  zIndex: 1000,
  padding: '20px'
};

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: 'rgba(0, 0, 0, 0.5)',
  zIndex: 999
};

// 바닥 컴포넌트
const Floor = () => {
  const [ref] = usePlane(() => ({ 
    rotation: [-Math.PI / 2, 0, 0],
    position: [0, 0, 0],
    type: 'Static'
  }));
  
  return (
    <mesh ref={ref} receiveShadow>
      <planeGeometry args={[20, 20]} />
      <meshStandardMaterial color="#e0e0e0" roughness={0.7} metalness={0.1} />
    </mesh>
  );
};

// 벽 컴포넌트 추가 (주사위가 화면 밖으로 나가지 않도록)
const Wall = ({ position, rotation }) => {
  const [ref] = usePlane(() => ({ 
    position,
    rotation,
    type: 'Static'
  }));
  
  return (
    <mesh ref={ref} receiveShadow>
      <planeGeometry args={[20, 8]} />
      <meshStandardMaterial color="#f5f5f5" transparent opacity={0.2} />
    </mesh>
  );
};

// 물리 기반 주사위 컴포넌트
const PhysicalDice = ({ type, result, isRolling, position }) => {
  // 주사위 크기
  const diceSize = 1.5;
  
  const [ref, api] = useBox(() => ({ 
    mass: 1,
    position: position || [0, 8, 0],
    rotation: [
      Math.random() * Math.PI,
      Math.random() * Math.PI,
      Math.random() * Math.PI
    ],
    args: [diceSize, diceSize, diceSize],
    allowSleep: true,
    sleepSpeedLimit: 0.1,
    sleepTimeLimit: 1,
    linearDamping: 0.1,
    angularDamping: 0.1
  }));
  
  // 텍스처 로드
  const textures = useLoader(TextureLoader, getTexturesForType(type));
  
  // 주사위 색상 설정
  const diceColor = getDiceColor(type);
  
  // 주사위 굴리기 효과
  useEffect(() => {
    if (isRolling) {
      // 약간의 지연 후 주사위 던지기 (떨림 방지)
      const throwTimeout = setTimeout(() => {
        // 주사위 위치 초기화
        api.position.set(
          (position?.[0] || 0) + (Math.random() * 1 - 0.5),
          position?.[1] || 8,
          (position?.[2] || 0) + (Math.random() * 1 - 0.5)
        );
        
        // 초기 회전 상태 재설정 (안정적인 초기 상태)
        api.rotation.set(
          Math.random() * Math.PI,
          Math.random() * Math.PI,
          Math.random() * Math.PI
        );
        
        // 모든 속도 초기화
        api.velocity.set(0, 0, 0);
        api.angularVelocity.set(0, 0, 0);
        
        // 잠시 후 실제 던지기 동작
        setTimeout(() => {
          // 강한 초기 속도로 주사위 던지기
          api.velocity.set(
            (Math.random() - 0.5) * 8,
            0,
            (Math.random() - 0.5) * 8
          );
          
          api.angularVelocity.set(
            Math.random() * 15 - 7.5,
            Math.random() * 15 - 7.5,
            Math.random() * 15 - 7.5
          );
        }, 100);
      }, 50);
      
      return () => clearTimeout(throwTimeout);
    } else {
      // 결과에 맞게 주사위 회전 조정
      const faces = DICE_FACES[type]?.faces || [];
      const faceIndex = faces.findIndex(face => face.type === result);
      adjustDiceRotation(result, faceIndex, api, ref);
    }
  }, [isRolling, api, ref, result, position, type]);
  
  return (
    <mesh 
      ref={ref}
      castShadow
      receiveShadow
    >
      <boxGeometry args={[diceSize, diceSize, diceSize]} />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[0]} 
        color={diceColor}
      />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[1]} 
        color={diceColor}
      />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[2]} 
        color={diceColor}
      />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[3]} 
        color={diceColor}
      />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[4]} 
        color={diceColor}
      />
      <meshStandardMaterial 
        attachArray="material" 
        map={textures[5]} 
        color={diceColor}
      />
    </mesh>
  );
};

// 주사위 애니메이션 영역 컴포넌트 - 상단 뷰로 수정
const DiceAnimationArea = ({ selectedDice, isRolling, onAnimationComplete }) => {
  const [animationProgress, setAnimationProgress] = useState(0);
  
  // 애니메이션 진행 효과
  useEffect(() => {
    if (isRolling) {
      const animationDuration = 4000; // 4초
      const startTime = Date.now();
      
      const animationTimer = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / animationDuration, 1);
        setAnimationProgress(progress);
        
        if (progress >= 1) {
          clearInterval(animationTimer);
          
          // 애니메이션 완료 후 결과 처리
          setTimeout(() => {
            if (onAnimationComplete) {
              // 주사위 결과 생성
              const results = selectedDice.map(diceType => {
                // 주사위 타입에 따른 면 정보 가져오기
                const diceFaces = DICE_FACES[diceType].faces;
                
                // 랜덤하게 면 선택 (0-5)
                const faceIndex = Math.floor(Math.random() * 6);
                const selectedFace = diceFaces[faceIndex];
                
                return {
                  type: diceType,
                  result: selectedFace.type,
                  faceIndex: faceIndex
                };
              });
              
              onAnimationComplete(results);
            }
          }, 800);
        }
      }, 16);
      
      return () => clearInterval(animationTimer);
    }
  }, [isRolling, selectedDice, onAnimationComplete]);
  
  return (
    <div className="dice-animation-area">
      {/* 카메라 위치 변경 - 상단에서 내려다보는 뷰 */}
      <Canvas shadows camera={{ position: [0, 15, 0], fov: 50, lookAt: [0, 0, 0] }}>
        <ambientLight intensity={0.7} />
        <directionalLight
          position={[5, 15, 5]}
          intensity={1.0}
          castShadow
          shadow-mapSize-width={2048}
          shadow-mapSize-height={2048}
        />
        <Suspense fallback={null}>
          <Physics 
            gravity={[0, -9.8, 0]} 
            defaultContactMaterial={{ restitution: 0.3, friction: 0.5 }}
          >
            {/* 바닥 */}
            <Floor />
            
            {/* 경계벽 - 더 높게 */}
            <Wall position={[10, 4, 0]} rotation={[0, -Math.PI/2, 0]} />
            <Wall position={[-10, 4, 0]} rotation={[0, Math.PI/2, 0]} />
            <Wall position={[0, 4, 10]} rotation={[0, Math.PI, 0]} />
            <Wall position={[0, 4, -10]} rotation={[0, 0, 0]} />
            
            {/* 주사위 */}
            {selectedDice.map((diceType, index) => (
              <PhysicalDice 
                key={index}
                type={diceType}
                result={'BRAIN'} // 임시 결과 
                isRolling={isRolling}
                position={[
                  (index % 3 - 1) * 2, // 간격 조정
                  8 + Math.floor(index / 3) * 2, // 높이 - 더 높게 시작
                  0
                ]}
              />
            ))}
          </Physics>
        </Suspense>
      </Canvas>
    </div>
  );
};

// 주사위 컨테이너 컴포넌트
const DiceContainer = ({ type, result, isRolling }) => {
  const [animationComplete, setAnimationComplete] = useState(false);
  
  const handleAnimationComplete = () => {
    setAnimationComplete(true);
  };
  
  return (
    <div className={`dice-result-container ${isRolling && !animationComplete ? 'rolling-container' : 'animation-ended'}`}>
      <div className={`dice-3d-container ${type.toLowerCase()}-dice-container`}>
        <Canvas shadows camera={{ position: [0, 2, 5], fov: 50 }}>
          <ambientLight intensity={0.5} />
          <directionalLight
            position={[10, 10, 10]}
            intensity={0.8}
            castShadow
            shadow-mapSize-width={1024}
            shadow-mapSize-height={1024}
          />
          <Suspense fallback={null}>
            <Physics 
              gravity={[0, -9.8, 0]} 
              defaultContactMaterial={{ restitution: 0.3 }}
            >
              <Floor />
              <Wall position={[15, 0, 0]} rotation={[0, -Math.PI/2, 0]} /> {/* 오른쪽 벽 */}
              <Wall position={[-15, 0, 0]} rotation={[0, Math.PI/2, 0]} /> {/* 왼쪽 벽 */}
              <Wall position={[0, 0, 15]} rotation={[0, Math.PI, 0]} /> {/* 뒤 벽 */}
              <Wall position={[0, 0, -15]} rotation={[0, 0, 0]} /> {/* 앞 벽 */}
              <PhysicalDice 
                type={type} 
                result={result} 
                isRolling={isRolling} 
                position={[0, 5, 0]}
              />
            </Physics>
          </Suspense>
        </Canvas>
      </div>
      <div className={`dice-result-label ${getResultClass(result)} ${animationComplete ? 'show-result' : ''}`}>
        {result === 'BRAIN' && '뇌'}
        {result === 'SHOTGUN' && '총'}
        {result === 'FOOTSTEPS' && '발자국'}
      </div>
    </div>
  );
};

// 기존 Dice 컴포넌트를 DiceContainer로 대체
const Dice = ({ type, result, isRolling }) => {
  return <DiceContainer type={type} result={result} isRolling={isRolling} />;
};

// 주사위 풀 상태 컴포넌트
const DicePoolStatus = ({ dicePool }) => {
  // 주사위 타입별 개수 계산
  const redCount = dicePool.filter(dice => dice === 'RED').length;
  const yellowCount = dicePool.filter(dice => dice === 'YELLOW').length;
  const greenCount = dicePool.filter(dice => dice === 'GREEN').length;
  
  return (
    <div className="dice-pool-status mt-4">
      <h4 className="text-center mb-3">남은 주사위</h4>
      <div className="dice-pool-container">
        <div className="dice-pool-item">
          <div className="dice-pool-color red"></div>
          <div className="dice-pool-count">{redCount}</div>
          <div className="dice-pool-label">빨강</div>
        </div>
        <div className="dice-pool-item">
          <div className="dice-pool-color yellow"></div>
          <div className="dice-pool-count">{yellowCount}</div>
          <div className="dice-pool-label">노랑</div>
        </div>
        <div className="dice-pool-item">
          <div className="dice-pool-color green"></div>
          <div className="dice-pool-count">{greenCount}</div>
          <div className="dice-pool-label">초록</div>
        </div>
      </div>
      <div className="dice-pool-total text-center mt-2">
        총 {dicePool.length}개 남음
      </div>
    </div>
  );
};

// 메인 ZombieDice 컴포넌트
function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  // 상태 정의
  const [dicePool, setDicePool] = useState([]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [isRolling, setIsRolling] = useState(false);
  const [winner, setWinner] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  
  // 모달 제거 - 주사위 애니메이션 상태만 유지
  const [showDiceAnimation, setShowDiceAnimation] = useState(false);
  
  // 게임 시작 시 주사위 풀 초기화
  useEffect(() => {
    if (isMyTurn && !dicePool.length) {
      // 주사위 풀 초기화
      const initialDicePool = [
        ...Array(3).fill('RED'),
        ...Array(4).fill('YELLOW'),
        ...Array(6).fill('GREEN')
      ];
      setDicePool(initialDicePool);
    }
  }, [isMyTurn, dicePool.length]);
  
  // 점수판 렌더링 함수
  const renderScoreBoard = () => {
    if (!players || players.length === 0) return null;
    
    return (
      <div className="scoreboard mb-4">
        <h3 className="text-center mb-3">점수판</h3>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>순서</th>
                <th>플레이어</th>
                <th>점수</th>
              </tr>
            </thead>
            <tbody>
              {players.map((player, index) => (
                <tr key={index} className={index === currentTurn ? 'table-active' : ''}>
                  <td>{index + 1}</td>
                  <td>
                    {player.nickname}
                    {index === currentTurn && <span className="ms-2 badge bg-primary">턴</span>}
                  </td>
                  <td>{player.score} / {WINNING_SCORE}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };
  
  // 주사위 굴리기 요청 함수 수정
  const handleRoll = () => {
    console.log('주사위 굴리기 요청');
    
    // 주사위 굴리기 상태 설정
    setIsRolling(true);
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      // 주사위 선택 요청
      window.socket.emit('gameAction', {
        action: 'selectDice',
        roomId,
        requestRoll: true
      });
    } else {
      console.error('소켓 연결이 없습니다!');
      setIsRolling(false);
    }
  };
  
  // 주사위 굴리기 결과 처리 함수 수정
  const handleRollComplete = (results) => {
    console.log('주사위 굴리기 완료:', results);
    
    // 주사위 굴리기 상태 종료
    setIsRolling(false);
    
    // 애니메이션 컨테이너는 결과가 표시된 후 일정 시간 지나서 숨김
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000); // 1초 후에 숨김
    
    if (!results || results.length === 0) {
      // 결과가 없는 경우 (오류)
      return;
    }
    
    // 주사위 결과 저장
    setRolledResults(results);
    
    // 뇌와 총 개수 집계
    let brains = currentBrains;
    let shotguns = currentShotguns;
    
    results.forEach(result => {
      if (result.result === 'BRAIN') {
        brains++;
      } else if (result.result === 'SHOTGUN') {
        shotguns++;
      }
    });
    
    setCurrentBrains(brains);
    setCurrentShotguns(shotguns);
    
    // 게임 액션 서버에 전송
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'rollDiceResult',
        roomId,
        results,
        brains,
        shotguns
      });
    }
  };
  
  // 계속하기 버튼 핸들러
  const handleContinue = () => {
    // 발자국 결과인 주사위만 필터링
    const footstepsDice = rolledResults
      .filter(result => result.result === 'FOOTSTEPS')
      .map(result => result.type);
    
    // 계속 진행 요청
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'continueTurn',
        roomId,
        footstepsDice
      });
    }
    
    // 결과 초기화
    setRolledResults([]);
  };
  
  // 멈추기 버튼 핸들러
  const handleStop = () => {
    // 턴 종료 요청
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'endTurn',
        roomId,
        score: currentShotguns >= 3 ? 0 : currentBrains
      });
    }
    
    // 상태 초기화
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    setSelectedDice([]);
  };
  
  // 서버 이벤트 핸들러 수정
  useEffect(() => {
    if (window.socket) {
      // 게임 시작 이벤트 리스너
      const handleGameStarted = (data) => {
        console.log('게임 시작됨:', data);
        setGameStarted(true);
        
        // 주사위 풀 초기화 추가
        if (data.gameState && data.gameState.dicePool) {
          setDicePool(data.gameState.dicePool);
        }
      };
      
      // 턴 시작 이벤트 리스너
      const handleTurnStarted = (data) => {
        console.log('턴 시작됨:', data);
        // 주사위 풀 설정 - 명시적으로 확인
        if (data.dicePool && Array.isArray(data.dicePool)) {
          setDicePool(data.dicePool);
        } else {
          // 주사위 풀이 없거나 배열이 아닌 경우 기본값 설정
          const defaultDicePool = [
            ...Array(3).fill('RED'),
            ...Array(4).fill('YELLOW'),
            ...Array(6).fill('GREEN')
          ];
          setDicePool(defaultDicePool);
        }
        setCurrentBrains(0);
        setCurrentShotguns(0);
      };
      
      // 주사위 선택 이벤트 리스너 수정
      const handleDiceSelected = (data) => {
        console.log('주사위 선택됨:', data);
        
        setSelectedDice(data.selectedDice || []);
        setDicePool(data.dicePool || []);
        
        // 주사위 굴리기 요청이 함께 있으면 애니메이션 시작
        if (data.requestRoll) {
          setIsRolling(true);
          setShowDiceAnimation(true);
        }
      };
      
      // 주사위 굴리기 결과 이벤트 리스너 수정
      const handleRollDiceResult = (data) => {
        console.log('서버에서 주사위 결과 확인:', data);
        
        // 주사위 풀 업데이트
        if (data.dicePool) {
          setDicePool(data.dicePool);
        }
        
        // 다른 플레이어의 결과도 표시
        if (data.results && data.results.length > 0) {
          setRolledResults(data.results);
          // 현재 턴 플레이어가 아닌 경우에도 결과 보여주기
          if (!isMyTurn) {
            // 애니메이션 종료
            setIsRolling(false);
            setShowDiceAnimation(false);
          }
        }
        
        if (!isMyTurn) {
          // 다른 플레이어의 뇌와 샷건 개수 정보도 업데이트
          if (data.brains !== undefined) {
            setCurrentBrains(data.brains);
          }
          if (data.shotguns !== undefined) {
            setCurrentShotguns(data.shotguns);
          }
        }
      };
      
      // 턴 종료 이벤트 리스너
      const handleTurnEnded = (data) => {
        console.log('턴 종료됨:', data);
        
        // 상태 초기화
        setCurrentBrains(0);
        setCurrentShotguns(0);
        setRolledResults([]);
        setSelectedDice([]);
        
        // 턴 종료 콜백 호출
        if (onTurnEnd) {
          onTurnEnd(data.nextPlayer);
        }
      };
      
      // 게임 종료 이벤트 리스너
      const handleGameEnded = (data) => {
        console.log('게임 종료됨:', data);
        
        if (data.winner) {
          setWinner(data.winner);
        }
      };
      
      // 새로운 이벤트 리스너 추가 - 주사위 굴리기 시작
      const handleDiceRolling = (data) => {
        console.log('주사위 굴리기 시작:', data);
        
        // 모든 플레이어에게 주사위 애니메이션 표시
        setSelectedDice(data.selectedDice || []);
        setIsRolling(true);
        setShowDiceAnimation(true);
        
        // 애니메이션 타이머 설정 (실제 애니메이션 종료는 결과 수신 시)
        if (!isMyTurn) {
          // 결과를 받지 못하는 경우를 대비한 타임아웃
          setTimeout(() => {
            if (isRolling) {
              setIsRolling(false);
              setShowDiceAnimation(false);
            }
          }, 3500);
        }
      };
      
      // 이벤트 리스너 등록
      window.socket.on('gameStarted', handleGameStarted);
      window.socket.on('turnStarted', handleTurnStarted);
      window.socket.on('diceSelected', handleDiceSelected);
      window.socket.on('rollDiceResult', handleRollDiceResult);
      window.socket.on('turnEnded', handleTurnEnded);
      window.socket.on('gameEnded', handleGameEnded);
      window.socket.on('diceRolling', handleDiceRolling); // 새 이벤트
      
      // 클린업
      return () => {
        window.socket.off('gameStarted', handleGameStarted);
        window.socket.off('turnStarted', handleTurnStarted);
        window.socket.off('diceSelected', handleDiceSelected);
        window.socket.off('rollDiceResult', handleRollDiceResult);
        window.socket.off('turnEnded', handleTurnEnded);
        window.socket.off('gameEnded', handleGameEnded);
        window.socket.off('diceRolling', handleDiceRolling); // 새 이벤트
      };
    }
  }, [onTurnEnd, isMyTurn, isRolling]);

  // 메인 렌더링 수정
  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>🏆 {winner.nickname}님이 승리하셨습니다! 🏆</h3>
        </div>
      ) : (
        <>
          {isMyTurn ? (
            <>
              <div className="text-center mb-3">
                <h3 className="my-turn-indicator">당신의 차례입니다!</h3>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>획득한 뇌: {currentBrains}</div>
                  <div>샷건 횟수: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation ? (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">주사위 굴리는 중...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={handleRollComplete}
                  />
                </div>
              ) : (
                <div className="dice-area mb-3">
                  {isRolling ? (
                    <div className="text-center">
                      <p>주사위 굴리는 중...</p>
                    </div>
                  ) : (
                    rolledResults.length > 0 ? (
                      <div className="rolled-results">
                        <h4>주사위 결과:</h4>
                        <div className="dice-results-container">
                          {rolledResults.map((result, index) => (
                            <div key={index} className={`dice-result ${result.result.toLowerCase()}`}>
                              <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                              <div className="dice-face">
                                {result.result === 'BRAIN' && '뇌'}
                                {result.result === 'SHOTGUN' && '총'}
                                {result.result === 'FOOTSTEPS' && '발자국'}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ) : (
                      <button 
                        className="btn btn-primary btn-lg"
                        onClick={handleRoll}
                        disabled={isRolling}
                      >
                        주사위 굴리기
                      </button>
                    )
                  )}
                </div>
              )}
            </>
          ) : (
            <>
              <div className="text-center mb-3">
                <h3>다른 플레이어의 차례입니다.</h3>
                <p>{players[currentTurn]?.nickname}님이 주사위를 굴리고 있습니다...</p>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>획득한 뇌: {currentBrains}</div>
                  <div>샷건 횟수: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation && (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">주사위 굴리는 중...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={null} // 관전자는 결과 처리하지 않음
                  />
                </div>
              )}
              
              {rolledResults.length > 0 && !isRolling && !showDiceAnimation && (
                <div className="rolled-results">
                  <h4>주사위 결과:</h4>
                  <div className="dice-results-container">
                    {rolledResults.map((result, index) => (
                      <div key={index} className={`dice-result ${result.result.toLowerCase()}`}>
                        <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                        <div className="dice-face">
                          {result.result === 'BRAIN' && '뇌'}
                          {result.result === 'SHOTGUN' && '총'}
                          {result.result === 'FOOTSTEPS' && '발자국'}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
          
          {rolledResults.length > 0 && !winner && !isRolling && !showDiceAnimation && isMyTurn && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                계속하기
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                {currentShotguns >= 3 ? "턴 종료 (0점)" : "멈추기"}
              </button>
            </div>
          )}
          
          {currentShotguns >= 3 && (
            <div className="alert alert-danger mt-3">
              <strong>총이 3개 이상!</strong> 이번 턴에 획득한 뇌를 모두 잃었습니다. 턴을 종료해주세요.
            </div>
          )}
          
          <DicePoolStatus dicePool={dicePool} />
        </>
      )}
    </div>
  );
}

export default ZombieDice;
