import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import './ZombieDice.css';

const ZombieDice = ({ socket, username, isHost }) => {
  const { roomId } = useParams();
  const [gameStarted, setGameStarted] = useState(false);
  const [players, setPlayers] = useState([]);
  const [currentTurn, setCurrentTurn] = useState(null);
  const [isMyTurn, setIsMyTurn] = useState(false);
  const [dicePool, setDicePool] = useState({
    red: 3,
    yellow: 4,
    green: 6
  });
  const [rolledDice, setRolledDice] = useState([]);
  const [turnScore, setTurnScore] = useState(0);
  const [gameLog, setGameLog] = useState([]);
  const [playerScores, setPlayerScores] = useState({});
  const [shotguns, setShotguns] = useState(0);
  const [brains, setBrains] = useState(0);

  useEffect(() => {
    if (!socket) return;

    // ÌîåÎ†àÏù¥Ïñ¥ Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏ Ïù¥Î≤§Ìä∏
    socket.on('playerList', (playerList) => {
      console.log('ÌîåÎ†àÏù¥Ïñ¥ Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏:', playerList);
      setPlayers(playerList);
    });

    // Í≤åÏûÑ ÏãúÏûë Ïù¥Î≤§Ìä∏
    socket.on('gameStarted', (data) => {
      console.log('Í≤åÏûÑ ÏãúÏûë Îç∞Ïù¥ÌÑ∞ ÏàòÏã†:', data);
      setGameStarted(true);
      setPlayers(data.players);
      setCurrentTurn(data.currentTurn);
      setIsMyTurn(data.currentTurn === username);
      setPlayerScores(data.playerScores || {});

      // Í≤åÏûÑ Î°úÍ∑∏Ïóê ÏãúÏûë Î©îÏãúÏßÄ Ï∂îÍ∞Ä
      addToGameLog(`Í≤åÏûÑÏù¥ ÏãúÏûëÎêòÏóàÏäµÎãàÎã§. Ï≤´ Î≤àÏß∏ ÌîåÎ†àÏù¥Ïñ¥: ${data.currentTurn}`);
    });

    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º Ïù¥Î≤§Ìä∏
    socket.on('diceRolled', (data) => {
      console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º ÏàòÏã†:', data);
      setRolledDice(data.rolledDice);
      setDicePool(data.dicePool);
      setShotguns(data.shotguns);
      setBrains(data.brains);

      // Í≤åÏûÑ Î°úÍ∑∏Ïóê Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º Ï∂îÍ∞Ä
      const diceResults = data.rolledDice.map(die => `${die.color} Ï£ºÏÇ¨ÏúÑ: ${die.face}`).join(', ');
      addToGameLog(`${data.player}ÎãòÏù¥ Ï£ºÏÇ¨ÏúÑÎ•º Íµ¥Î†∏ÏäµÎãàÎã§. Í≤∞Í≥º: ${diceResults}`);

      if (data.turnEnded) {
        addToGameLog(`${data.player}ÎãòÏùò ÌÑ¥Ïù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§. Îã§Ïùå ÌîåÎ†àÏù¥Ïñ¥: ${data.nextPlayer}`);
        setCurrentTurn(data.nextPlayer);
        setIsMyTurn(data.nextPlayer === username);
        setShotguns(0);
        setBrains(0);
        setPlayerScores(data.playerScores || {});
      }
    });

    // ÌÑ¥ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏
    socket.on('turnEnded', (data) => {
      console.log('ÌÑ¥ Ï¢ÖÎ£å Îç∞Ïù¥ÌÑ∞ ÏàòÏã†:', data);
      setCurrentTurn(data.nextPlayer);
      setIsMyTurn(data.nextPlayer === username);
      setShotguns(0);
      setBrains(0);
      setDicePool({
        red: 3,
        yellow: 4,
        green: 6
      });
      setPlayerScores(data.playerScores || {});

      addToGameLog(`${data.player}ÎãòÏù¥ ÌÑ¥ÏùÑ Ï¢ÖÎ£åÌñàÏäµÎãàÎã§. ÌöçÎìù Ï†êÏàò: ${data.score}. Îã§Ïùå ÌîåÎ†àÏù¥Ïñ¥: ${data.nextPlayer}`);
    });

    // Í≤åÏûÑ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏
    socket.on('gameEnded', (data) => {
      console.log('Í≤åÏûÑ Ï¢ÖÎ£å Îç∞Ïù¥ÌÑ∞ ÏàòÏã†:', data);
      setGameStarted(false);
      addToGameLog(`Í≤åÏûÑÏù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§. ÏäπÏûê: ${data.winner}, Ï†êÏàò: ${data.score}`);
    });

    return () => {
      socket.off('playerList');
      socket.off('gameStarted');
      socket.off('diceRolled');
      socket.off('turnEnded');
      socket.off('gameEnded');
    };
  }, [socket, username]);

  // Í≤åÏûÑ Î°úÍ∑∏Ïóê Î©îÏãúÏßÄ Ï∂îÍ∞ÄÌïòÎäî Ìï®Ïàò
  const addToGameLog = (message) => {
    setGameLog(prevLog => [...prevLog, `[${new Date().toLocaleTimeString()}] ${message}`]);
  };

  // Í≤åÏûÑ ÏãúÏûë ÏöîÏ≤≠ Ìï®Ïàò
  const startGame = () => {
    console.log('Í≤åÏûÑ ÏãúÏûë ÏöîÏ≤≠ Ï†ÑÏÜ°');
    socket.emit('startGame', { roomId });
  };

  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ Ìï®Ïàò
  const rollDice = () => {
    console.log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ Ï†ÑÏÜ°:', dicePool);
    socket.emit('rollDice', { roomId, dicePool });
  };

  // ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠ Ìï®Ïàò
  const endTurn = () => {
    console.log('ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠ Ï†ÑÏÜ°');
    socket.emit('endTurn', { roomId });
  };

  // Ï£ºÏÇ¨ÏúÑ ÏÉâÏÉÅÏóê Îî∞Î•∏ ÌÅ¥ÎûòÏä§ Î∞òÌôò Ìï®Ïàò
  const getDiceColorClass = (color) => {
    switch (color) {
      case 'red': return 'dice-red';
      case 'yellow': return 'dice-yellow';
      case 'green': return 'dice-green';
      default: return '';
    }
  };

  // Ï£ºÏÇ¨ÏúÑ Î©¥Ïóê Îî∞Î•∏ ÏïÑÏù¥ÏΩò Î∞òÌôò Ìï®Ïàò
  const getDiceFaceIcon = (face) => {
    switch (face) {
      case 'brain': return 'üß†';
      case 'shotgun': return 'üí•';
      case 'footsteps': return 'üë£';
      default: return '';
    }
  };

  return (
    <div className="zombie-dice-container">
      <h2>Ï¢ÄÎπÑ Îã§Ïù¥Ïä§ - Î∞© {roomId}</h2>

      {!gameStarted ? (
        <div className="game-setup">
          <h3>ÌîåÎ†àÏù¥Ïñ¥ Î™©Î°ù</h3>
          <ul className="player-list">
            {players.map((player, index) => (
              <li key={index} className={player.ready ? 'ready' : ''}>
                {player.username} {player.ready ? '(Ï§ÄÎπÑÏôÑÎ£å)' : '(ÎåÄÍ∏∞Ï§ë)'} {player.isHost ? '(Î∞©Ïû•)' : ''}
              </li>
            ))}
          </ul>

          {isHost && (
            <button 
              className="start-game-btn" 
              onClick={startGame}
              disabled={!players.every(player => player.ready)}
            >
              Í≤åÏûÑ ÏãúÏûë
            </button>
          )}
        </div>
      ) : (
        <div className="game-board">
          <div className="game-info">
