import { useState, useEffect } from 'react';
import '../../styles/ZombieDice.css';

// 상수 정의
const WINNING_SCORE = 13;  // 승리 점수
const INITIAL_DICE_POOL = [
  'RED', 'RED', 'RED',
  'YELLOW', 'YELLOW', 'YELLOW', 'YELLOW',
  'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN'
];

const ZombieDice = ({ players, isHost, roomId }) => {
  // 게임 상태 관리
  const [gameStarted, setGameStarted] = useState(false);
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(0);
  const [dicePool, setDicePool] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const [scores, setScores] = useState({});
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [winner, setWinner] = useState(null);
  
  // 소켓 참조
  const socket = window.socket;
  
  // 로그 출력 함수
  const log = (message, data) => {
    if (data) {
      console.log(`[좀비다이스 클라이언트] ${message}`, data);
    } else {
      console.log(`[좀비다이스 클라이언트] ${message}`);
    }
  };
  
  // 게임 시작 처리
  const handleGameStart = () => {
    if (!socket) return;

    // 게임 시작 상태 설정
    setGameStarted(true);
    setDicePool([...INITIAL_DICE_POOL]);

    // 플레이어 점수 초기화
    const initialScores = {};
    players.forEach((player, index) => {
      initialScores[player.id] = 0;
    });
    setScores(initialScores);

    // 첫 번째 플레이어 설정
    setCurrentPlayerIndex(0);

    // 서버에 게임 시작 알림
    socket.emit('gameStarted', { roomId, players });
  };
  
  // 게임 이벤트 리스너 설정
  useEffect(() => {
    if (!socket) {
      log('소켓 연결이 없습니다');
      return;
    }
    
    // 게임 시작 이벤트 수신
    socket.on('gameStarted', (data) => {
      log('게임 시작 이벤트 수신:', data);
      
      const { players } = data;
      
      // 게임 상태 업데이트
      setGameStarted(true);
      
      // 내 차례인지 확인
      const currentPlayerIndex = players.findIndex(p => p.id === socket.id);
      setCurrentPlayerIndex(currentPlayerIndex);
      
      log(`게임이 시작되었습니다. ${currentPlayerIndex !== -1 ? '내 차례입니다.' : '다른 플레이어의 차례입니다.'}`);
    });
    
    // 주사위 굴림 결과 수신
    socket.on('diceRolled', (data) => {
      log('주사위 굴림 결과 수신:', data);
      
      const { 
        results, brains, shotguns, 
        updatedPool, totalBrains, totalShotguns 
      } = data;
      
      // 주사위 결과 표시
      setRolledResults(results);
      
      // 내 차례인 경우 점수 업데이트
      if (currentPlayerIndex !== -1) {
        setCurrentBrains(totalBrains || brains);
        setCurrentShotguns(totalShotguns || shotguns);
        setDicePool(updatedPool);
      }
      
      // 애니메이션 종료
      setIsRolling(false);
    });
    
    // 턴 변경 이벤트 수신
    socket.on('turnChanged', (data) => {
      log('턴 변경 이벤트 수신:', data);
      
      const { nextPlayerIndex } = data;
      
      // 점수 업데이트
      setScores(prevScores => ({
        ...prevScores,
        [players[nextPlayerIndex].id]: prevScores[players[nextPlayerIndex].id] + currentBrains
      }));
      
      // 다음 플레이어 설정
      setCurrentPlayerIndex(nextPlayerIndex);
      
      // 턴 초기화
      resetTurn();
    });
    
    // 정리 함수
    return () => {
      socket.off('gameStarted');
      socket.off('diceRolled');
      socket.off('turnChanged');
    };
  }, [socket, players]);
  
  // 턴 초기화
  const resetTurn = () => {
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    log('턴 초기화');
  };
  
  // 주사위 굴리기
  const handleRollDice = () => {
    if (!socket || isRolling || dicePool.length < 3) return;
    
    log('주사위 굴리기');
    
    // 주사위 굴림 상태 설정
    setIsRolling(true);
    
    // 서버에 주사위 굴리기 요청
    socket.emit('rollDice', {
      roomId,
      dicePool
    });
  };
  
  // 턴 종료
  const handleEndTurn = () => {
    if (!socket || isRolling) return;
    
    log('턴 종료');
    
    // 점수 업데이트
    if (currentShotguns < 3) {
      const currentPlayerId = players[currentPlayerIndex].id;
      setScores(prevScores => ({
        ...prevScores,
        [currentPlayerId]: prevScores[currentPlayerId] + currentBrains
      }));
    }
    
    // 다음 플레이어로 턴 변경
    setCurrentPlayerIndex((prevIndex) => (prevIndex + 1) % players.length);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setDicePool([...INITIAL_DICE_POOL]);
    setRolledResults([]);
    
    // 서버에 턴 종료 요청
    socket.emit('endTurn', {
      roomId,
      keepScore: currentShotguns < 3
    });
  };
  
  return (
    <div className="zombie-dice">
      {!gameStarted ? (
        <div className="text-center mb-4">
          <h2>좀비 다이스</h2>
          <p>각자 좀비가 되어 뇌를 많이 먹는 사람이 승리하는 게임입니다!</p>
          {isHost && (
            <button 
              className="btn btn-primary btn-lg"
              onClick={handleGameStart}
            >
              게임 시작
            </button>
          )}
        </div>
      ) : (
        <div>
          {/* 게임 화면 내용 */}
          <div className="score-board mb-4">
            <h3 className="text-center mb-3">점수판</h3>
            <table className="table">
              <thead>
                <tr>
                  <th>플레이어</th>
                  <th>점수</th>
                </tr>
              </thead>
              <tbody>
                {players.map((player, index) => (
                  <tr key={player.id} className={currentPlayerIndex === index ? 'table-active' : ''}>
                    <td>{player.nickname} {player.id === socket.id && '(나)'}</td>
                    <td>{scores[player.id]}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {/* 주사위 영역 */}
          <div className="dice-area">
            {/* 주사위 결과 영역 */}
            {rolledResults.length > 0 && (
              <div className="rolled-results mb-3">
                <h4>주사위 결과:</h4>
                <div className="dice-results-container">
                  {rolledResults.map((result, index) => (
                    <div 
                      key={index} 
                      className={`dice-result result-${result.result.toLowerCase()}`}
                    >
                      <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                      <img 
                        src={`/zombie-dice/faces/${result.type.toLowerCase()}-${result.result.toLowerCase()}.png`} 
                        alt={`${result.result}`}
                        className="dice-result-image"
                      />
                      <div className="dice-result-text">{result.result}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}
            
            {/* 경고 메시지 */}
            {currentShotguns >= 3 && (
              <div className="alert alert-danger">
                <strong>경고:</strong> 총이 3개 이상 발견되었습니다! 턴을 종료해야 합니다.
              </div>
            )}
            
            {/* 주사위 풀 상태 */}
            <div className="dice-pool-status mb-3">
              <h4>남은 주사위: {dicePool.length}개</h4>
              <div className="dice-counts">
                <div className="dice-count">
                  <div className="dice-sample red"></div>
                  <span>빨간 주사위: {dicePool.filter(d => d === 'RED').length}개</span>
                </div>
                <div className="dice-count">
                  <div className="dice-sample yellow"></div>
                  <span>노란 주사위: {dicePool.filter(d => d === 'YELLOW').length}개</span>
                </div>
                <div className="dice-count">
                  <div className="dice-sample green"></div>
                  <span>녹색 주사위: {dicePool.filter(d => d === 'GREEN').length}개</span>
                </div>
              </div>
            </div>
            
            {/* 조작 버튼 */}
            <div className="controls">
              {players[currentPlayerIndex].id === socket.id ? (
                <>
                  <button 
                    className="btn btn-primary" 
                    onClick={handleRollDice}
                    disabled={isRolling || dicePool.length < 3}
                  >
                    주사위 굴리기
                  </button>
                  <button 
                    className="btn btn-warning" 
                    onClick={handleEndTurn}
                    disabled={isRolling}
                  >
                    멈추기
                  </button>
                </>
              ) : (
                <p>다른 플레이어의 차례입니다...</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ZombieDice;
