import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';
import { FaUser, FaSignOutAlt, FaPlay, FaCheck, FaEye, FaUserSlash } from 'react-icons/fa';
import { toast } from 'react-toastify';

const PLAYER_STATUS = {
  WAITING: 'WAITING',
  READY: 'READY',
  PLAYING: 'PLAYING',
  SPECTATING: 'SPECTATING'
};

function BanConfirmModal({ nickname, onConfirm, onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content bg-white p-4 rounded-3">
        <h5 className="mb-3">플레이어 추방</h5>
        <p className="mb-4">{nickname}님을 정말로 추방하시겠습니까?</p>
        <div className="d-flex justify-content-end gap-2">
          <button 
            className="btn btn-danger"
            onClick={onConfirm}
          >
            추방
          </button>
          <button 
            className="btn btn-secondary"
            onClick={onClose}
          >
            취소
          </button>
        </div>
      </div>
    </div>
  );
}

function Room({ nickname }) {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [players, setPlayers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const socketRef = useRef();
  const chatMessagesRef = useRef();
  const isLeaving = useRef(false);
  const [error, setError] = useState(null);
  const [banTargetNickname, setBanTargetNickname] = useState(null);

  useEffect(() => {
    socketRef.current = io('http://localhost:3001');
    joinRoom();

    socketRef.current.on('playerJoined', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('playerLeft', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('chatMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    socketRef.current.on('playerStatusChanged', ({ players }) => {
      setPlayers(players);
    });

    socketRef.current.on('gameStarted', () => {
      // 게임 시작 시 처리
    });

    socketRef.current.on('playerBanned', ({ nickname: bannedNick, players }) => {
      setPlayers(players);
      if (bannedNick === nickname) {
        toast.error('방에서 추방되었습니다.');
        navigate('/');
      }
    });

    // 방 정보 가져오기
    const fetchRoomInfo = async () => {
      try {
        const response = await axios.get(`/api/rooms/${roomId}`);
        setRoom(response.data);
      } catch (error) {
        console.error('방 정보를 가져오는데 실패했습니다:', error);
        navigate('/');
      }
    };
    fetchRoomInfo();

    return () => {
      if (isLeaving.current) {
        leaveRoom();
      }
      socketRef.current.disconnect();
    };
  }, [roomId]);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  const joinRoom = async () => {
    try {
      const response = await axios.post(`/api/rooms/${roomId}/join`, { nickname });
      setPlayers(response.data.players);
    } catch (error) {
      if (error.response?.status === 403) {
        toast.error(error.response.data.error);
        navigate('/');
      } else {
        console.error('방 참가에 실패했습니다:', error);
        navigate('/');
      }
    }
  };

  const leaveRoom = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/leave`, { nickname });
    } catch (error) {
      console.error('방 나가기에 실패했습니다:', error);
    }
  };

  const handleLeave = () => {
    isLeaving.current = true;
    navigate('/');
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      socketRef.current.emit('sendMessage', {
        roomId,
        nickname,
        message: inputMessage
      });
      setInputMessage('');
    }
  };

  const handleStatusChange = async (status) => {
    try {
      await axios.post(`/api/rooms/${roomId}/player-status`, {
        nickname,
        status
      });
    } catch (error) {
      console.error('상태 변경에 실패했습니다:', error);
    }
  };

  const handleGameStart = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/start`);
    } catch (error) {
      if (error.response?.status === 400) {
        toast.error(error.response.data.error);
      }
    }
  };

  const handleBanClick = (playerNickname) => {
    setBanTargetNickname(playerNickname);
  };

  const handleBanConfirm = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/ban`, {
        nickname: banTargetNickname,
        bannedBy: nickname
      });
      setBanTargetNickname(null);
    } catch (error) {
      if (error.response?.data?.error) {
        toast.error(error.response.data.error);
      }
    }
  };

  const isHost = players[0]?.nickname === nickname;
  const currentPlayer = players.find(p => p.nickname === nickname);
  const canInteract = room?.status === 'WAITING';

  if (!room) {
    return (
      <div className="container py-4">
        <div className="d-flex justify-content-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">로딩중...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h2 className="mb-0">{room.title}</h2>
          <div className="d-flex gap-2">
            {!isHost && canInteract && (
              <>
                {currentPlayer?.status === PLAYER_STATUS.SPECTATING ? (
                  <button 
                    className="btn btn-primary"
                    onClick={() => handleStatusChange(PLAYER_STATUS.WAITING)}
                  >
                    <FaUser className="me-2" />
                    참가하기
                  </button>
                ) : (
                  <>
                    {currentPlayer?.status === PLAYER_STATUS.READY ? (
                      <button 
                        className="btn btn-warning"
                        onClick={() => handleStatusChange(PLAYER_STATUS.WAITING)}
                        disabled={!canInteract}
                      >
                        <FaCheck className="me-2" />
                        준비 취소
                      </button>
                    ) : (
                      <>
                        <button 
                          className="btn btn-success"
                          onClick={() => handleStatusChange(PLAYER_STATUS.READY)}
                          disabled={!canInteract}
                        >
                          <FaCheck className="me-2" />
                          준비
                        </button>
                        <button 
                          className="btn btn-outline-info"
                          onClick={() => handleStatusChange(PLAYER_STATUS.SPECTATING)}
                          disabled={!canInteract}
                        >
                          <FaEye className="me-2" />
                          관전
                        </button>
                      </>
                    )}
                  </>
                )}
              </>
            )}
            {isHost && canInteract && (
              <button 
                className="btn btn-primary"
                onClick={handleGameStart}
                disabled={!canInteract}
              >
                <FaPlay className="me-2" />
                게임 시작
              </button>
            )}
            <button 
              className="btn btn-outline-danger"
              onClick={handleLeave}
              disabled={!canInteract}
            >
              <FaSignOutAlt className="me-2" />
              나가기
            </button>
          </div>
        </div>
        
        <div className="card-body">
          <div className="row">
            {/* 플레이어 목록 */}
            <div className="col-md-3">
              <div className="card mb-3">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">참가자 목록</h5>
                  <span className="badge bg-secondary">
                    {players.filter(p => p.status !== PLAYER_STATUS.SPECTATING).length}/{room.maxPlayers}명
                  </span>
                </div>
                <div className="card-body p-0">
                  <ul className="list-group list-group-flush">
                    {players
                      .filter(p => p.status !== PLAYER_STATUS.SPECTATING)
                      .map(player => (
                        <li key={player.nickname} className="list-group-item d-flex align-items-center justify-content-between">
                          <div className="d-flex align-items-center">
                            <FaUser className="me-2 text-secondary" />
                            <span>{player.nickname}</span>
                            {player.nickname === players[0].nickname && (
                              <span className="badge bg-primary ms-2">방장</span>
                            )}
                          </div>
                          <div className="d-flex align-items-center gap-2">
                            {player.status === PLAYER_STATUS.READY && (
                              <span className="badge bg-success">준비완료</span>
                            )}
                            {isHost && player.nickname !== nickname && canInteract && (
                              <button
                                className="btn btn-link text-danger p-0"
                                onClick={() => handleBanClick(player.nickname)}
                                title="추방하기"
                              >
                                <FaUserSlash size="1.2em" />
                              </button>
                            )}
                          </div>
                        </li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* 관전자 목록 */}
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0">관전자 목록</h5>
                </div>
                <div className="card-body p-0">
                  <ul className="list-group list-group-flush">
                    {players
                      .filter(p => p.status === PLAYER_STATUS.SPECTATING)
                      .map(player => (
                        <li key={player.nickname} className="list-group-item d-flex align-items-center justify-content-between">
                          <div className="d-flex align-items-center">
                            <FaEye className="me-2 text-secondary" />
                            <span>{player.nickname}</span>
                          </div>
                          {isHost && player.nickname !== nickname && canInteract && (
                            <button
                              className="btn btn-link text-danger p-0"
                              onClick={() => handleBanClick(player.nickname)}
                              title="추방하기"
                            >
                              <FaUserSlash size="1.2em" />
                            </button>
                          )}
                        </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            {/* 채팅 영역 */}
            <div className="col-md-9">
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0">채팅</h5>
                </div>
                <div className="card-body p-0 d-flex flex-column" style={{ height: '500px' }}>
                  <div 
                    className="chat-messages p-3 flex-grow-1 overflow-auto"
                    ref={chatMessagesRef}
                  >
                    {messages.map((msg, index) => (
                      <div 
                        key={index} 
                        className={`mb-2 ${msg.nickname === nickname ? 'text-end' : ''}`}
                      >
                        <div
                          className={`d-inline-block p-2 rounded-3 ${
                            msg.nickname === nickname 
                              ? 'bg-primary text-white' 
                              : 'bg-light'
                          }`}
                          style={{ maxWidth: '75%' }}
                        >
                          <div className="small fw-bold mb-1">
                            {msg.nickname}
                          </div>
                          <div style={{ wordBreak: 'break-word' }}>
                            {msg.message}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                  <form 
                    onSubmit={sendMessage} 
                    className="border-top p-2 mt-auto"
                  >
                    <div className="input-group">
                      <input
                        type="text"
                        className="form-control"
                        value={inputMessage}
                        onChange={(e) => setInputMessage(e.target.value)}
                        placeholder="메시지를 입력하세요"
                      />
                      <button type="submit" className="btn btn-primary">
                        전송
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* 추방 확인 모달 */}
      {banTargetNickname && (
        <BanConfirmModal
          nickname={banTargetNickname}
          onConfirm={handleBanConfirm}
          onClose={() => setBanTargetNickname(null)}
        />
      )}
    </div>
  );
}

export default Room;
