import { useState, useEffect, useRef } from 'react';
import '../../styles/ZombieDice.css';

// ÏÉÅÏàò Ï†ïÏùò
const WINNING_SCORE = 13;  // ÏäπÎ¶¨ Ï†êÏàò
const INITIAL_DICE_POOL = [
  'RED', 'RED', 'RED',
  'YELLOW', 'YELLOW', 'YELLOW', 'YELLOW',
  'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN'
];

const ZombieDice = ({ players, onGameEnd }) => {
  // Í≤åÏûÑ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [gameStarted, setGameStarted] = useState(false);
  const [dicePool, setDicePool] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const [scores, setScores] = useState({});
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [winner, setWinner] = useState(null);
  
  // ÏÜåÏºì Ï∞∏Ï°∞
  const socket = window.socket;
  
  // Î°úÍ∑∏ Ï∂úÎ†• Ìï®Ïàò
  const log = (message, data) => {
    if (data) {
      console.log(`[Ï¢ÄÎπÑÎã§Ïù¥Ïä§ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏] ${message}`, data);
    } else {
      console.log(`[Ï¢ÄÎπÑÎã§Ïù¥Ïä§ ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏] ${message}`);
    }
  };
  
  // Í≤åÏûÑ ÏãúÏûë Ï≤òÎ¶¨
  useEffect(() => {
    if (!socket) {
      log('ÏÜåÏºì Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§.');
      return;
    }
    
    // Í≤åÏûÑ ÏãúÏûë Ïù¥Î≤§Ìä∏ ÏàòÏã†
    socket.on('zombieDiceStarted', (data) => {
      log('Í≤åÏûÑ ÏãúÏûë Ïù¥Î≤§Ìä∏ ÏàòÏã†:', data);
      
      const { players: gamePlayers, currentTurn, currentPlayer } = data;
      setGameStarted(true);
      
      // ÎÇ¥ Ï∞®Î°ÄÏù∏ÏßÄ ÌôïÏù∏
      const myPlayer = gamePlayers.find(p => p.id === socket.id);
      setIsMyTurn(myPlayer && currentPlayer.id === socket.id);
      setCurrentPlayer(currentPlayer);
      
      // Ï¥àÍ∏∞ Ï†êÏàò ÏÑ§Ï†ï
      const initialScores = {};
      gamePlayers.forEach(p => {
        initialScores[p.id] = 0;
      });
      setScores(initialScores);
      
      // Ï£ºÏÇ¨ÏúÑ ÌíÄ Ï¥àÍ∏∞Ìôî
      resetDicePool();
    });
    
    // Ï†ïÎ¶¨ Ìï®Ïàò
    return () => {
      socket.off('zombieDiceStarted');
    };
  }, [socket]);
  
  // Í≤åÏûÑ Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà ÏÑ§Ï†ï
  useEffect(() => {
    if (!socket || !gameStarted) return;
    
    log('Í≤åÏûÑ Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà ÏÑ§Ï†ï');
    
    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶º Í≤∞Í≥º ÏàòÏã†
    socket.on('diceRolled', (data) => {
      log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶º Í≤∞Í≥º ÏàòÏã†:', data);
      
      const { 
        player, results, brains, shotguns, 
        dicePool: updatedPool, totalBrains, totalShotguns 
      } = data;
      
      // Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º ÌëúÏãú
      setRolledResults(results);
      setSelectedDice(results.map(r => r.type));
      
      // Ï¥ù ÌöçÎìù Îáå/Ï¥ù ÏóÖÎç∞Ïù¥Ìä∏
      setCurrentBrains(totalBrains);
      setCurrentShotguns(totalShotguns);
      
      // Ï£ºÏÇ¨ÏúÑ ÌíÄ ÏóÖÎç∞Ïù¥Ìä∏
      setDicePool(updatedPool);
      
      // Ïï†ÎãàÎ©îÏù¥ÏÖò Ï¢ÖÎ£å
      setIsRolling(false);
    });
    
    // ÌÑ¥ Î≥ÄÍ≤Ω Ïù¥Î≤§Ìä∏ ÏàòÏã†
    socket.on('turnChanged', (data) => {
      log('ÌÑ¥ Î≥ÄÍ≤Ω Ïù¥Î≤§Ìä∏ ÏàòÏã†:', data);
      
      const { nextPlayer, scores: updatedScores } = data;
      
      // Ï†êÏàò ÏóÖÎç∞Ïù¥Ìä∏
      setScores(updatedScores);
      
      // ÏäπÏûê ÌôïÏù∏
      let gameWinner = null;
      for (const [playerId, score] of Object.entries(updatedScores)) {
        if (score >= WINNING_SCORE) {
          gameWinner = players.find(p => p.id === playerId);
          break;
        }
      }
      
      if (gameWinner) {
        setWinner(gameWinner);
        log('Í≤åÏûÑ Ï¢ÖÎ£å: ÏäπÏûê Í≤∞Ï†ïÎê®', gameWinner);
      } else {
        // ÎÇ¥ Ï∞®Î°ÄÏù∏ÏßÄ ÌôïÏù∏
        setIsMyTurn(socket.id === nextPlayer);
        
        // ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥ ÏóÖÎç∞Ïù¥Ìä∏
        const nextPlayerObj = players.find(p => p.id === nextPlayer);
        setCurrentPlayer(nextPlayerObj);
        
        // ÏÉà ÌÑ¥ Ï¥àÍ∏∞Ìôî
        resetTurn();
      }
    });
    
    // Í≤åÏûÑ Ïò§Î•ò ÏàòÏã†
    socket.on('gameError', (error) => {
      log('Í≤åÏûÑ Ïò§Î•ò ÏàòÏã†:', error);
      alert(`Í≤åÏûÑ Ïò§Î•ò: ${error.message}`);
    });
    
    // Ï†ïÎ¶¨ Ìï®Ïàò
    return () => {
      socket.off('diceRolled');
      socket.off('turnChanged');
      socket.off('gameError');
    };
  }, [socket, gameStarted, players]);
  
  // Í≤åÏûÑ ÏãúÏûë Ìï®Ïàò
  const startGame = () => {
    if (!socket) {
      log('ÏÜåÏºì Ïó∞Í≤∞Ïù¥ ÏóÜÏäµÎãàÎã§.');
      return;
    }
    
    const roomId = window.location.pathname.split('/')[2];
    log('Í≤åÏûÑ ÏãúÏûë ÏöîÏ≤≠ Ï†ÑÏÜ°:', { roomId, players });
    
    socket.emit('startZombieDice', {
      roomId,
      players
    });
  };
  
  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠
  const handleRoll = () => {
    if (!socket || !isMyTurn) return;
    
    const roomId = window.location.pathname.split('/')[2];
    
    // Ï£ºÏÇ¨ÏúÑ ÌíÄÏù¥ ÎπÑÏñ¥ÏûàÎäîÏßÄ ÌôïÏù∏
    if (dicePool.length < 3) {
      alert('Ï£ºÏÇ¨ÏúÑÍ∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§. ÌÑ¥ÏùÑ Ï¢ÖÎ£åÌïòÏÑ∏Ïöî.');
      return;
    }
    
    setIsRolling(true);
    
    log('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ Ï†ÑÏÜ°:', { roomId, dicePool });
    
    socket.emit('rollDice', {
      roomId,
      dicePool
    });
  };
  
  // ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠
  const handleStop = () => {
    if (!socket || !isMyTurn) return;
    
    const roomId = window.location.pathname.split('/')[2];
    const keepScore = currentShotguns < 3;
    
    log('ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠ Ï†ÑÏÜ°:', { roomId, keepScore });
    
    socket.emit('endTurn', {
      roomId,
      keepScore
    });
  };
  
  // Ï£ºÏÇ¨ÏúÑ ÌíÄ Ï¥àÍ∏∞Ìôî
  const resetDicePool = () => {
    setDicePool([...INITIAL_DICE_POOL]);
    log('Ï£ºÏÇ¨ÏúÑ ÌíÄ Ï¥àÍ∏∞ÌôîÎê®');
  };
  
  // ÌÑ¥ Ï¥àÍ∏∞Ìôî
  const resetTurn = () => {
    setRolledResults([]);
    setSelectedDice([]);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    resetDicePool();
    log('ÌÑ¥ Ï¥àÍ∏∞ÌôîÎê®');
  };
  
  // Í≤åÏûÑ Îã§Ïãú ÏãúÏûë
  const restartGame = () => {
    setGameStarted(false);
    setWinner(null);
    resetTurn();
    startGame();
    log('Í≤åÏûÑ Ïû¨ÏãúÏûë');
  };
  
  // Í≤åÏûÑ UI Î†åÎçîÎßÅ
  return (
    <div className="zombie-dice">
      <h2 className="text-center mb-4">Ï¢ÄÎπÑ Îã§Ïù¥Ïä§</h2>
      
      {!gameStarted ? (
        <div className="text-center">
          <p>Í≤åÏûÑÏùÑ ÏãúÏûëÌïòÎ†§Î©¥ ÏïÑÎûò Î≤ÑÌäºÏùÑ ÌÅ¥Î¶≠ÌïòÏÑ∏Ïöî.</p>
          <button className="btn btn-primary" onClick={startGame}>
            Í≤åÏûÑ ÏãúÏûë
          </button>
        </div>
      ) : winner ? (
        <div className="winner-announcement">
          <h3>üèÜ {winner.nickname} ÏäπÎ¶¨! üèÜ</h3>
          <p>ÏµúÏ¢Ö Ï†êÏàò: {scores[winner.id]}Ï†ê</p>
          <button className="btn btn-primary" onClick={restartGame}>
            Îã§Ïãú ÏãúÏûë
          </button>
        </div>
      ) : (
        <>
          {/* Ï†êÏàòÌåê */}
          <div className="score-board mb-4">
            <h3 className="text-center mb-3">Ï†êÏàòÌåê</h3>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>ÌîåÎ†àÏù¥Ïñ¥</th>
                  <th>Ï†êÏàò</th>
                </tr>
              </thead>
              <tbody>
                {players.map(player => (
                  <tr 
                    key={player.id} 
                    className={currentPlayer && currentPlayer.id === player.id ? 'current-player' : ''}
                  >
                    <td>{player.nickname}</td>
                    <td>{scores[player.id] || 0}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {/* ÌòÑÏû¨ ÌÑ¥ Ï†ïÎ≥¥ */}
          <div className="current-status mb-3">
            <p className="mb-0">
              ÌòÑÏû¨ Ï∞®Î°Ä: <strong>{currentPlayer?.nickname || 'ÎåÄÍ∏∞ Ï§ë'}</strong>
              {isMyTurn && ' (ÎÇò)'}
            </p>
          </div>
          
          {/* ÌòÑÏû¨ ÌÑ¥ ÏÉÅÌÉú */}
          <div className="turn-stats mb-3">
            <div className="row text-center">
              <div className="col">
                <div className="card">
                  <div className="card-body">
                    <h5>ÌöçÎìùÌïú Îáå</h5>
                    <p className="display-4">{currentBrains}</p>
                  </div>
                </div>
              </div>
              <div className="col">
                <div className="card">
                  <div className="card-body">
                    <h5>Î∞úÍ≤¨Îêú Ï¥ù</h5>
                    <p className="display-4">{currentShotguns}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          
          {/* Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º ÏòÅÏó≠ */}
          {rolledResults.length > 0 && (
            <div className="rolled-results mb-3">
              <h4>Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º:</h4>
              <div className="dice-results-container">
                {rolledResults.map((result, index) => (
                  <div 
                    key={index} 
                    className={`dice-result result-${result.result.toLowerCase()}`}
                  >
                    <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                    <img 
                      src={`/zombie-dice/faces/${result.type.toLowerCase()}-${result.result.toLowerCase()}.png`} 
                      alt={`${result.result}`}
                      className="dice-result-image"
                    />
                    <div className="dice-result-text">{result.result}</div>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {/* Í≤ΩÍ≥† Î©îÏãúÏßÄ */}
          {currentShotguns >= 3 && (
            <div className="alert alert-danger">
              <strong>Í≤ΩÍ≥†:</strong> Ï¥ùÏù¥ 3Í∞ú Ïù¥ÏÉÅ Î∞úÍ≤¨ÎêòÏóàÏäµÎãàÎã§! ÌÑ¥ÏùÑ Ï¢ÖÎ£åÌï¥Ïïº Ìï©ÎãàÎã§.
            </div>
          )}
          
          {/* Ï£ºÏÇ¨ÏúÑ ÌíÄ ÏÉÅÌÉú */}
          <div className="dice-pool-status mb-3">
            <h4>ÎÇ®ÏùÄ Ï£ºÏÇ¨ÏúÑ: {dicePool.length}Í∞ú</h4>
            <div className="dice-counts">
              <div className="dice-count">
                <div className="dice-sample red"></div>
                <span>Îπ®Í∞Ñ Ï£ºÏÇ¨ÏúÑ: {dicePool.filter(d => d === 'RED').length}Í∞ú</span>
              </div>
              <div className="dice-count">
                <div className="dice-sample yellow"></div>
                <span>ÎÖ∏ÎûÄ Ï£ºÏÇ¨ÏúÑ: {dicePool.filter(d => d === 'YELLOW').length}Í∞ú</span>
              </div>
              <div className="dice-count">
                <div className="dice-sample green"></div>
                <span>ÎÖπÏÉâ Ï£ºÏÇ¨ÏúÑ: {dicePool.filter(d => d === 'GREEN').length}Í∞ú</span>
              </div>
            </div>
          </div>
          
          {/* Ï°∞Ïûë Î≤ÑÌäº */}
          <div className="controls">
            {isMyTurn ? (
              <>
                <button 
                  className="btn btn-primary btn-lg me-2" 
                  onClick={handleRoll} 
                  disabled={isRolling || dicePool.length < 3 || currentShotguns >= 3}
                >
                  {isRolling ? 'Íµ¥Î¶¨Îäî Ï§ë...' : 'Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞'}
                </button>
                <button 
                  className="btn btn-warning btn-lg" 
                  onClick={handleStop} 
                  disabled={isRolling}
                >
                  {currentShotguns >= 3 ? 'ÌÑ¥ Ï¢ÖÎ£å (0Ï†ê)' : `ÌÑ¥ Ï¢ÖÎ£å (${currentBrains}Ï†ê ÌöçÎìù)`}
                </button>
              </>
            ) : (
              <p className="text-center">
                {currentPlayer?.nickname}ÎãòÏùò Ï∞®Î°ÄÎ•º Í∏∞Îã§Î¶¨Í≥† ÏûàÏäµÎãàÎã§...
              </p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default ZombieDice;
