import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaLock, FaUser } from 'react-icons/fa';

function NicknameModal({ currentNickname, onSubmit, onClose }) {
  const [inputValue, setInputValue] = useState(currentNickname || '');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim()) {
      onSubmit(inputValue);
      onClose();
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>닉네임 설정</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>닉네임</label>
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              placeholder="닉네임을 입력하세요"
              required
            />
          </div>
          <div className="modal-buttons">
            <button type="submit">확인</button>
            <button type="button" onClick={onClose}>취소</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function CreateRoomModal({ onClose, onCreate }) {
  const [roomTitle, setRoomTitle] = useState('');
  const [maxPlayers, setMaxPlayers] = useState(4);
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onCreate({
      title: roomTitle,
      maxPlayers: parseInt(maxPlayers),
      password: password
    });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>방 만들기</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>방 제목</label>
            <input
              type="text"
              value={roomTitle}
              onChange={(e) => setRoomTitle(e.target.value)}
              required
              placeholder="방 제목을 입력하세요"
            />
          </div>
          <div className="form-group">
            <label>최대 인원</label>
            <select value={maxPlayers} onChange={(e) => setMaxPlayers(e.target.value)}>
              <option value="2">2명</option>
              <option value="4">4명</option>
              <option value="6">6명</option>
              <option value="8">8명</option>
            </select>
          </div>
          <div className="form-group">
            <label>비밀번호 (선택)</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="비밀번호를 입력하세요"
            />
          </div>
          <div className="modal-buttons">
            <button type="submit">만들기</button>
            <button type="button" onClick={onClose}>취소</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function Lobby({ nickname: initialNickname, setNickname }) {
  const [rooms, setRooms] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showNicknameModal, setShowNicknameModal] = useState(false);
  const [nickname, setLocalNickname] = useState(initialNickname);
  const navigate = useNavigate();

  useEffect(() => {
    fetchRooms();
    const interval = setInterval(fetchRooms, 3000);
    return () => clearInterval(interval);
  }, []);

  const fetchRooms = async () => {
    try {
      const response = await axios.get('/api/rooms');
      setRooms(response.data);
    } catch (error) {
      console.error('방 목록을 가져오는데 실패했습니다:', error);
    }
  };

  const handleNicknameChange = (newNickname) => {
    sessionStorage.setItem('nickname', newNickname);
    setLocalNickname(newNickname);
    setNickname(newNickname);
  };

  const createRoom = async (roomData) => {
    try {
      const response = await axios.post('/api/rooms', {
        creator: nickname,
        title: roomData.title,
        maxPlayers: roomData.maxPlayers,
        password: roomData.password
      });
      setShowCreateModal(false);
      navigate(`/room/${response.data.roomId}`);
    } catch (error) {
      console.error('방 생성에 실패했습니다:', error);
    }
  };

  const joinRoom = async (room) => {
    if (room.hasPassword) {
      const password = prompt('비밀번호를 입력하세요:');
      if (!password) return;
      
      try {
        await axios.post(`/api/rooms/${room.id}/check-password`, { password });
      } catch (error) {
        alert('비밀번호가 일치하지 않습니다.');
        return;
      }
    }
    navigate(`/room/${room.id}`);
  };

  return (
    <div className="lobby-container">
      <div className="lobby-header">
        <h1>게임 로비</h1>
        <div className="user-info" onClick={() => setShowNicknameModal(true)}>
          <FaUser />
          <span>{nickname}</span>
        </div>
      </div>

      <button onClick={() => setShowCreateModal(true)}>방 만들기</button>
      
      <div className="rooms-list">
        {rooms.map(room => (
          <div key={room.id} className="room-item">
            <div className="room-info">
              <span className="room-title">
                {room.hasPassword && <FaLock className="lock-icon" />}
                {room.title || `${room.players[0]}의 방`}
              </span>
              <span className="room-players">
                ({room.players.length}/{room.maxPlayers}명)
              </span>
            </div>
            <button 
              onClick={() => joinRoom(room)}
              disabled={room.players.length >= room.maxPlayers}
            >
              참가하기
            </button>
          </div>
        ))}
      </div>

      {showCreateModal && (
        <CreateRoomModal
          onClose={() => setShowCreateModal(false)}
          onCreate={createRoom}
        />
      )}

      {showNicknameModal && (
        <NicknameModal
          currentNickname={nickname}
          onSubmit={handleNicknameChange}
          onClose={() => setShowNicknameModal(false)}
        />
      )}
    </div>
  );
}

export default Lobby;
