import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';
import { FaUser, FaSignOutAlt, FaPlay, FaCheck, FaEye, FaUserSlash, FaCog } from 'react-icons/fa';
import { toast } from 'react-toastify';

const PLAYER_STATUS = {
  WAITING: 'WAITING',
  READY: 'READY',
  PLAYING: 'PLAYING',
  SPECTATING: 'SPECTATING'
};

const GAME_INFO = {
  ZOMBIE_DICE: {
    icon: '🎲',
    label: '좀비 다이스',
    color: 'success'
  },
  CAH: {
    icon: '🃏',
    label: '비인도적 카드게임',
    color: 'info'
  },
  CAH_ADULT: {
    icon: '🔞',
    label: '비인도적 카드게임 (19금)',
    color: 'danger'
  }
};

const STATUS_INFO = {
  WAITING: {
    label: '대기중',
    color: 'secondary'
  },
  IN_PROGRESS: {
    label: '진행중',
    color: 'danger'
  }
};

function BanConfirmModal({ nickname, onConfirm, onClose }) {
  return (
    <div className="modal-overlay">
      <div className="modal-content bg-white p-4 rounded-3">
        <h5 className="mb-3">플레이어 추방</h5>
        <p className="mb-4">{nickname}님을 정말로 추방하시겠습니까?</p>
        <div className="d-flex justify-content-end gap-2">
          <button 
            className="btn btn-danger"
            onClick={onConfirm}
          >
            추방
          </button>
          <button 
            className="btn btn-secondary"
            onClick={onClose}
          >
            취소
          </button>
        </div>
      </div>
    </div>
  );
}

// 방 설정 모달 컴포넌트
function RoomSettingsModal({ room, onSubmit, onClose }) {
  const [title, setTitle] = useState(room.title);
  const [maxPlayers, setMaxPlayers] = useState(room.maxPlayers);
  const [password, setPassword] = useState(room.password || '');
  const [gameType, setGameType] = useState(room.gameType);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ title, maxPlayers, password, gameType });
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content bg-white p-4 rounded-3">
        <h5 className="mb-3">방 설정</h5>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">게임</label>
            <select
              className="form-select"
              value={gameType}
              onChange={(e) => setGameType(e.target.value)}
            >
              {Object.entries(GAME_INFO).map(([type, info]) => (
                <option key={type} value={type}>
                  {info.icon} {info.label}
                </option>
              ))}
            </select>
          </div>
          <div className="mb-3">
            <label className="form-label">방 제목</label>
            <input
              type="text"
              className="form-control"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">최대 인원</label>
            <select
              className="form-select"
              value={maxPlayers}
              onChange={(e) => setMaxPlayers(Number(e.target.value))}
            >
              {Array.from({length: 19}, (_, i) => i + 2).map(num => (
                <option key={num} value={num}>{num}명</option>
              ))}
            </select>
          </div>
          <div className="mb-4">
            <label className="form-label">비밀번호 (선택)</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="비워두면 공개방"
            />
          </div>
          <div className="d-flex justify-content-end gap-2">
            <button type="submit" className="btn btn-primary">
              저장
            </button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={onClose}
            >
              취소
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

function Room({ nickname }) {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [players, setPlayers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const socketRef = useRef();
  const chatMessagesRef = useRef();
  const isLeaving = useRef(false);
  const [error, setError] = useState(null);
  const [banTargetNickname, setBanTargetNickname] = useState(null);
  const [showSettings, setShowSettings] = useState(false);
  const [gameStartTime, setGameStartTime] = useState(null);

  useEffect(() => {
    socketRef.current = io('http://localhost:3001');

    // 2. 소켓 이벤트 리스너 설정
    socketRef.current.on('roomInfo', (roomData) => {
      setRoom(roomData);
      setPlayers(roomData.players);
    });

    socketRef.current.on('error', ({ message }) => {
      toast.error(message);
      navigate('/');
    });

    socketRef.current.on('chatMessage', (message) => {
      if (message.roomId === roomId) {
        setMessages(prev => [...prev, message]);
      }
    });

    socketRef.current.on('playerJoined', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('playerLeft', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    // 추방 이벤트 처리 추가
    socketRef.current.on('playerBanned', ({ nickname: bannedNick, players: updatedPlayers }) => {
      setPlayers(updatedPlayers);
      if (bannedNick === nickname) {
        toast.error('방에서 추방되었습니다.');
        navigate('/');
      }
    });

    socketRef.current.on('gameStarted', ({ players }) => {
      setPlayers(players);
      setGameStartTime(Date.now());
    });

    socketRef.current.on('gameEnded', ({ players }) => {
      setPlayers(players);
      setGameStartTime(null);
      setRoom(prev => ({ ...prev, status: 'WAITING' }));
    });

    // 플레이어 상태 변경 이벤트 리스너 추가
    socketRef.current.on('playerStatusChanged', ({ players }) => {
      setPlayers(players);
      setRoom(prev => ({
        ...prev,
        players: players
      }));
    });

    // 3. 방 참가 요청
    socketRef.current.emit('joinRoom', { roomId, nickname });

    // 4. 클린업
    return () => {
      if (isLeaving.current) {
        socketRef.current.emit('leaveRoom', { roomId, nickname });
      }
      socketRef.current.disconnect();
    };
  }, [roomId, nickname, navigate]);

  // 새 메시지가 추가될 때마다 스크롤을 아래로 이동
  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  const leaveRoom = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/leave`, { nickname });
    } catch (error) {
      console.error('방 나가기에 실패했습니다:', error);
    }
  };

  const handleLeave = () => {
    isLeaving.current = true;
    navigate('/');
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      socketRef.current.emit('sendMessage', {
        roomId,
        nickname,
        message: inputMessage
      });
      setInputMessage('');
    }
  };

  const handleStatusChange = async (status) => {
    try {
      await axios.post(`/api/rooms/${roomId}/player-status`, {
        nickname,
        status
      });
    } catch (error) {
      console.error('상태 변경에 실패했습니다:', error);
      toast.error('상태 변경에 실패했습니다.');
    }
  };

  const handleGameStart = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/start`);
    } catch (error) {
      if (error.response?.status === 400) {
        toast.error(error.response.data.error);
      }
    }
  };

  const handleBanClick = (playerNickname) => {
    setBanTargetNickname(playerNickname);
  };

  const handleBanConfirm = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/ban`, {
        nickname: banTargetNickname,
        bannedBy: nickname
      });
      setBanTargetNickname(null);
    } catch (error) {
      if (error.response?.data?.error) {
        toast.error(error.response.data.error);
      }
    }
  };

  const handleSettingsSubmit = async (settings) => {
    try {
      await axios.post(`/api/rooms/${roomId}/settings`, settings);
      setShowSettings(false);
    } catch (error) {
      toast.error('방 설정 변경에 실패했습니다.');
    }
  };

  const isHost = players[0]?.nickname === nickname;
  const currentPlayer = players.find(p => p.nickname === nickname);
  const canInteract = room?.status === 'WAITING';

  // 채팅 영역 렌더링
  const renderChat = (isGameMode = false) => (
    <div className={`card ${isGameMode ? 'mt-3' : ''}`} style={isGameMode ? { height: '200px' } : null}>
      <div className="card-header">
        <h5 className="mb-0">채팅</h5>
      </div>
      <div className="card-body p-0 d-flex flex-column" style={{ height: isGameMode ? '200px' : '500px' }}>
        <div 
          className="chat-messages p-3 flex-grow-1 overflow-auto"
          ref={chatMessagesRef}
        >
          {messages.map((msg, index) => (
            (msg.private && msg.to !== nickname) ? null : (
              <div 
                key={index} 
                className={`mb-2 ${msg.nickname !== 'SYSTEM' && msg.nickname === nickname ? 'text-end' : ''}`}
              >
                {msg.type === 'system' ? (
                  <div className="text-center text-muted small my-2">
                    {msg.message}
                  </div>
                ) : (
                  <div
                    className={`d-inline-block p-2 rounded-3 ${
                      msg.nickname === nickname 
                        ? 'bg-primary text-white' 
                        : 'bg-light'
                    }`}
                    style={{ maxWidth: '75%' }}
                  >
                    <div className="small fw-bold mb-1">
                      {msg.nickname}
                    </div>
                    <div style={{ wordBreak: 'break-word' }}>
                      {msg.message}
                    </div>
                  </div>
                )}
              </div>
            )
          ))}
        </div>
        <form 
          onSubmit={sendMessage} 
          className="border-top p-2 mt-auto"
        >
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="메시지를 입력하세요"
            />
            <button type="submit" className="btn btn-primary">
              전송
            </button>
          </div>
        </form>
      </div>
    </div>
  );

  // 게임 영역 렌더링
  const renderGameArea = () => (
    <div className="card">
      <div className="card-header">
        <h5 className="mb-0">게임 진행중</h5>
      </div>
      <div className="card-body" style={{ height: '300px' }}>
        <div className="text-center">
          <h3>테스트 모드</h3>
          <p>게임이 시작된 지 {Math.floor((Date.now() - gameStartTime) / 1000)}초 경과</p>
          <p>10초 후 자동으로 게임이 종료됩니다.</p>
        </div>
      </div>
    </div>
  );

  if (!room) {
    return (
      <div className="container py-4">
        <div className="d-flex justify-content-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">로딩중...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container py-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <div className="d-flex align-items-center">
            <span className={`badge bg-${GAME_INFO[room.gameType].color} me-2`} style={{ fontSize: '1rem' }}>
              {GAME_INFO[room.gameType].label}
            </span>
            <span className={`badge bg-${STATUS_INFO[room.status].color} me-2`} style={{ fontSize: '0.9rem' }}>
              {STATUS_INFO[room.status].label}
            </span>
            <h2 className="mb-0" style={{ fontSize: '1.5rem' }}>
              {room.title}
            </h2>
          </div>
          <div className="d-flex gap-2">
            {/* 게임 진행중이 아닐 때만 버튼들 표시 */}
            {room.status !== 'IN_PROGRESS' && (
              <>
                {!isHost && canInteract && (
                  <>
                    {currentPlayer?.status === PLAYER_STATUS.SPECTATING ? (
                      <button 
                        className="btn btn-primary"
                        onClick={() => handleStatusChange(PLAYER_STATUS.WAITING)}
                      >
                        <FaUser className="me-2" />
                        참가하기
                      </button>
                    ) : (
                      <>
                        {currentPlayer?.status === PLAYER_STATUS.READY ? (
                          <button 
                            className="btn btn-warning"
                            onClick={() => handleStatusChange(PLAYER_STATUS.WAITING)}
                          >
                            <FaCheck className="me-2" />
                            준비 취소
                          </button>
                        ) : (
                          <>
                            <button 
                              className="btn btn-success"
                              onClick={() => handleStatusChange(PLAYER_STATUS.READY)}
                            >
                              <FaCheck className="me-2" />
                              준비
                            </button>
                            <button 
                              className="btn btn-outline-info"
                              onClick={() => handleStatusChange(PLAYER_STATUS.SPECTATING)}
                            >
                              <FaEye className="me-2" />
                              관전
                            </button>
                          </>
                        )}
                      </>
                    )}
                  </>
                )}
                {isHost && (
                  <>
                    <button 
                      className="btn btn-primary"
                      onClick={handleGameStart}
                    >
                      <FaPlay className="me-2" />
                      게임 시작
                    </button>
                    <button 
                      className="btn btn-outline-secondary"
                      onClick={() => setShowSettings(true)}
                    >
                      <FaCog className="me-2" />
                      방 설정
                    </button>
                  </>
                )}
              </>
            )}
            {/* 게임 진행중일 때는 관전자만 나가기 버튼 활성화 */}
            <button 
              className="btn btn-outline-danger"
              onClick={handleLeave}
              disabled={room.status === 'IN_PROGRESS' && currentPlayer?.status !== PLAYER_STATUS.SPECTATING}
            >
              <FaSignOutAlt className="me-2" />
              나가기
            </button>
          </div>
        </div>
        
        <div className="card-body">
          <div className="row">
            {/* 플레이어 목록 */}
            <div className="col-md-3">
              <div className="card mb-3">
                <div className="card-header d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">참가자 목록</h5>
                  <span className="badge bg-secondary">
                    {players.filter(p => p.status !== PLAYER_STATUS.SPECTATING).length}/{room.maxPlayers}명
                  </span>
                </div>
                <div className="card-body p-0">
                  <ul className="list-group list-group-flush">
                    {players
                      .filter(p => p.status !== PLAYER_STATUS.SPECTATING)
                      .map(player => (
                        <li key={player.nickname} className="list-group-item d-flex align-items-center justify-content-between">
                          <div className="d-flex align-items-center">
                            <FaUser className="me-2 text-secondary" />
                            <span>{player.nickname}</span>
                            {player.nickname === players[0].nickname && (
                              <span className="badge bg-primary ms-2">방장</span>
                            )}
                            {player.status === PLAYER_STATUS.READY && (
                              <span className="badge bg-success ms-2">준비완료</span>
                            )}
                          </div>
                          <div className="d-flex align-items-center">
                            {isHost && player.nickname !== nickname && canInteract && (
                              <button
                                className="btn btn-link text-danger p-0"
                                onClick={() => handleBanClick(player.nickname)}
                                title="추방하기"
                              >
                                <FaUserSlash size="1.2em" />
                              </button>
                            )}
                          </div>
                        </li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* 관전자 목록 */}
              <div className="card">
                <div className="card-header">
                  <h5 className="mb-0">관전자 목록</h5>
                </div>
                <div className="card-body p-0">
                  <ul className="list-group list-group-flush">
                    {players
                      .filter(p => p.status === PLAYER_STATUS.SPECTATING)
                      .map(player => (
                        <li key={player.nickname} className="list-group-item d-flex align-items-center justify-content-between">
                          <div className="d-flex align-items-center">
                            <FaEye className="me-2 text-secondary" />
                            <span>{player.nickname}</span>
                          </div>
                          {isHost && player.nickname !== nickname && canInteract && (
                            <button
                              className="btn btn-link text-danger p-0"
                              onClick={() => handleBanClick(player.nickname)}
                              title="추방하기"
                            >
                              <FaUserSlash size="1.2em" />
                            </button>
                          )}
                        </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>

            {/* 게임/채팅 영역 */}
            <div className="col-md-9">
              {room.status === 'IN_PROGRESS' ? (
                <>
                  {renderGameArea()}
                  {renderChat(true)}
                </>
              ) : (
                renderChat()
              )}
            </div>
          </div>
        </div>
      </div>

      {/* 추방 확인 모달 */}
      {banTargetNickname && (
        <BanConfirmModal
          nickname={banTargetNickname}
          onConfirm={handleBanConfirm}
          onClose={() => setBanTargetNickname(null)}
        />
      )}

      {/* 방 설정 모달 */}
      {showSettings && (
        <RoomSettingsModal
          room={room}
          onSubmit={handleSettingsSubmit}
          onClose={() => setShowSettings(false)}
        />
      )}
    </div>
  );
}

export default Room;
