import { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

function Room({ nickname }) {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const socketRef = useRef();
  const isLeaving = useRef(false); // 명시적인 퇴장 여부를 추적

  useEffect(() => {
    // 소켓 연결
    socketRef.current = io('http://localhost:3001');
    
    // 방 입장
    joinRoom();

    // 소켓 이벤트 리스너
    socketRef.current.on('playerJoined', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('playerLeft', (updatedPlayers) => {
      setPlayers(updatedPlayers);
    });

    socketRef.current.on('chatMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    return () => {
      // 명시적인 퇴장일 때만 leaveRoom 호출
      if (isLeaving.current) {
        leaveRoom();
      }
      socketRef.current.disconnect();
    };
  }, [roomId]);

  const joinRoom = async () => {
    try {
      const response = await axios.post(`/api/rooms/${roomId}/join`, { nickname });
      setPlayers(response.data.players);
    } catch (error) {
      console.error('방 참가에 실패했습니다:', error);
      navigate('/lobby');
    }
  };

  const leaveRoom = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/leave`, { nickname });
    } catch (error) {
      console.error('방 나가기에 실패했습니다:', error);
    }
  };

  const handleLeave = () => {
    isLeaving.current = true; // 명시적인 퇴장 표시
    leaveRoom();
    navigate('/lobby');
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      socketRef.current.emit('sendMessage', {
        roomId,
        nickname,
        message: inputMessage
      });
      setInputMessage('');
    }
  };

  return (
    <div className="room-container">
      <div className="room-header">
        <h1>방 #{roomId}</h1>
        <button onClick={handleLeave}>나가기</button>
      </div>
      
      <div className="room-content">
        <div className="players-list">
          <h3>참가자 목록</h3>
          {players.map(player => (
            <div key={player} className="player-item">
              {player}
            </div>
          ))}
        </div>

        <div className="chat-container">
          <div className="chat-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`message ${msg.nickname === nickname ? 'my-message' : ''}`}>
                <span className="message-nickname">{msg.nickname}</span>
                <span className="message-content">{msg.message}</span>
              </div>
            ))}
          </div>
          
          <form onSubmit={sendMessage} className="chat-input">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="메시지를 입력하세요"
            />
            <button type="submit">전송</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Room;
