import { useState, useEffect, useRef } from 'react';
import '../../styles/ZombieDice.css';

// 상수 정의
const WINNING_SCORE = 13;  // 승리 점수
const INITIAL_DICE_POOL = [
  'RED', 'RED', 'RED',
  'YELLOW', 'YELLOW', 'YELLOW', 'YELLOW',
  'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN', 'GREEN'
];

const ZombieDice = ({ players, isHost, roomId }) => {
  // 게임 상태 관리
  const [gameState, setGameState] = useState('waiting'); // waiting, playing, finished
  const [dicePool, setDicePool] = useState([]);
  const [isMyTurn, setIsMyTurn] = useState(false);
  const [currentPlayer, setCurrentPlayer] = useState(null);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const [scores, setScores] = useState({});
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [winner, setWinner] = useState(null);
  
  // 소켓 참조
  const socket = window.socket;
  
  // 로그 출력 함수
  const log = (message, data) => {
    if (data) {
      console.log(`[좀비다이스 클라이언트] ${message}`, data);
    } else {
      console.log(`[좀비다이스 클라이언트] ${message}`);
    }
  };
  
  // 게임 시작 처리
  const startGame = () => {
    log('게임 시작 요청');
    
    if (!socket) {
      log('오류: 소켓 연결이 없습니다');
      return;
    }
    
    // 서버에 게임 시작 요청 전송
    socket.emit('startZombieDice', {
      roomId,
      players: players.map(p => ({ id: p.id, nickname: p.nickname }))
    });
    
    log('게임 시작 요청 전송됨');
  };
  
  // 주사위 풀 초기화
  const resetDicePool = () => {
    setDicePool([...INITIAL_DICE_POOL]);
    log('주사위 풀 초기화');
  };
  
  // 턴 초기화
  const resetTurn = () => {
    setCurrentBrains(0);
    setCurrentShotguns(0);
    resetDicePool();
    setRolledResults([]);
    log('턴 초기화');
  };
  
  // 게임 이벤트 리스너 설정
  useEffect(() => {
    if (!socket) {
      log('소켓 연결이 없습니다');
      return;
    }
    
    // 게임 시작 이벤트 수신
    socket.on('zombieDiceStarted', (data) => {
      log('게임 시작 이벤트 수신:', data);
      
      const { players: gamePlayers, currentTurn, currentPlayer } = data;
      
      // 게임 상태 업데이트
      setGameState('playing');
      
      // 내 차례인지 확인
      const isMyTurn = currentPlayer.id === socket.id;
      setIsMyTurn(isMyTurn);
      setCurrentPlayer(currentPlayer);
      
      // 초기 점수 설정
      const initialScores = {};
      gamePlayers.forEach(p => {
        initialScores[p.id] = 0;
      });
      setScores(initialScores);
      
      // 주사위 풀 초기화
      resetDicePool();
      
      log(`게임이 시작되었습니다. ${isMyTurn ? '내 차례입니다.' : '다른 플레이어의 차례입니다.'}`);
    });
    
    // 주사위 굴림 결과 수신
    socket.on('diceRolled', (data) => {
      log('주사위 굴림 결과 수신:', data);
      
      const { 
        player, results, brains, shotguns, 
        dicePool: updatedPool, totalBrains, totalShotguns 
      } = data;
      
      // 주사위 결과 표시
      setRolledResults(results);
      
      // 내 차례인 경우 점수 업데이트
      if (player === socket.id) {
        setCurrentBrains(totalBrains || brains);
        setCurrentShotguns(totalShotguns || shotguns);
        setDicePool(updatedPool);
      }
      
      // 애니메이션 종료
      setIsRolling(false);
    });
    
    // 턴 변경 이벤트 수신
    socket.on('turnChanged', (data) => {
      log('턴 변경 이벤트 수신:', data);
      
      const { nextPlayer, scores: updatedScores } = data;
      
      // 점수 업데이트
      setScores(updatedScores);
      
      // 승자 확인
      let winner = null;
      Object.entries(updatedScores).forEach(([playerId, score]) => {
        if (score >= WINNING_SCORE) {
          winner = players.find(p => p.id === playerId);
        }
      });
      
      if (winner) {
        log('게임 종료! 승자:', winner);
        setWinner(winner);
        setGameState('finished');
        return;
      }
      
      // 내 차례인지 확인
      const isMyTurn = nextPlayer === socket.id;
      setIsMyTurn(isMyTurn);
      
      // 현재 플레이어 설정
      const nextPlayerObj = players.find(p => p.id === nextPlayer);
      setCurrentPlayer(nextPlayerObj);
      
      // 턴 초기화
      resetTurn();
      
      log(`턴이 변경되었습니다. ${isMyTurn ? '내 차례입니다.' : '다른 플레이어의 차례입니다.'}`);
    });
    
    // 게임 오류 수신
    socket.on('gameError', (data) => {
      log('게임 오류 수신:', data);
      alert('게임 오류: ' + data.message);
    });
    
    // 컴포넌트 언마운트 시 이벤트 리스너 제거
    return () => {
      socket.off('zombieDiceStarted');
      socket.off('diceRolled');
      socket.off('turnChanged');
      socket.off('gameError');
      log('이벤트 리스너 제거됨');
    };
  }, [socket, players]);
  
  // 주사위 굴리기 요청
  const handleRoll = () => {
    if (!isMyTurn || isRolling) return;
    
    setIsRolling(true);
    log('주사위 굴리기 요청');
    
    socket.emit('rollDice', {
      roomId,
      dicePool
    });
  };
  
  // 턴 종료 요청
  const handleStop = () => {
    if (!isMyTurn || isRolling) return;
    
    log('턴 종료 요청');
    
    // 총 3개 이상이면 점수 획득 불가
    const keepScore = currentShotguns < 3;
    
    socket.emit('endTurn', {
      roomId,
      keepScore
    });
  };
  
  // 게임 다시 시작
  const handleRestart = () => {
    setGameState('waiting');
    setWinner(null);
    resetTurn();
    log('게임 재시작 준비');
  };
  
  return (
    <div className="zombie-dice">
      <h2 className="text-center mb-4">좀비 다이스</h2>
      
      {/* 게임 대기 상태 */}
      {gameState === 'waiting' && (
        <div className="waiting-area text-center">
          <h3>게임 참가자</h3>
          <ul className="list-group mb-4">
            {players.map((player) => (
              <li key={player.id} className="list-group-item">
                {player.nickname} {player.id === socket.id && '(나)'}
                {player.isHost && ' (방장)'}
                {player.isReady && ' - 준비완료'}
              </li>
            ))}
          </ul>
          
          {isHost && (
            <button 
              className="btn btn-primary btn-lg" 
              onClick={startGame}
              disabled={players.filter(p => p.isReady || p.isHost).length !== players.length}
            >
              게임 시작
            </button>
          )}
          
          {!isHost && (
            <p className="alert alert-info">
              방장이 게임을 시작하기를 기다리고 있습니다.
            </p>
          )}
        </div>
      )}
      
      {/* 게임 플레이 상태 */}
      {gameState === 'playing' && !winner && (
        <>
          {/* 점수판 */}
          <div className="score-board mb-3">
            <h3 className="text-center mb-3">점수판</h3>
            <table className="table">
              <thead>
                <tr>
                  <th>플레이어</th>
                  <th>점수</th>
                  <th>상태</th>
                </tr>
              </thead>
              <tbody>
                {players.map((player) => (
                  <tr 
                    key={player.id} 
                    className={currentPlayer?.id === player.id ? 'current-player' : ''}
                  >
                    <td>{player.nickname} {player.id === socket.id && '(나)'}</td>
                    <td>{scores[player.id] || 0}</td>
                    <td>
                      {currentPlayer?.id === player.id ? '턴 진행중' : '대기중'}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          
          {/* 현재 턴 정보 */}
          <div className="current-status mb-3">
            {isMyTurn ? (
              <strong>내 차례입니다.</strong>
            ) : (
              <strong>{currentPlayer?.nickname}님의 차례입니다.</strong>
            )}
          </div>
          
          {/* 턴 통계 */}
          <div className="turn-stats mb-4">
            <div className="row">
              <div className="col-6">
                <div className="card text-center">
                  <div className="card-body">
                    <h5>먹은 뇌</h5>
                    <p className="display-4">{currentBrains}</p>
                  </div>
                </div>
              </div>
              <div className="col-6">
                <div className="card text-center">
                  <div className="card-body">
                    <h5>발견된 총</h5>
                    <p className="display-4">{currentShotguns}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
          
          {/* 주사위 결과 영역 */}
          {rolledResults.length > 0 && (
            <div className="rolled-results mb-3">
              <h4>주사위 결과:</h4>
              <div className="dice-results-container">
                {rolledResults.map((result, index) => (
                  <div 
                    key={index} 
                    className={`dice-result result-${result.result.toLowerCase()}`}
                  >
                    <div className={`dice-type ${result.type.toLowerCase()}`}></div>
                    <img 
                      src={`/zombie-dice/faces/${result.type.toLowerCase()}-${result.result.toLowerCase()}.png`} 
                      alt={`${result.result}`}
                      className="dice-result-image"
                    />
                    <div className="dice-result-text">{result.result}</div>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {/* 경고 메시지 */}
          {currentShotguns >= 3 && (
            <div className="alert alert-danger">
              <strong>경고:</strong> 총이 3개 이상 발견되었습니다! 턴을 종료해야 합니다.
            </div>
          )}
          
          {/* 주사위 풀 상태 */}
          <div className="dice-pool-status mb-3">
            <h4>남은 주사위: {dicePool.length}개</h4>
            <div className="dice-counts">
              <div className="dice-count">
                <div className="dice-sample red"></div>
                <span>빨간 주사위: {dicePool.filter(d => d === 'RED').length}개</span>
              </div>
              <div className="dice-count">
                <div className="dice-sample yellow"></div>
                <span>노란 주사위: {dicePool.filter(d => d === 'YELLOW').length}개</span>
              </div>
              <div className="dice-count">
                <div className="dice-sample green"></div>
                <span>녹색 주사위: {dicePool.filter(d => d === 'GREEN').length}개</span>
              </div>
            </div>
          </div>
          
          {/* 조작 버튼 */}
          <div className="controls">
            {isMyTurn ? (
              <>
                <button 
                  className="btn btn-primary btn-lg me-2" 
                  onClick={handleRoll} 
                  disabled={isRolling || dicePool.length < 3 || currentShotguns >= 3}
                >
                  {isRolling ? '굴리는 중...' : '주사위 굴리기'}
                </button>
                <button 
                  className="btn btn-warning btn-lg" 
                  onClick={handleStop} 
                  disabled={isRolling}
                >
                  {currentShotguns >= 3 ? '턴 종료 (0점)' : `턴 종료 (${currentBrains}점 획득)`}
                </button>
              </>
            ) : (
              <p className="text-center">
                {currentPlayer?.nickname}님의 차례를 기다리고 있습니다...
              </p>
            )}
          </div>
        </>
      )}
      
      {/* 게임 종료 상태 */}
      {gameState === 'finished' && winner && (
        <div className="winner-announcement">
          <h2>게임 종료!</h2>
          <h3>{winner.nickname}님이 승리했습니다!</h3>
          <div className="final-scores mb-4">
            <h4>최종 점수</h4>
            <table className="table">
              <thead>
                <tr>
                  <th>플레이어</th>
                  <th>점수</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(scores)
                  .sort(([, a], [, b]) => b - a)
                  .map(([playerId, score]) => {
                    const player = players.find(p => p.id === playerId);
                    return (
                      <tr key={playerId} className={winner.id === playerId ? 'table-success' : ''}>
                        <td>{player?.nickname} {playerId === socket.id && '(나)'}</td>
                        <td>{score}</td>
                      </tr>
                    );
                  })}
              </tbody>
            </table>
          </div>
          
          {isHost && (
            <button className="btn btn-primary btn-lg" onClick={handleRestart}>
              새 게임 시작
            </button>
          )}
          
          {!isHost && (
            <p className="alert alert-info">
              방장이 새 게임을 시작하기를 기다리고 있습니다.
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default ZombieDice;
