import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { SocketContext } from '../../context/SocketContext';
import { UserContext } from '../../context/UserContext';
import './ZombieDice.css';

const ZombieDice = () => {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const socket = useContext(SocketContext);
  const { user } = useContext(UserContext);

  // Í≤åÏûÑ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [gameState, setGameState] = useState({
    isStarted: false,
    players: [],
    currentTurn: null,
    dicePool: {
      red: 3,
      yellow: 4,
      green: 6
    },
    rolledDice: [],
    collectedBrains: 0,
    collectedShotguns: 0,
    turnScores: {},
    totalScores: {},
    playerOrder: [],
    winner: null
  });

  // Î°úÍ∑∏ Î©îÏãúÏßÄ Í¥ÄÎ¶¨
  const [logs, setLogs] = useState([]);

  // Î°úÍ∑∏ Ï∂îÍ∞Ä Ìï®Ïàò
  const addLog = (message) => {
    console.log(message); // ÌÑ∞ÎØ∏ÎÑêÏóê Î°úÍ∑∏ Ï∂úÎ†•
    setLogs(prevLogs => [...prevLogs, { message, timestamp: new Date().toLocaleTimeString() }]);
  };

  useEffect(() => {
    if (!socket) return;

    // Í≤åÏûÑ ÏãúÏûë Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:start', (data) => {
      addLog(`Í≤åÏûÑÏù¥ ÏãúÏûëÎêòÏóàÏäµÎãàÎã§. ÌîåÎ†àÏù¥Ïñ¥ ÏàúÏÑú: ${data.playerOrder.map(p => p.username).join(', ')}`);
      setGameState(prev => ({
        ...prev,
        isStarted: true,
        players: data.players,
        currentTurn: data.currentTurn,
        playerOrder: data.playerOrder,
        turnScores: data.turnScores || {},
        totalScores: data.totalScores || {}
      }));
    });

    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:diceRolled', (data) => {
      addLog(`${data.currentPlayer.username}ÎãòÏù¥ Ï£ºÏÇ¨ÏúÑÎ•º Íµ¥Î†∏ÏäµÎãàÎã§.`);
      addLog(`Í≤∞Í≥º: ${data.rolledDice.map(die => `${die.color} ${die.face}`).join(', ')}`);

      setGameState(prev => ({
        ...prev,
        dicePool: data.dicePool,
        rolledDice: data.rolledDice,
        collectedBrains: data.collectedBrains,
        collectedShotguns: data.collectedShotguns,
        currentTurn: data.currentTurn
      }));
    });

    // ÌÑ¥ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:turnEnd', (data) => {
      addLog(`${data.previousPlayer.username}ÎãòÏùò ÌÑ¥Ïù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§.`);
      addLog(`${data.currentPlayer.username}ÎãòÏùò ÌÑ¥Ïù¥ ÏãúÏûëÎê©ÎãàÎã§.`);

      setGameState(prev => ({
        ...prev,
        currentTurn: data.currentTurn,
        dicePool: {
          red: 3,
          yellow: 4,
          green: 6
        },
        rolledDice: [],
        collectedBrains: 0,
        collectedShotguns: 0,
        turnScores: data.turnScores,
        totalScores: data.totalScores
      }));
    });

    // Í≤åÏûÑ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:end', (data) => {
      addLog(`Í≤åÏûÑÏù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§. ÏäπÏûê: ${data.winner.username}`);
      setGameState(prev => ({
        ...prev,
        isStarted: false,
        winner: data.winner
      }));
    });

    // Í≤åÏûÑ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:stateUpdate', (data) => {
      addLog('Í≤åÏûÑ ÏÉÅÌÉúÍ∞Ä ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§.');
      setGameState(prev => ({
        ...prev,
        ...data
      }));
    });

    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
    return () => {
      socket.off('game:start');
      socket.off('game:diceRolled');
      socket.off('game:turnEnd');
      socket.off('game:end');
      socket.off('game:stateUpdate');
    };
  }, [socket]);

  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Ìï®Ïàò
  const rollDice = () => {
    addLog('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉÖÎãàÎã§.');
    socket.emit('game:rollDice', { roomId, dicePool: gameState.dicePool });
  };

  // ÌÑ¥ Ï¢ÖÎ£å Ìï®Ïàò
  const endTurn = () => {
    addLog('ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉÖÎãàÎã§.');
    socket.emit('game:endTurn', { roomId });
