import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { SocketContext } from '../../context/SocketContext';
import { UserContext } from '../../context/UserContext';
import './ZombieDice.css';

const ZombieDice = () => {
  const { roomId } = useParams();
  const socket = useContext(SocketContext);
  const { user } = useContext(UserContext);
  
  // Í≤åÏûÑ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [gameState, setGameState] = useState({
    isStarted: false,
    players: [],
    currentTurn: null,
    dicePool: {
      red: 3,
      yellow: 4,
      green: 6
    },
    rolledDice: [],
    collectedBrains: 0,
    collectedShotguns: 0,
    turnScores: {},
    totalScores: {},
    playerOrder: []
  });
  
  // Î°úÍ∑∏ Î©îÏãúÏßÄ Í¥ÄÎ¶¨
  const [logs, setLogs] = useState([]);
  
  // Î°úÍ∑∏ Ï∂îÍ∞Ä Ìï®Ïàò
  const addLog = (message) => {
    console.log(`[ZombieDice] ${message}`); // ÌÑ∞ÎØ∏ÎÑêÏóê Î°úÍ∑∏ Ï∂úÎ†•
    setLogs(prevLogs => [...prevLogs, { message, timestamp: new Date().toLocaleTimeString() }]);
  };

  useEffect(() => {
    if (!socket) return;
    
    // Í≤åÏûÑ ÏãúÏûë Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:start', (data) => {
      addLog(`Í≤åÏûÑÏù¥ ÏãúÏûëÎêòÏóàÏäµÎãàÎã§.`);
      addLog(`ÌîåÎ†àÏù¥Ïñ¥ ÏàúÏÑú: ${data.players.map(p => p.nickname).join(' -> ')}`);
      addLog(`ÏÑ† ÌîåÎ†àÏù¥Ïñ¥: ${data.currentTurn.nickname}`);
      
      setGameState(prev => ({
        ...prev,
        isStarted: true,
        players: data.players,
        currentTurn: data.currentTurn,
        playerOrder: data.players,
        turnScores: data.turnScores || {},
        totalScores: data.totalScores || {}
      }));
    });
    
    // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Í≤∞Í≥º Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:diceRolled', (data) => {
      addLog(`${data.currentPlayer.nickname}ÎãòÏù¥ Ï£ºÏÇ¨ÏúÑÎ•º Íµ¥Î†∏ÏäµÎãàÎã§.`);
      addLog(`Í≤∞Í≥º: ${data.rolledDice.map(die => `${die.color} ${die.face}`).join(', ')}`);
      
      setGameState(prev => ({
        ...prev,
        dicePool: data.dicePool,
        rolledDice: data.rolledDice,
        collectedBrains: data.collectedBrains,
        collectedShotguns: data.collectedShotguns,
        currentTurn: data.currentTurn
      }));
    });
    
    // ÌÑ¥ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:turnEnd', (data) => {
      addLog(`${data.previousPlayer.nickname}ÎãòÏùò ÌÑ¥Ïù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§.`);
      addLog(`${data.currentPlayer.nickname}ÎãòÏùò ÌÑ¥Ïù¥ ÏãúÏûëÎê©ÎãàÎã§.`);
      
      setGameState(prev => ({
        ...prev,
        currentTurn: data.currentTurn,
        dicePool: {
          red: 3,
          yellow: 4,
          green: 6
        },
        rolledDice: [],
        collectedBrains: 0,
        collectedShotguns: 0,
        turnScores: data.turnScores,
        totalScores: data.totalScores
      }));
    });
    
    // Í≤åÏûÑ Ï¢ÖÎ£å Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà
    socket.on('game:end', (data) => {
      addLog(`Í≤åÏûÑÏù¥ Ï¢ÖÎ£åÎêòÏóàÏäµÎãàÎã§.`);
      addLog(`ÏäπÏûê: ${data.winner.nickname} (${data.scores[data.winner.nickname]}Ï†ê)`);
      
      setGameState(prev => ({
        ...prev,
        isStarted: false,
        winner: data.winner,
        totalScores: data.scores
      }));
    });

    // Ïª¥Ìè¨ÎÑåÌä∏ Ïñ∏ÎßàÏö¥Ìä∏ Ïãú Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Ï†úÍ±∞
    return () => {
      socket.off('game:start');
      socket.off('game:diceRolled');
      socket.off('game:turnEnd');
      socket.off('game:end');
    };
  }, [socket]);

  // Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ Ìï®Ïàò
  const rollDice = () => {
    if (!isCurrentPlayer()) {
      toast.error('ÎãπÏã†Ïùò ÌÑ¥Ïù¥ ÏïÑÎãôÎãàÎã§.');
      return;
    }
    addLog('Ï£ºÏÇ¨ÏúÑ Íµ¥Î¶¨Í∏∞ ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉÖÎãàÎã§.');
    socket.emit('game:rollDice', { roomId });
  };

  // ÌÑ¥ Ï¢ÖÎ£å Ìï®Ïàò
  const endTurn = () => {
    if (!isCurrentPlayer()) {
      toast.error('ÎãπÏã†Ïùò ÌÑ¥Ïù¥ ÏïÑÎãôÎãàÎã§.');
      return;
    }
    addLog('ÌÑ¥ Ï¢ÖÎ£å ÏöîÏ≤≠ÏùÑ Î≥¥ÎÉÖÎãàÎã§.');
    socket.emit('game:endTurn', { roomId });
  };

  // ÌòÑÏû¨ ÌîåÎ†àÏù¥Ïñ¥Ïù∏ÏßÄ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
  const isCurrentPlayer = () => {
    return gameState.currentTurn && gameState.currentTurn.nickname === user.nickname;
  };

  // Ï£ºÏÇ¨ÏúÑ ÏÉâÏÉÅÏóê Îî∞Î•∏ Ïä§ÌÉÄÏùº ÌÅ¥ÎûòÏä§ Î∞òÌôò
  const getDiceColorClass = (color) => {
    switch (color.toLowerCase()) {
      case 'red': return 'dice-red';
      case 'yellow': return 'dice-yellow';
      case 'green': return 'dice-green';
      default: return '';
    }
  };

  // Ï£ºÏÇ¨ÏúÑ Î©¥Ïóê Îî∞Î•∏ ÏïÑÏù¥ÏΩò Î∞òÌôò
  const getDiceFaceIcon = (face) => {
    switch (face.toLowerCase()) {
      case 'brain': return 'üß†';
      case 'shotgun': return 'üí•';
      case 'footsteps': return 'üë£';
      default: return '';
    }
  };

  return (
    <div className="zombie-dice-game">
      {/* ÌîåÎ†àÏù¥Ïñ¥ Ï†ïÎ≥¥ */}
      <div className="players-info mb-4">
        <h3>ÌîåÎ†àÏù¥Ïñ¥ Ï†êÏàò</h3>
        <div className="player-list">
          {gameState.players.map(player => (
            <div 
              key={player.nickname}
              className={`player-item ${gameState.currentTurn?.nickname === player.nickname ? 'current-turn' : ''}`}
            >
              <span className="player-name">{player.nickname}</span>
              <span className="player-score">
                {gameState.totalScores[player.nickname] || 0}Ï†ê
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Í≤åÏûÑ Î≥¥Îìú */}
      <div className="game-board mb-4">
        <div className="dice-pool mb-3">
          <h4>Ï£ºÏÇ¨ÏúÑ ÌíÄ</h4>
          <div className="dice-counts">
            <span className="dice-red">Îπ®Í∞ï: {gameState.dicePool.red}</span>
            <span className="dice-yellow">ÎÖ∏Îûë: {gameState.dicePool.yellow}</span>
            <span className="dice-green">Ï¥àÎ°ù: {gameState.dicePool.green}</span>
          </div>
        </div>

        {/* Íµ¥Î¶∞ Ï£ºÏÇ¨ÏúÑ Í≤∞Í≥º */}
        <div className="rolled-dice mb-3">
