import { useState, useEffect, useRef } from 'react';
import '../styles/ZombieDice.css';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import redDice from '../../assets/zombie-dice/dice-red.png';
import yellowDice from '../../assets/zombie-dice/dice-yellow.png';
import greenDice from '../../assets/zombie-dice/dice-green.png';

// 승리 점수
const WINNING_SCORE = 13;

// 주사위 면 정의
const DICE_FACES = {
  RED: {
    color: '#ff5252',
    name: '빨간 주사위',
    image: redDice,
    faces: [
      { image: redBrain, type: 'SHOTGUN' },      // 1면
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2면
      { image: redFootsteps, type: 'SHOTGUN' }, // 3면
      { image: redShotgun, type: 'BRAIN' },   // 4면
      { image: redShotgun, type: 'FOOTSTEPS' },   // 5면
      { image: redShotgun, type: 'SHOTGUN' }    // 6면
    ]
  },
  YELLOW: {
    color: '#ffeb3b',
    name: '노란 주사위',
    image: yellowDice,
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1면
      { image: yellowBrain, type: 'FOOTSTEPS' },     // 2면
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 3면
      { image: yellowFootsteps, type: 'SHOTGUN' }, // 4면
      { image: yellowShotgun, type: 'FOOTSTEPS' },  // 5면
      { image: yellowShotgun, type: 'BRAIN' }   // 6면
    ]
  },
  GREEN: {
    color: '#4caf50',
    name: '녹색 주사위',
    image: greenDice,
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1면
      { image: greenBrain, type: 'FOOTSTEPS' },     // 2면
      { image: greenBrain, type: 'BRAIN' },     // 3면
      { image: greenFootsteps, type: 'SHOTGUN' }, // 4면
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5면
      { image: greenShotgun, type: 'SHOTGUN' }   // 6면
    ]
  }
};

// 개선된 3D 스타일 주사위 컴포넌트
const EnhancedDice = ({ type, isRolling, result, faceIndex }) => {
  const diceRef = useRef(null);
  const [currentFace, setCurrentFace] = useState(0);
  const diceFaces = DICE_FACES[type]?.faces || [];
  
  // 주사위 굴리기 애니메이션
  useEffect(() => {
    if (isRolling && diceRef.current) {
      // 빠르게 면을 바꾸는 애니메이션
      const faceChangeInterval = setInterval(() => {
        setCurrentFace(prev => (prev + 1) % 6);
      }, 150); // 0.15초마다 면 변경
      
      // 3D 회전 애니메이션
      diceRef.current.style.animation = 'rollDice 2s cubic-bezier(0.17, 0.84, 0.44, 1)';
      
      return () => {
        clearInterval(faceChangeInterval);
      };
    } else if (!isRolling && faceIndex !== undefined) {
      // 굴리기가 끝나면 결과 면으로 설정
      setCurrentFace(faceIndex);
      if (diceRef.current) {
        diceRef.current.style.animation = 'none';
      }
    }
  }, [isRolling, faceIndex]);
  
  // 주사위 배경색 설정
  const diceColor = DICE_FACES[type]?.color || '#ffffff';
  
  return (
    <div 
      ref={diceRef}
      className={`enhanced-dice ${isRolling ? 'rolling' : ''}`}
      style={{ 
        backgroundColor: diceColor
      }}
    >
      <div className="dice-inner">
        {/* 주사위 면 이미지 */}
        <img 
          src={diceFaces[currentFace]?.image} 
          alt={`${type} 주사위 ${diceFaces[currentFace]?.type}`}
          className="dice-face-image"
        />
      </div>
    </div>
  );
};

// 주사위 애니메이션 영역 (개선된 버전)
const DiceAnimationArea = ({ selectedDice, isRolling, onAnimationComplete, rolledResults }) => {
  // 애니메이션 완료 처리
  useEffect(() => {
    if (isRolling) {
      const timer = setTimeout(() => {
        // 주사위 결과 생성 (이미 결과가 있으면 사용, 없으면 새로 생성)
        if (!rolledResults && onAnimationComplete) {
          const results = selectedDice.map(diceType => {
            // 주사위 타입에 따른 면 정보 가져오기
            const diceFaces = DICE_FACES[diceType].faces;
            
            // 랜덤하게 면 선택 (0-5)
            const faceIndex = Math.floor(Math.random() * 6);
            const selectedFace = diceFaces[faceIndex];
            
            return {
              type: diceType,
              result: selectedFace.type,
              faceIndex: faceIndex
            };
          });
          
          onAnimationComplete(results);
        }
      }, 2500); // 2.5초 후 결과 처리
      
      return () => clearTimeout(timer);
    }
  }, [isRolling, selectedDice, onAnimationComplete, rolledResults]);
  
  return (
    <div className="enhanced-dice-animation-area">
      <div className="dice-container">
        {selectedDice.map((diceType, index) => {
          // 결과가 있으면 해당 결과의 faceIndex 사용
          const resultItem = rolledResults && rolledResults[index];
          const faceIndex = resultItem ? resultItem.faceIndex : undefined;
          
          return (
            <EnhancedDice 
              key={index}
              type={diceType}
              isRolling={isRolling}
              faceIndex={faceIndex}
            />
          );
        })}
      </div>
    </div>
  );
};

// 개선된 주사위 풀 상태 컴포넌트
const DicePoolStatus = ({ dicePool }) => {
  // 색상별 주사위 카운트
  const redCount = dicePool.filter(dice => dice === 'RED').length;
  const yellowCount = dicePool.filter(dice => dice === 'YELLOW').length;
  const greenCount = dicePool.filter(dice => dice === 'GREEN').length;
  
  return (
    <div className="dice-pool-status">
      <h4>남은 주사위</h4>
      <div className="dice-counts">
        <div className="dice-count">
          <img src={redDice} alt="빨간 주사위" className="dice-pool-icon" />
          <span>빨간 주사위: {redCount}개</span>
        </div>
        <div className="dice-count">
          <img src={yellowDice} alt="노란 주사위" className="dice-pool-icon" />
          <span>노란 주사위: {yellowCount}개</span>
        </div>
        <div className="dice-count">
          <img src={greenDice} alt="녹색 주사위" className="dice-pool-icon" />
          <span>녹색 주사위: {greenCount}개</span>
        </div>
      </div>
    </div>
  );
};

// 주사위 결과 표시 컴포넌트 개선
const DiceResult = ({ result }) => {
  // 결과에 따른 이미지 가져오기
  const getFaceImage = () => {
    const diceType = result.type;
    const faceIndex = result.faceIndex || 0;
    return DICE_FACES[diceType]?.faces[faceIndex]?.image;
  };
  
  // 결과에 따른 텍스트
  const getResultText = () => {
    if (result.result === 'BRAIN') return '뇌';
    if (result.result === 'SHOTGUN') return '총';
    if (result.result === 'FOOTSTEPS') return '발자국';
    return '';
  };
  
  return (
    <div className={`dice-result ${result.result.toLowerCase()}`}>
      <div className={`dice-type ${result.type.toLowerCase()}`}></div>
      <img src={getFaceImage()} alt={getResultText()} className="dice-result-image" />
      <div className="dice-face">{getResultText()}</div>
    </div>
  );
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  // 상태 정의
  const [dicePool, setDicePool] = useState([]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [isRolling, setIsRolling] = useState(false);
  const [winner, setWinner] = useState(null);
  const [gameStarted, setGameStarted] = useState(false);
  const [showDiceAnimation, setShowDiceAnimation] = useState(false);
  
  // 점수판 렌더링
  const renderScoreBoard = () => {
    if (!players) return null;
    
    return (
      <div className="score-board">
        <table className="table">
          <thead>
            <tr>
              <th>플레이어</th>
              <th>점수</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(players).map(([id, player]) => (
              <tr 
                key={id} 
                className={parseInt(id) === currentTurn ? 'current-player' : ''}
              >
                <td>{player.nickname}</td>
                <td>{player.score || 0}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };
  
  // 주사위 굴리기 요청
  const handleRoll = () => {
    console.log('주사위 굴리기 요청');
    
    // 주사위 굴리기 상태 설정
    setIsRolling(true);
    setShowDiceAnimation(true);
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      // 주사위 선택 요청 - 서버에 전송하여 모든 플레이어가 볼 수 있게 함
      window.socket.emit('gameAction', {
        action: 'selectDice',
        roomId,
        requestRoll: true // 주사위 굴리기도 요청
      });
    } else {
      console.error('소켓 연결이 없습니다!');
      setIsRolling(false);
      setShowDiceAnimation(false);
    }
  };
  
  // 주사위 굴리기 결과 처리
  const handleRollComplete = (results) => {
    console.log('주사위 굴리기 완료:', results);
    
    // 주사위 굴리기 상태 종료 (애니메이션은 잠시 유지)
    setIsRolling(false);
    
    // 결과 표시 및 유지를 위해 지연 후 애니메이션 영역 숨김
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000);
    
    if (!results || results.length === 0) {
      return;
    }
    
    // 주사위 결과 저장
    setRolledResults(results);
    
    // 뇌와 총 개수 집계
    let brains = currentBrains;
    let shotguns = currentShotguns;
    
    results.forEach(result => {
      if (result.result === 'BRAIN') {
        brains++;
      } else if (result.result === 'SHOTGUN') {
        shotguns++;
      }
    });
    
    setCurrentBrains(brains);
    setCurrentShotguns(shotguns);
    
    // 게임 액션 서버에 전송
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'rollDiceResult',
        roomId,
        results,
        brains,
        shotguns
      });
    }
  };
  
  // 계속 굴리기
  const handleContinue = () => {
    console.log('계속 굴리기');
    
    // 주사위 굴리기 상태 설정
    handleRoll();
  };
  
  // 턴 종료 처리
  const handleStop = () => {
    console.log('턴 종료');
    
    // 총이 3개 이상이면 점수 획득 없음
    const score = currentShotguns >= 3 ? 0 : currentBrains;
    
    // 게임 액션 서버에 전송
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      window.socket.emit('gameAction', {
        action: 'endTurn',
        roomId,
        score
      });
    }
    
    // 턴 종료 콜백 호출
    if (onTurnEnd) {
      onTurnEnd(score);
    }
  };
  
  // 소켓 이벤트 핸들러
  const handleDiceSelected = (data) => {
    console.log('주사위 선택됨:', data);
    const { selectedDice: newSelectedDice, dicePool: newDicePool, requestRoll } = data;
    
    setSelectedDice(newSelectedDice);
    setDicePool(newDicePool);
    
    if (requestRoll) {
      // 주사위 굴리기 애니메이션 시작 - 모든 플레이어에게 표시
      setShowDiceAnimation(true);
      setIsRolling(true);
    }
  };
  
  const handleDiceRolling = (data) => {
    console.log('주사위 굴리는 중:', data);
    // 모든 플레이어에게 주사위 굴리기 애니메이션 표시
    setShowDiceAnimation(true);
    setIsRolling(true);
    // 선택된 주사위 정보 업데이트
    if (data.selectedDice) {
      setSelectedDice(data.selectedDice);
    }
  };
  
  const handleRollDiceResult = (data) => {
    console.log('주사위 결과 수신:', data);
    
    // 굴리기 애니메이션 종료
    setIsRolling(false);
    
    // 결과 표시를 위해 애니메이션 영역은 잠시 유지
    setTimeout(() => {
      setShowDiceAnimation(false);
    }, 1000);
    
    const { results, brains, shotguns } = data;
    
    if (results) {
      setRolledResults(results);
      setCurrentBrains(brains);
      setCurrentShotguns(shotguns);
    }
  };
  
  const handleTurnStarted = (data) => {
    console.log('턴 시작됨:', data);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
  };
  
  const handleTurnEnded = (data) => {
    console.log('턴 종료됨:', data);
  };
  
  const handleGameEnded = (data) => {
    console.log('게임 종료됨:', data);
    setWinner(data.winner);
  };
  
  // 소켓 이벤트 리스너 등록
  useEffect(() => {
    if (window.socket) {
      window.socket.on('diceSelected', handleDiceSelected);
      window.socket.on('diceRolling', handleDiceRolling);
      window.socket.on('rollDiceResult', handleRollDiceResult);
      window.socket.on('turnStarted', handleTurnStarted);
      window.socket.on('turnEnded', handleTurnEnded);
      window.socket.on('gameEnded', handleGameEnded);
      
      return () => {
        window.socket.off('turnStarted', handleTurnStarted);
        window.socket.off('diceSelected', handleDiceSelected);
        window.socket.off('rollDiceResult', handleRollDiceResult);
        window.socket.off('turnEnded', handleTurnEnded);
        window.socket.off('gameEnded', handleGameEnded);
        window.socket.off('diceRolling', handleDiceRolling);
      };
    }
  }, [onTurnEnd, isMyTurn, isRolling]);
  
  // 게임 초기화
  useEffect(() => {
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      // 게임 상태 요청
      window.socket.emit('getGameState', { roomId });
      
      window.socket.on('gameState', (gameState) => {
        if (gameState) {
          setDicePool(gameState.dicePool || []);
          setGameStarted(true);
          
          if (gameState.currentPlayer) {
            setCurrentBrains(gameState.currentPlayer.brains || 0);
            setCurrentShotguns(gameState.currentPlayer.shotguns || 0);
          }
        }
      });
      
      return () => {
        window.socket.off('gameState');
      };
    }
  }, []);
  
  // 메인 렌더링
  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>🏆 {winner.nickname}님이 승리하셨습니다! 🏆</h3>
        </div>
      ) : (
        <>
          {isMyTurn ? (
            <>
              <div className="text-center mb-3">
                <h3 className="my-turn-indicator">당신의 차례입니다!</h3>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>획득한 뇌: {currentBrains}</div>
                  <div>샷건 횟수: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation ? (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">주사위 굴리는 중...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={handleRollComplete}
                    rolledResults={!isRolling ? rolledResults : null}
                  />
                </div>
              ) : (
                <div className="dice-area mb-3">
                  {isRolling ? (
                    <div className="text-center">
                      <p>주사위 굴리는 중...</p>
                    </div>
                  ) : (
                    rolledResults.length > 0 ? (
                      <div className="rolled-results">
                        <h4>주사위 결과:</h4>
                        <div className="dice-results-container">
                          {rolledResults.map((result, index) => (
                            <DiceResult key={index} result={result} />
                          ))}
                        </div>
                      </div>
                    ) : (
                      <button 
                        className="btn btn-primary btn-lg"
                        onClick={handleRoll}
                        disabled={isRolling}
                      >
                        주사위 굴리기
                      </button>
                    )
                  )}
                </div>
              )}
            </>
          ) : (
            <>
              <div className="text-center mb-3">
                <h3>다른 플레이어의 차례입니다.</h3>
                <p>{players[currentTurn]?.nickname}님이 주사위를 굴리고 있습니다...</p>
              </div>
              
              <div className="current-status mb-3">
                <div className="d-flex justify-content-around">
                  <div>획득한 뇌: {currentBrains}</div>
                  <div>샷건 횟수: {currentShotguns}/3</div>
                </div>
              </div>
              
              {showDiceAnimation && (
                <div className="dice-animation-container mb-3">
                  <h4 className="text-center">주사위 굴리는 중...</h4>
                  <DiceAnimationArea 
                    selectedDice={selectedDice}
                    isRolling={isRolling}
                    onAnimationComplete={null} // 관전자는 결과 처리하지 않음
                    rolledResults={!isRolling ? rolledResults : null}
                  />
                </div>
              )}
              
              {rolledResults.length > 0 && !isRolling && !showDiceAnimation && (
                <div className="rolled-results">
                  <h4>주사위 결과:</h4>
                  <div className="dice-results-container">
                    {rolledResults.map((result, index) => (
                      <DiceResult key={index} result={result} />
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
          
          {rolledResults.length > 0 && !winner && !isRolling && !showDiceAnimation && isMyTurn && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                계속하기
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                {currentShotguns >= 3 ? "턴 종료 (0점)" : "멈추기"}
              </button>
            </div>
          )}
          
          {currentShotguns >= 3 && (
            <div className="alert alert-danger mt-3">
              <strong>총이 3개 이상!</strong> 이번 턴에 획득한 뇌를 모두 잃었습니다. 턴을 종료해주세요.
            </div>
          )}
          
          <DicePoolStatus dicePool={dicePool} />
        </>
      )}
    </div>
  );
}

export default ZombieDice;
