import { useState, useEffect, useRef } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import '../styles/ZombieDice.css';

// 승리 점수
const WINNING_SCORE = 13;

// 주사위 면 정의
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'BRAIN' },      // 1면
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2면
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 3면
      { image: redShotgun, type: 'SHOTGUN' },   // 4면
      { image: redShotgun, type: 'SHOTGUN' },   // 5면
      { image: redShotgun, type: 'SHOTGUN' }    // 6면
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1면
      { image: yellowBrain, type: 'BRAIN' },     // 2면
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 3면
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 4면
      { image: yellowShotgun, type: 'SHOTGUN' },  // 5면
      { image: yellowShotgun, type: 'SHOTGUN' }   // 6면
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1면
      { image: greenBrain, type: 'BRAIN' },     // 2면
      { image: greenBrain, type: 'BRAIN' },     // 3면
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 4면
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5면
      { image: greenShotgun, type: 'SHOTGUN' }   // 6면
    ]
  }
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  const [dicePool, setDicePool] = useState([]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [isRolling, setIsRolling] = useState(false);
  const [winner, setWinner] = useState(null);
  const diceContainerRef = useRef(null);

  // 주사위 선택 요청
  const handleSelectDice = () => {
    console.log('주사위 선택 요청');
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      window.socket.emit('gameAction', {
        action: 'rollDice',
        selectDice: true,
        roomId
      });
    } else {
      console.error('소켓 연결이 없습니다!');
    }
  };

  // 주사위 굴리기 요청
  const handleRoll = () => {
    console.log('주사위 굴리기 요청');
    
    // 선택된 주사위가 없으면 먼저 선택
    if (selectedDice.length === 0) {
      handleSelectDice();
      return;
    }
    
    // 주사위 굴리기 요청
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      window.socket.emit('gameAction', {
        action: 'rollDice',
        roomId
      });
      
      setIsRolling(true);
    } else {
      console.error('소켓 연결이 없습니다!');
    }
  };

  // 턴 종료 요청
  const handleStop = () => {
    // 샷건이 3개 이상이면 점수 0으로 처리
    const finalScore = currentShotguns >= 3 ? 0 : currentBrains;
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      window.socket.emit('endTurn', {
        roomId,
        score: finalScore
      });
    } else {
      console.error('소켓 연결이 없습니다!');
    }
    
    // 로컬 콜백 호출
    onTurnEnd(finalScore);
  };

  // 계속하기 요청
  const handleContinue = () => {
    // 도망간 주사위 찾기
    const footstepsDice = rolledResults
      .filter(result => result.result === 'FOOTSTEPS')
      .map(result => result.type);
    
    if (window.socket) {
      const roomId = window.location.pathname.split('/')[2];
      
      window.socket.emit('continueTurn', {
        roomId,
        footstepsDice
      });
      
      // 이전 결과에서 도망이 아닌 주사위만 유지
      const nonFootstepsResults = rolledResults.filter(result => result.result !== 'FOOTSTEPS');
      setRolledResults(nonFootstepsResults);
    } else {
      console.error('소켓 연결이 없습니다!');
    }
  };

  // 소켓 이벤트 리스너
  useEffect(() => {
    if (window.socket) {
      console.log('소켓 이벤트 리스너 등록');
      
      // 게임 시작 이벤트
      const handleGameStarted = (data) => {
        console.log('게임 시작:', data);
        
        // 서버에서 받은 게임 상태로 초기화
        if (data.gameState) {
          setDicePool(data.gameState.dicePool || []);
          setCurrentBrains(0);
          setCurrentShotguns(0);
          setRolledResults([]);
          setSelectedDice([]);
          setWinner(null);
        }
      };
      
      // 주사위 선택 이벤트
      const handleDiceSelected = (data) => {
        console.log('주사위 선택됨:', data);
        
        setSelectedDice(data.selectedDice || []);
        setDicePool(data.dicePool || []);
      };
      
      // 주사위 굴리기 결과 이벤트
      const handleGameAction = (data) => {
        console.log('게임 액션 수신:', data);
        
        if (data.action === 'rollDice') {
          console.log('주사위 결과 수신:', data.results);
          
          // 결과 표시
          setRolledResults(data.results);
          setIsRolling(true);
          
          // 주사위 풀 업데이트
          setDicePool(data.dicePool || []);
          
          // 애니메이션 후 결과 처리
          setTimeout(() => {
            // 내 턴일 때만 결과 처리
            if (isMyTurn) {
              // 결과 처리 - 뇌와 샷건 개수 누적
              let newBrains = currentBrains;
              let newShotguns = currentShotguns;
              
              data.results.forEach(({ result }) => {
                if (result === 'BRAIN') newBrains++;
                if (result === 'SHOTGUN') newShotguns++;
              });
              
              // 상태 업데이트
              setCurrentBrains(newBrains);
              setCurrentShotguns(newShotguns);
              
              // 현재 상태를 서버에 전송
              window.socket.emit('updateTurnStatus', {
                roomId: window.location.pathname.split('/')[2],
                currentBrains: newBrains,
                currentShotguns: newShotguns
              });
            }
            
            setIsRolling(false);
          }, 2500);
        }
      };
      
      // 턴 상태 업데이트 이벤트
      const handleTurnStatusUpdated = (data) => {
        console.log('턴 상태 업데이트:', data);
        
        // 내 턴이 아닐 때만 상태 업데이트
        if (!isMyTurn) {
          setCurrentBrains(data.currentBrains);
          setCurrentShotguns(data.currentShotguns);
        }
        
        // 주사위 풀 업데이트
        setDicePool(data.dicePool || []);
      };
      
      // 게임 상태 업데이트 이벤트
      const handleGameStateUpdated = (gameState) => {
        console.log('게임 상태 업데이트:', gameState);
        
        // 주사위 풀 업데이트
        setDicePool(gameState.dicePool || []);
        
        // 턴 상태 초기화
        setCurrentBrains(gameState.currentBrains || 0);
        setCurrentShotguns(gameState.currentShotguns || 0);
        setRolledResults([]);
        setSelectedDice([]);
      };
      
      // 게임 종료 이벤트
      const handleGameEnded = (data) => {
        console.log('게임 종료:', data);
        
        setWinner(data.winner);
      };
      
      // 게임 에러 이벤트
      const handleGameError = (data) => {
        console.error('게임 에러:', data.message);
        
        // 에러 처리
        alert(`게임 에러: ${data.message}`);
      };
      
      // 이벤트 리스너 등록
      window.socket.on('gameStarted', handleGameStarted);
      window.socket.on('diceSelected', handleDiceSelected);
      window.socket.on('gameAction', handleGameAction);
      window.socket.on('turnStatusUpdated', handleTurnStatusUpdated);
      window.socket.on('gameStateUpdated', handleGameStateUpdated);
      window.socket.on('gameEnded', handleGameEnded);
      window.socket.on('gameError', handleGameError);
      
      // 클린업
      return () => {
        console.log('소켓 이벤트 리스너 제거');
        window.socket.off('gameStarted', handleGameStarted);
        window.socket.off('diceSelected', handleDiceSelected);
        window.socket.off('gameAction', handleGameAction);
        window.socket.off('turnStatusUpdated', handleTurnStatusUpdated);
        window.socket.off('gameStateUpdated', handleGameStateUpdated);
        window.socket.off('gameEnded', handleGameEnded);
        window.socket.off('gameError', handleGameError);
      };
    }
  }, [currentBrains, currentShotguns, isMyTurn, onTurnEnd]);

  // 주사위 풀 상태 표시 컴포넌트
  const DicePoolStatus = ({ dicePool }) => {
    // 주사위 색상별 개수 계산
    const redCount = dicePool.filter(dice => dice === 'RED').length;
    const yellowCount = dicePool.filter(dice => dice === 'YELLOW').length;
    const greenCount = dicePool.filter(dice => dice === 'GREEN').length;
    
    return (
      <div className="dice-pool-status mt-3">
        <h5>남은 주사위:</h5>
        <div className="dice-pool-counts">
          <div className="dice-count red">
            <span className="dice-color-indicator red"></span>
            빨간색: {redCount}개
          </div>
          <div className="dice-count yellow">
            <span className="dice-color-indicator yellow"></span>
            노란색: {yellowCount}개
          </div>
          <div className="dice-count green">
            <span className="dice-color-indicator green"></span>
            초록색: {greenCount}개
          </div>
        </div>
      </div>
    );
  };

  // 점수판 렌더링
  const renderScoreBoard = () => {
    return (
      <div className="score-board mb-4">
        <h4 className="text-center mb-3">점수판</h4>
        <div className="row">
          {players.map((player, index) => (
            <div key={index} className={`col-md-${12 / players.length} mb-2`}>
              <div className={`card ${index === currentTurn ? 'bg-light' : ''}`}>
                <div className="card-body text-center">
                  <h5 className="card-title">{player.nickname}</h5>
                  <p className="card-text">점수: {player.score || 0}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  // 다른 플레이어의 턴일 때 화면
  if (!isMyTurn) {
    return (
      <div className="zombie-dice">
        {renderScoreBoard()}
        <div className="text-center mb-3">
          <h3>{players[currentTurn].nickname}님의 차례입니다</h3>
        </div>
        
        <div className="current-status mb-3">
          <div className="d-flex justify-content-around">
            <div>획득한 뇌: {currentBrains}</div>
            <div>샷건 횟수: {currentShotguns}/3</div>
          </div>
        </div>

        <div className="dice-area mb-3">
          {isRolling ? (
            <div className="text-center">
              <p>주사위 굴리는 중...</p>
            </div>
          ) : (
            rolledResults.length > 0 && (
              rolledResults.map((result, index) => (
                <Dice 
                  key={index}
                  type={result.type}
                  result={result.result}
                  isRolling={isRolling}
                />
              ))
            )
          )}
        </div>

        <DicePoolStatus dicePool={dicePool} />
      </div>
    );
  }

  // 내 턴일 때 화면
  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>🏆 {winner.nickname}님이 승리하셨습니다! 🏆</h3>
        </div>
      ) : (
        <>
          <div className="text-center mb-3">
            <h3 className="my-turn-indicator">당신의 차례입니다!</h3>
          </div>
          
          <div className="current-status mb-3">
            <div className="d-flex justify-content-around">
              <div>획득한 뇌: {currentBrains}</div>
              <div>샷건 횟수: {currentShotguns}/3</div>
            </div>
          </div>

          <div className="dice-area mb-3">
            {isRolling ? (
              <div className="text-center">
                <p>주사위 굴리는 중...</p>
              </div>
            ) : (
              rolledResults.length > 0 ? (
                rolledResults.map((result, index) => (
                  <Dice 
                    key={index}
                    type={result.type}
                    result={result.result}
                    isRolling={isRolling}
                  />
                ))
              ) : (
                <button 
                  className="btn btn-primary"
                  onClick={handleRoll}
                  disabled={isRolling}
                >
                  {selectedDice.length === 0 ? "주사위 선택" : "주사위 굴리기"}
                </button>
              )
            )}
          </div>

          {rolledResults.length > 0 && !winner && !isRolling && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                계속하기
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                {currentShotguns >= 3 ? "턴 종료 (0점)" : "멈추기"}
              </button>
            </div>
          )}

          {currentShotguns >= 3 && (
            <div className="alert alert-danger mt-3">
              <strong>총이 3개 이상!</strong> 이번 턴에 획득한 뇌를 모두 잃었습니다. 턴을 종료해주세요.
            </div>
          )}
          
          <DicePoolStatus dicePool={dicePool} />
        </>
      )}
    </div>
  );
}

// Dice 컴포넌트는 그대로 유지

export default ZombieDice;
