  };

  // ê³„ì† êµ´ë¦¬ê¸°
  const handleContinue = () => {
    handleRoll();
  };

  // í„´ ì¢…ë£Œ
  const handleStop = () => {
    // ì„œë²„ì— í„´ ì¢…ë£Œ ìš”ì²­
    const score = currentShotguns >= 3 ? 0 : currentBrains;
    
    // ë¡œê·¸ ì¶”ê°€
    console.log('ì„œë²„ì— í„´ ì¢…ë£Œ ìš”ì²­:', {
      roomId,
      score
