      // ì£¼ì‚¬ìœ„ êµ´ë¦¼ ì´ë²¤íŠ¸ - ì„œë²„ì—ì„œ ê²°ê³¼ ë°›ê¸°
      window.socket.on('diceRolled', (data) => {
        // ë¡œê·¸ ì¶”ê°€
        console.log('ì„œë²„ë¡œë¶€í„° ì£¼ì‚¬ìœ„ ê²°ê³¼ ìˆ˜ì‹ :', data);
        
        const { results, brains, shotguns, footsteps, remainingDice } = data;
        
        setServerResults(results);
        setRolledResults(results);
        setCurrentBrains(currentBrains + brains);
        setCurrentShotguns(currentShotguns + shotguns);
        setDicePool(remainingDice);
        
        // ì£¼ì‚¬ìœ„ í’€ ì—…ë°ì´íŠ¸
        const updatedPool = [...dicePool];
