      });
      
      // í„´ ì¢…ë£Œ ì´ë²¤íŠ¸
      window.socket.on('turnEnded', (data) => {
        // ë¡œê·¸ ì¶”ê°€
        console.log('í„´ ì¢…ë£Œ ì´ë²¤íŠ¸ ìˆ˜ì‹ :', data);
        
        const { nextPlayer, scores } = data;
        
        // ì ìˆ˜ ì—…ë°ì´íŠ¸
        setScores(scores);
        
        // ìŠ¹ì í™•ì¸
        for (const [playerId, score] of Object.entries(scores)) {
          if (score >= WINNING_SCORE) {
            setWinner(players.find(p => p.id === playerId));
