          }
        }
        
        // ì£¼ì‚¬ìœ„ í’€ ì´ˆê¸°í™”
        resetDicePool();
        
        // í˜„ì¬ í„´ ì ìˆ˜ ì´ˆê¸°í™”
        setCurrentBrains(0);
        setCurrentShotguns(0);
        setRolledResults([]);
      });
      
      return () => {
        window.socket.off('gameState');
        window.socket.off('diceRolled');
        window.socket.off('turnEnded');
