import { useState, useEffect } from 'react';
import redDice from '../../assets/zombie-dice/dice/red.png';
import yellowDice from '../../assets/zombie-dice/dice/yellow.png';
import greenDice from '../../assets/zombie-dice/dice/green.png';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import '../styles/ZombieDice.css';

const DICE_TYPES = {
  RED: {
    image: redDice,
    faces: ['BRAIN', 'FOOTSTEPS', 'FOOTSTEPS', 'SHOTGUN', 'SHOTGUN', 'SHOTGUN']
  },
  YELLOW: {
    image: yellowDice,
    faces: ['BRAIN', 'BRAIN', 'FOOTSTEPS', 'FOOTSTEPS', 'SHOTGUN', 'SHOTGUN']
  },
  GREEN: {
    image: greenDice,
    faces: ['BRAIN', 'BRAIN', 'BRAIN', 'FOOTSTEPS', 'FOOTSTEPS', 'SHOTGUN']
  }
};

const FACE_IMAGES = {
  RED: {
    BRAIN: redBrain,
    FOOTSTEPS: redFootsteps,
    SHOTGUN: redShotgun
  },
  YELLOW: {
    BRAIN: yellowBrain,
    FOOTSTEPS: yellowFootsteps,
    SHOTGUN: yellowShotgun
  },
  GREEN: {
    BRAIN: greenBrain,
    FOOTSTEPS: greenFootsteps,
    SHOTGUN: greenShotgun
  }
};

// 상수 추가
const WINNING_SCORE = 13; // 승리 조건 점수

// 주사위 면 배열 수정
const DICE_FACES = {
  GREEN: [
    'BRAIN', 'BRAIN', 'BRAIN',    // 1, 2, 3
    'FOOTSTEPS', 'FOOTSTEPS',     // 4, 5
    'SHOTGUN'                     // 6
  ],
  YELLOW: [
    'BRAIN', 'BRAIN',             // 1, 2
    'FOOTSTEPS', 'FOOTSTEPS',     // 3, 4
    'SHOTGUN', 'SHOTGUN'          // 5, 6
  ],
  RED: [
    'BRAIN',                      // 1
    'FOOTSTEPS', 'FOOTSTEPS',     // 2, 3
    'SHOTGUN', 'SHOTGUN', 'SHOTGUN' // 4, 5, 6
  ]
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  const [dicePool, setDicePool] = useState([
    ...Array(3).fill('RED'),
    ...Array(4).fill('YELLOW'),
    ...Array(6).fill('GREEN')
  ]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [keptDice, setKeptDice] = useState([]);
  const [isRolling, setIsRolling] = useState(false);

  // 주사위 3개 랜덤 선택
  const pickDice = () => {
    const newDice = [];
    const tempPool = [...dicePool];
    
    for (let i = 0; i < 3; i++) {
      if (tempPool.length === 0) break;
      const randomIndex = Math.floor(Math.random() * tempPool.length);
      newDice.push(tempPool[randomIndex]);
      tempPool.splice(randomIndex, 1);
    }
    
    setDicePool(tempPool);
    setSelectedDice(newDice);
  };

  // 주사위 굴리기
  const rollDice = () => {
    const results = selectedDice.map(diceType => {
      const faces = DICE_FACES[diceType];
      const randomIndex = Math.floor(Math.random() * 6);
      return {
        type: diceType,
        result: faces[randomIndex]
      };
    });
    
    setRolledResults(results);
    
    // 결과 계산
    let newBrains = currentBrains;
    let newShotguns = currentShotguns;
    
    results.forEach(({ result, type }) => {
      if (result === 'BRAIN') newBrains++;
      if (result === 'SHOTGUN') newShotguns++;
      if (result === 'FOOTSTEPS') {
        // 도망간 주사위는 다시 풀에 넣음
        setDicePool(prev => [...prev, type]);
      }
    });

    setCurrentBrains(newBrains);
    setCurrentShotguns(newShotguns);

    // 샷건이 3개 이상이면 자동으로 턴 종료
    if (newShotguns >= 3) {
      setTimeout(() => {
        onTurnEnd(0);
      }, 1000);
    }
  };

  // 계속 진행
  const handleContinue = () => {
    setRolledResults([]);
    // 도망간 주사위를 제외한 나머지는 보관
    const keptDice = rolledResults
      .filter(result => result.result !== 'FOOTSTEPS')
      .map(result => result.type);
    
    // 새로운 주사위 선택
    pickDice();
  };

  // 턴 종료
  const handleStop = () => {
    const finalScore = currentBrains;
    // 주사위 풀 초기화
    setDicePool([
      ...Array(3).fill('RED'),
      ...Array(4).fill('YELLOW'),
      ...Array(6).fill('GREEN')
    ]);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    setSelectedDice([]);
    onTurnEnd(finalScore);
  };

  // 점수판 렌더링
  const renderScoreBoard = () => (
    <div className="score-board">
      <table>
        <thead>
          <tr>
            <th>플레이어</th>
            <th>점수</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => (
            <tr 
              key={player.nickname}
              className={index === currentTurn ? 'current-player' : ''}
            >
              <td>{player.nickname}</td>
              <td>{player.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  // 주사위 굴리기 처리
  const handleRoll = () => {
    setIsRolling(true);
    setTimeout(() => {
      rollDice();
      setIsRolling(false);
    }, 1500);
  };

  // 승자가 있는지 확인
  const winner = players.find(player => player.score >= WINNING_SCORE);

  useEffect(() => {
    if (isMyTurn) {
      pickDice();
    }
  }, [isMyTurn]);

  // Dice 컴포넌트 수정
  function Dice({ type, result, isRolling }) {
    const [showResult, setShowResult] = useState(false);
    const [rotation, setRotation] = useState({ x: 0, y: 0, z: 0 });

    useEffect(() => {
      if (isRolling) {
        setShowResult(false);
        // 랜덤한 회전 애니메이션 시작
        const interval = setInterval(() => {
          setRotation({
            x: Math.random() * 360,
            y: Math.random() * 360,
            z: Math.random() * 360
          });
        }, 50);

        // 애니메이션 종료 후 결과 표시
        setTimeout(() => {
          clearInterval(interval);
          setShowResult(true);
        }, 2000);

        return () => clearInterval(interval);
      }
    }, [isRolling]);

    return (
      <div className="dice-container">
        <div 
          className={`dice ${isRolling ? 'rolling' : ''}`}
          style={{
            transform: `rotateX(${rotation.x}deg) rotateY(${rotation.y}deg) rotateZ(${rotation.z}deg)`
          }}
        >
          {/* 앞면 */}
          <div className="dice-face front">
            {showResult && (
              <img 
                src={FACE_IMAGES[type][result]} 
                alt={result}
                className={`face-result ${showResult ? 'show' : ''}`}
              />
            )}
          </div>
          {/* 뒷면 */}
          <div className="dice-face back">
            <img src={FACE_IMAGES[type]['BRAIN']} alt="back" />
          </div>
          {/* 오른쪽 */}
          <div className="dice-face right">
            <img src={FACE_IMAGES[type]['SHOTGUN']} alt="right" />
          </div>
          {/* 왼쪽 */}
          <div className="dice-face left">
            <img src={FACE_IMAGES[type]['FOOTSTEPS']} alt="left" />
          </div>
          {/* 위 */}
          <div className="dice-face top">
            <img src={FACE_IMAGES[type]['BRAIN']} alt="top" />
          </div>
          {/* 아래 */}
          <div className="dice-face bottom">
            <img src={FACE_IMAGES[type]['SHOTGUN']} alt="bottom" />
          </div>
        </div>
      </div>
    );
  }

  // 주사위 색상 helper 함수
  function getDiceColor(type) {
    switch(type) {
      case 'RED': return '#ff6b6b';
      case 'YELLOW': return '#ffd93d';
      case 'GREEN': return '#51cf66';
      default: return 'white';
    }
  }

  if (!isMyTurn) {
    return (
      <div className="zombie-dice">
        {renderScoreBoard()}
        <div className="text-center">
          <h3>{players[currentTurn].nickname}님의 차례입니다</h3>
        </div>
      </div>
    );
  }

  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>🏆 {winner.nickname}님이 승리하셨습니다! 🏆</h3>
        </div>
      ) : (
        <>
          <div className="current-status mb-3">
            <div className="d-flex justify-content-around">
              <div>획득한 뇌: {currentBrains}</div>
              <div>샷건 횟수: {currentShotguns}/3</div>
            </div>
          </div>

          <div className="dice-area mb-3">
            {selectedDice.length > 0 && !rolledResults.length ? (
              <button 
                className="btn btn-primary"
                onClick={handleRoll}
                disabled={isRolling}
              >
                {isRolling ? '주사위 굴리는 중...' : '주사위 굴리기'}
              </button>
            ) : (
              rolledResults.map((result, index) => (
                <Dice 
                  key={index}
                  type={result.type}
                  result={result.result}
                  isRolling={isRolling}
                />
              ))
            )}
          </div>

          {rolledResults.length > 0 && !winner && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                계속하기
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                멈추기
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default ZombieDice;
