import { useState, useEffect } from 'react';
import '../../styles/ZombieDice.css';

// 상수 정의
const WINNING_SCORE = 13;  // 승리 점수
const INITIAL_DICE_POOL = {
  RED: 3,
  YELLOW: 4,
  GREEN: 6
};

const ZombieDice = ({ players, isHost, roomId }) => {
  // 게임 상태 관리
  const [gameStarted, setGameStarted] = useState(false);
  const [currentPlayerIndex, setCurrentPlayerIndex] = useState(null);
  const [dicePool, setDicePool] = useState(INITIAL_DICE_POOL);
  const [rolledResults, setRolledResults] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const [playerOrder, setPlayerOrder] = useState([]);
  const [scores, setScores] = useState({});
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [winner, setWinner] = useState(null);

  // 소켓 참조
  const socket = window.socket;

  // 로그 출력 함수
  const log = (message, data) => {
    if (data) {
      console.log(`[좀비다이스 클라이언트] ${message}`, data);
    } else {
      console.log(`[좀비다이스 클라이언트] ${message}`);
    }
  };

  // 게임 시작 처리
  const handleGameStart = () => {
    if (!socket) return;

    // 플레이어 목록을 랜덤하게 섞기
    const shuffledPlayers = [...players].sort(() => Math.random() - 0.5);

    const order = shuffledPlayers.map((player, index) => ({
      id: player.id,
      order: index + 1
    }));

    setPlayerOrder(order);
    setCurrentPlayerIndex(0);
    setGameStarted(true);

    socket.emit('gameStarted', { roomId, order });
    log('게임이 시작되었습니다.', order);
  };

  // 게임 이벤트 리스너 설정
  useEffect(() => {
    if (!socket) {
      log('소켓 연결이 없습니다');
      return;
    }

    // 게임 시작 이벤트 수신
    socket.on('gameStarted', (data) => {
      log('게임 시작 이벤트 수신:', data);
      setPlayerOrder(data.order);
      // 시작 플레이어를 1번 플레이어로 설정
      const startingPlayerIndex = data.order.findIndex(player => player.order === 1);
      if (startingPlayerIndex !== -1) {
        setCurrentPlayerIndex(startingPlayerIndex);
      } else {
        log('시작 플레이어를 찾을 수 없습니다.');
      }
      setGameStarted(true);
    });

    // 주사위 굴림 결과 수신
    socket.on('diceRolled', (data) => {
      log('주사위 굴림 결과 수신:', data);
      setRolledResults(data.results);
      setDicePool(data.updatedPool);
      setIsRolling(false);
    });

    // 턴 변경 이벤트 수신
    socket.on('turnChanged', (data) => {
      log('턴 변경 이벤트 수신:', data);

      const { nextPlayerIndex } = data;

      // 점수 업데이트
      setScores(prevScores => ({
        ...prevScores,
        [players[nextPlayerIndex].id]: prevScores[players[nextPlayerIndex].id] + currentBrains
      }));

      // 다음 플레이어 설정
      setCurrentPlayerIndex(nextPlayerIndex);

      // 턴 초기화
      resetTurn();
    });

    // 정리 함수
    return () => {
      socket.off('gameStarted');
      socket.off('diceRolled');
      socket.off('turnChanged');
    };
  }, [socket, players]);

  // 턴 초기화
  const resetTurn = () => {
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    log('턴 초기화');
  };

  // 주사위 굴리기
  const handleRollDice = () => {
    if (!socket || isRolling || Object.values(dicePool).reduce((a, b) => a + b) < 3) return;

    log('주사위 굴리기 요청');

    // 주사위 굴림 상태 설정
    setIsRolling(true);

    // 서버에 주사위 굴리기 요청
    socket.emit('rollDice', {
      roomId,
      dicePool
    });
  };

  // 턴 종료
  const handleEndTurn = () => {
    if (!socket || isRolling) return;

    log('턴 종료');

    // 점수 업데이트
    if (currentShotguns < 3) {
      const currentPlayerId = players[currentPlayerIndex].id;
      setScores(prevScores => ({
        ...prevScores,
        [currentPlayerId]: prevScores[currentPlayerId] + currentBrains
      }));
    }

    // 다음 플레이어로 턴 변경
    setCurrentPlayerIndex((prevIndex) => (prevIndex + 1) % players.length);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setDicePool(INITIAL_DICE_POOL);
    setRolledResults([]);

    // 서버에 턴 종료 요청
    socket.emit('endTurn', {
      roomId,
      keepScore: currentShotguns < 3
    });
  };

  function startGame() {
    if (allPlayersReady()) {
      console.log("모든 플레이어가 준비되었습니다. 게임을 시작합니다.");
      // 서버로 게임 시작 요청 보내기
      socket.emit('startGame');
    } else {
      console.log("모든 플레이어가 준비되지 않았습니다.");
    }
  }

  function rollDice() {
    console.log("주사위 굴리기 요청을 보냅니다.");
    socket.emit('rollDice');
  }

  socket.on('diceResult', (data) => {
    console.log("주사위 결과를 받았습니다:", data);
    // 화면에 주사위 결과 표시
    displayDiceResult(data.result);
  });

  function playerChoice(continueRolling) {
    if (continueRolling) {
      console.log("플레이어가 계속 굴리기를 선택했습니다.");
      rollDice();
    } else {
      console.log("플레이어가 멈추기를 선택했습니다.");
      // 다음 플레이어로 턴 넘기기 로직
    }
  }

  return (
    <div className="zombie-dice">
      {gameStarted ? (
        <div>
          <div className="score-board mb-4">
            <h3 className="text-center mb-3">점수판</h3>
            <table className="table">
              <thead>
                <tr>
                  <th>플레이어</th>
                  <th>순서</th>
                </tr>
              </thead>
              <tbody>
                {playerOrder.map((player, index) => (
                  <tr key={player.id} className={currentPlayerIndex === index ? 'table-active' : ''}>
                    <td>{players.find(p => p.id === player.id).nickname} {player.id === socket.id && '(나)'}</td>
                    <td>{player.order}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="dice-area">
            {rolledResults.length > 0 && (
              <div className="rolled-results mb-3">
                <h4>주사위 결과:</h4>
                <div className="dice-results-container">
                  {rolledResults.map((result, index) => (
                    <div 
                      key={index} 
                      className={`dice-result result-${result.toLowerCase()}`}
                    >
                      <div className={`dice-type ${result.toLowerCase()}`}></div>
                      <img 
                        src={`/zombie-dice/faces/${result.toLowerCase()}.png`} 
                        alt={`${result}`}
                        className="dice-result-image"
                      />
                      <div className="dice-result-text">{result}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {currentShotguns >= 3 && (
              <div className="alert alert-danger">
                <strong>경고:</strong> 총이 3개 이상 발견되었습니다! 턴을 종료해야 합니다.
              </div>
            )}

            <div className="dice-pool-status mb-3">
              <h4>남은 주사위:</h4>
              <div className="dice-counts">
                <div className="dice-count">
                  <div className="dice-sample red"></div>
                  <span>빨간 주사위: {dicePool.RED}개</span>
                </div>
                <div className="dice-count">
                  <div className="dice-sample yellow"></div>
                  <span>노란 주사위: {dicePool.YELLOW}개</span>
                </div>
                <div className="dice-count">
                  <div className="dice-sample green"></div>
                  <span>녹색 주사위: {dicePool.GREEN}개</span>
                </div>
              </div>
            </div>

            <div className="controls">
              {players[currentPlayerIndex]?.id === socket.id ? (
                <>
                  <button 
                    className="btn btn-primary" 
                    onClick={handleRollDice}
                    disabled={isRolling || Object.values(dicePool).reduce((a, b) => a + b) < 3}
                  >
                    주사위 굴리기
                  </button>
                  <button 
                    className="btn btn-warning" 
                    onClick={handleEndTurn}
                    disabled={isRolling}
                  >
                    멈추기
                  </button>
                </>
              ) : (
                <p>다른 플레이어의 차례입니다...</p>
              )}
            </div>
          </div>
        </div>
      ) : (
        isHost && (
          <div className="text-center mb-4">
            <button 
              className="btn btn-primary btn-lg"
              onClick={handleGameStart}
            >
              게임 시작
            </button>
          </div>
        )
      )}
    </div>
  );
};

export default ZombieDice;
