import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Room({ nickname }) {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [players, setPlayers] = useState([]);

  useEffect(() => {
    joinRoom();
    return () => leaveRoom();
  }, []);

  const joinRoom = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/join`, { nickname });
    } catch (error) {
      console.error('방 참가에 실패했습니다:', error);
      navigate('/lobby');
    }
  };

  const leaveRoom = async () => {
    try {
      await axios.post(`/api/rooms/${roomId}/leave`, { nickname });
    } catch (error) {
      console.error('방 나가기에 실패했습니다:', error);
    }
  };

  const handleLeave = () => {
    leaveRoom();
    navigate('/lobby');
  };

  return (
    <div className="room-container">
      <h1>방 #{roomId}</h1>
      <div className="players-list">
        {players.map(player => (
          <div key={player} className="player-item">
            {player}
          </div>
        ))}
      </div>
      <button onClick={handleLeave}>나가기</button>
    </div>
  );
}

export default Room;
