import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Lobby({ nickname }) {
  const [rooms, setRooms] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchRooms();
    const interval = setInterval(fetchRooms, 3000);
    return () => clearInterval(interval);
  }, []);

  const fetchRooms = async () => {
    try {
      const response = await axios.get('/api/rooms');
      setRooms(response.data);
    } catch (error) {
      console.error('방 목록을 가져오는데 실패했습니다:', error);
    }
  };

  const createRoom = async () => {
    try {
      const response = await axios.post('/api/rooms', { creator: nickname });
      navigate(`/room/${response.data.roomId}`);
    } catch (error) {
      console.error('방 생성에 실패했습니다:', error);
    }
  };

  const joinRoom = (roomId) => {
    navigate(`/room/${roomId}`);
  };

  return (
    <div className="lobby-container">
      <h1>게임 로비</h1>
      <button onClick={createRoom}>방 만들기</button>
      <div className="rooms-list">
        {rooms.map(room => (
          <div key={room.id} className="room-item">
            <span>방 #{room.id}</span>
            <span>참가자: {room.players.length}명</span>
            <button onClick={() => joinRoom(room.id)}>참가하기</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Lobby;
