import { useState, useEffect, useRef, useCallback } from 'react';
import * as THREE from 'three';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import redBrain from '../../assets/zombie-dice/faces/red-brain.png';
import redFootsteps from '../../assets/zombie-dice/faces/red-footsteps.png';
import redShotgun from '../../assets/zombie-dice/faces/red-shotgun.png';
import yellowBrain from '../../assets/zombie-dice/faces/yellow-brain.png';
import yellowFootsteps from '../../assets/zombie-dice/faces/yellow-footsteps.png';
import yellowShotgun from '../../assets/zombie-dice/faces/yellow-shotgun.png';
import greenBrain from '../../assets/zombie-dice/faces/green-brain.png';
import greenFootsteps from '../../assets/zombie-dice/faces/green-footsteps.png';
import greenShotgun from '../../assets/zombie-dice/faces/green-shotgun.png';
import '../styles/ZombieDice.css';

// ìŠ¹ë¦¬ ì ìˆ˜
const WINNING_SCORE = 13;

// ì£¼ì‚¬ìœ„ ë©´ ì •ì˜
const DICE_FACES = {
  RED: {
    faces: [
      { image: redBrain, type: 'BRAIN' },      // 1ë©´
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 2ë©´
      { image: redFootsteps, type: 'FOOTSTEPS' }, // 3ë©´
      { image: redShotgun, type: 'SHOTGUN' },   // 4ë©´
      { image: redShotgun, type: 'SHOTGUN' },   // 5ë©´
      { image: redShotgun, type: 'SHOTGUN' }    // 6ë©´
    ]
  },
  YELLOW: {
    faces: [
      { image: yellowBrain, type: 'BRAIN' },     // 1ë©´
      { image: yellowBrain, type: 'BRAIN' },     // 2ë©´
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 3ë©´
      { image: yellowFootsteps, type: 'FOOTSTEPS' }, // 4ë©´
      { image: yellowShotgun, type: 'SHOTGUN' },  // 5ë©´
      { image: yellowShotgun, type: 'SHOTGUN' }   // 6ë©´
    ]
  },
  GREEN: {
    faces: [
      { image: greenBrain, type: 'BRAIN' },     // 1ë©´
      { image: greenBrain, type: 'BRAIN' },     // 2ë©´
      { image: greenBrain, type: 'BRAIN' },     // 3ë©´
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 4ë©´
      { image: greenFootsteps, type: 'FOOTSTEPS' }, // 5ë©´
      { image: greenShotgun, type: 'SHOTGUN' }   // 6ë©´
    ]
  }
};

function ZombieDice({ players, currentTurn, onTurnEnd, isMyTurn }) {
  const [dicePool, setDicePool] = useState([
    ...Array(3).fill('RED'),
    ...Array(4).fill('YELLOW'),
    ...Array(6).fill('GREEN')
  ]);
  const [selectedDice, setSelectedDice] = useState([]);
  const [rolledResults, setRolledResults] = useState([]);
  const [currentBrains, setCurrentBrains] = useState(0);
  const [currentShotguns, setCurrentShotguns] = useState(0);
  const [keptDice, setKeptDice] = useState([]);
  const [isRolling, setIsRolling] = useState(false);
  const diceContainerRef = useRef(null);

  // ì£¼ì‚¬ìœ„ 3ê°œ ëœë¤ ì„ íƒ
  const pickDice = () => {
    const newDice = [];
    const tempPool = [...dicePool];
    
    for (let i = 0; i < 3; i++) {
      if (tempPool.length === 0) break;
      const randomIndex = Math.floor(Math.random() * tempPool.length);
      newDice.push(tempPool[randomIndex]);
      tempPool.splice(randomIndex, 1);
    }
    
    setDicePool(tempPool);
    setSelectedDice(newDice);
  };

  // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸°
  const handleRoll = () => {
    rollDiceWithSelected(selectedDice, dicePool);
  };

  // ê²°ê³¼ ì²˜ë¦¬ í•¨ìˆ˜ ìˆ˜ì •
  const processResults = (results) => {
    // ê²°ê³¼ ê³„ì‚° - ì´ì „ ê°’ì— ëˆ„ì 
    let newBrains = currentBrains;
    let newShotguns = currentShotguns;
    
    results.forEach(({ result, type }) => {
      if (result === 'BRAIN') newBrains++;
      if (result === 'SHOTGUN') newShotguns++;
      // ë°œìêµ­ì€ ì²˜ë¦¬í•˜ì§€ ì•ŠìŒ
    });

    // ìƒíƒœ ì—…ë°ì´íŠ¸
    setCurrentBrains(newBrains);
    setCurrentShotguns(newShotguns);
    
    // ì´ì´ 3ê°œ ì´ìƒì´ë©´ í„´ ì¢…ë£Œ
    if (newShotguns >= 3) {
      setTimeout(() => {
        onTurnEnd(0); // ì´ 3ê°œ ì´ìƒì´ë©´ ì ìˆ˜ 0
      }, 1000);
    }
  };

  // ê²Œì„ ìƒíƒœ ì´ˆê¸°í™” í•¨ìˆ˜ ìˆ˜ì •
  const resetGameState = () => {
    console.log('ê²Œì„ ìƒíƒœ ì´ˆê¸°í™”');
    // ì£¼ì‚¬ìœ„ í’€ ì´ˆê¸°í™”
    setDicePool([
      ...Array(3).fill('RED'),
      ...Array(4).fill('YELLOW'),
      ...Array(6).fill('GREEN')
    ]);
    setCurrentBrains(0);
    setCurrentShotguns(0);
    setRolledResults([]);
    setSelectedDice([]);
  };

  // í„´ ì¢…ë£Œ í•¨ìˆ˜ ìˆ˜ì •
  const handleStop = () => {
    const finalScore = currentBrains;
    
    // ì ìˆ˜ ì „ë‹¬í•˜ê³  í„´ ì¢…ë£Œ
    onTurnEnd(finalScore);
    
    // í„´ ì¢…ë£Œ í›„ ìƒíƒœ ì´ˆê¸°í™”ëŠ” ì„œë²„ì—ì„œ gameStateUpdated ì´ë²¤íŠ¸ë¥¼ í†µí•´ ì²˜ë¦¬ë¨
    // ì—¬ê¸°ì„œëŠ” ì´ˆê¸°í™”í•˜ì§€ ì•ŠìŒ
  };

  // ê³„ì† ì§„í–‰ í•¨ìˆ˜ ìˆ˜ì •
  const handleContinue = () => {
    // ë„ë§ê°„ ì£¼ì‚¬ìœ„ ì°¾ê¸°
    const footstepsDice = rolledResults
      .filter(result => result.result === 'FOOTSTEPS')
      .map(result => result.type);
    
    // ìƒˆë¡œìš´ ì£¼ì‚¬ìœ„ 3ê°œ ì„ íƒ
    const newSelectedDice = [];
    let tempPool = [...dicePool];
    
    // ì£¼ì‚¬ìœ„ í’€ì´ 3ê°œ ì´í•˜ì¸ ê²½ìš° í’€ ì´ˆê¸°í™”
    if (tempPool.length < 3) {
      console.log('ì£¼ì‚¬ìœ„ í’€ ë¶€ì¡±, í’€ ì´ˆê¸°í™”');
      
      // ì£¼ì‚¬ìœ„ í’€ ì´ˆê¸°í™”
      tempPool = [
        ...Array(3).fill('RED'),
        ...Array(4).fill('YELLOW'),
        ...Array(6).fill('GREEN')
      ];
      
      // ë„ë§ê°„ ì£¼ì‚¬ìœ„ ì œì™¸
      footstepsDice.forEach(diceType => {
        const index = tempPool.indexOf(diceType);
        if (index !== -1) {
          tempPool.splice(index, 1);
        }
      });
    }
    
    // 3ê°œì˜ ìƒˆ ì£¼ì‚¬ìœ„ ì„ íƒ
    for (let i = 0; i < 3; i++) {
      if (tempPool.length === 0) break;
      const randomIndex = Math.floor(Math.random() * tempPool.length);
      newSelectedDice.push(tempPool[randomIndex]);
      tempPool.splice(randomIndex, 1);
    }
    
    // ë„ë§ê°„ ì£¼ì‚¬ìœ„ì™€ ìƒˆ ì£¼ì‚¬ìœ„ í•©ì¹˜ê¸°
    const allDiceToRoll = [...footstepsDice, ...newSelectedDice];
    
    // ì£¼ì‚¬ìœ„ í’€ ì—…ë°ì´íŠ¸
    setDicePool(tempPool);
    
    // ì´ì „ ê²°ê³¼ ì´ˆê¸°í™” - ë„ë§ê°„ ì£¼ì‚¬ìœ„ëŠ” ìƒˆë¡œ êµ´ë¦´ ê²ƒì´ë¯€ë¡œ í‘œì‹œí•  í•„ìš” ì—†ìŒ
    setRolledResults([]);
    
    // ì¦‰ì‹œ ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸°
    setTimeout(() => {
      rollDiceWithSelected(allDiceToRoll, tempPool);
    }, 100);
  };

  // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° í•¨ìˆ˜ ìˆ˜ì •
  const rollDiceWithSelected = (selectedDiceToRoll, currentPool) => {
    setIsRolling(true);
    
    // ì„œë²„ì— ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìš”ì²­
    if (window.socket) {
      // Room ì»´í¬ë„ŒíŠ¸ì—ì„œ roomId ê°€ì ¸ì˜¤ê¸°
      const roomId = window.location.pathname.split('/')[2]; // URLì—ì„œ roomId ì¶”ì¶œ
      
      console.log('ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ìš”ì²­ ì „ì†¡:', {
        action: 'rollDice',
        roomId,
        selectedDice: selectedDiceToRoll,
        dicePool: currentPool,
        currentBrains, // í˜„ì¬ê¹Œì§€ ëˆ„ì ëœ ë‡Œ ê°œìˆ˜
        currentShotguns // í˜„ì¬ê¹Œì§€ ëˆ„ì ëœ ì´ ê°œìˆ˜
      });
      
      window.socket.emit('gameAction', {
        action: 'rollDice',
        roomId,
        selectedDice: selectedDiceToRoll,
        dicePool: currentPool,
        currentBrains: currentBrains,
        currentShotguns: currentShotguns
      });
    } else {
      console.error('ì†Œì¼“ ì—°ê²°ì´ ì—†ìŠµë‹ˆë‹¤!');
      setIsRolling(false);
    }
    
    // ì„œë²„ ì‘ë‹µì´ ì—†ì„ ê²½ìš°ë¥¼ ëŒ€ë¹„í•œ íƒ€ì„ì•„ì›ƒ
    setTimeout(() => {
      if (isRolling) {
        console.error('ì„œë²„ ì‘ë‹µ íƒ€ì„ì•„ì›ƒ');
        setIsRolling(false);
      }
    }, 5000);
  };

  // ì ìˆ˜íŒ ë Œë”ë§
  const renderScoreBoard = () => (
    <div className="score-board">
      <table>
        <thead>
          <tr>
            <th>í”Œë ˆì´ì–´</th>
            <th>ì ìˆ˜</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => (
            <tr 
              key={player.nickname}
              className={index === currentTurn ? 'current-player' : ''}
            >
              <td>{player.nickname}</td>
              <td>{player.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  // ìŠ¹ìê°€ ìˆëŠ”ì§€ í™•ì¸
  const winner = players.find(player => player.score >= WINNING_SCORE);

  // ì†Œì¼“ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ìˆ˜ì •
  useEffect(() => {
    if (window.socket) {
      console.log('ì†Œì¼“ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ë“±ë¡');
      
      // ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸° ê²°ê³¼ ìˆ˜ì‹ 
      const handleGameAction = (data) => {
        console.log('ê²Œì„ ì•¡ì…˜ ìˆ˜ì‹ :', data);
        
        if (data.action === 'rollDice') {
          console.log('ì£¼ì‚¬ìœ„ ê²°ê³¼ ìˆ˜ì‹ :', data.results);
          
          // ê²°ê³¼ í‘œì‹œ - ìƒˆ ê²°ê³¼ë¡œ ì„¤ì •
          setRolledResults(data.results);
          
          // ì• ë‹ˆë©”ì´ì…˜ í›„ ê²°ê³¼ ì²˜ë¦¬
          setTimeout(() => {
            processResults(data.results);
            setIsRolling(false);
          }, 2500);
        }
      };
      
      // ê²Œì„ ìƒíƒœ ì—…ë°ì´íŠ¸ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì¶”ê°€
      const handleGameStateUpdated = (gameState) => {
        console.log('ê²Œì„ ìƒíƒœ ì—…ë°ì´íŠ¸:', gameState);
        
        // í˜„ì¬ í„´ì´ ë³€ê²½ë˜ì—ˆëŠ”ì§€ í™•ì¸
        const isMyTurnChanged = gameState.currentTurn !== currentTurn;
        
        if (isMyTurnChanged) {
          // í„´ì´ ë³€ê²½ë˜ì—ˆìœ¼ë©´ ê²Œì„ ìƒíƒœ ì´ˆê¸°í™”
          resetGameState();
        } else {
          // í„´ì´ ë³€ê²½ë˜ì§€ ì•Šì•˜ìœ¼ë©´ ì£¼ì‚¬ìœ„ í’€ë§Œ ì—…ë°ì´íŠ¸
          if (gameState.dicePool) {
            setDicePool(gameState.dicePool);
          }
        }
      };
      
      window.socket.on('gameAction', handleGameAction);
      window.socket.on('gameStateUpdated', handleGameStateUpdated);
      
      return () => {
        console.log('ì†Œì¼“ ì´ë²¤íŠ¸ ë¦¬ìŠ¤ë„ˆ ì œê±°');
        window.socket.off('gameAction', handleGameAction);
        window.socket.off('gameStateUpdated', handleGameStateUpdated);
      };
    }
  }, [currentTurn]);

  // ì»´í¬ë„ŒíŠ¸ ë§ˆìš´íŠ¸/ì–¸ë§ˆìš´íŠ¸ ì‹œ ìƒíƒœ ì´ˆê¸°í™” ìˆ˜ì •
  useEffect(() => {
    // ë‚´ í„´ì´ ì‹œì‘ë  ë•Œë§Œ ì´ˆê¸°í™”
    if (isMyTurn) {
      // ì´ì „ì— ë‚´ í„´ì´ ì•„ë‹ˆì—ˆë‹¤ë©´ ì´ˆê¸°í™”
      if (!prevIsMyTurnRef.current) {
        resetGameState();
        pickDice();
      }
    }
    
    // í˜„ì¬ í„´ ìƒíƒœ ì €ì¥
    prevIsMyTurnRef.current = isMyTurn;
  }, [isMyTurn]);

  // ì´ì „ í„´ ìƒíƒœ ì €ì¥ìš© ref ì¶”ê°€
  const prevIsMyTurnRef = useRef(false);

  // Three.jsë¥¼ ì‚¬ìš©í•œ ì£¼ì‚¬ìœ„ ì»´í¬ë„ŒíŠ¸
  function Dice({ type, result, isRolling }) {
    const diceRef = useRef(null);
    const animationRef = useRef(null);

    useEffect(() => {
      // ì´ì „ ìº”ë²„ìŠ¤ ì œê±°
      while (diceRef.current && diceRef.current.firstChild) {
        diceRef.current.removeChild(diceRef.current.firstChild);
      }

      // ì”¬ ì„¤ì •
      const scene = new THREE.Scene();
      scene.background = new THREE.Color(0xf0f0f0);
      
      // ì¹´ë©”ë¼ ì„¤ì •
      const camera = new THREE.PerspectiveCamera(75, 1, 0.1, 1000);
      camera.position.z = 2;
      
      // ë Œë”ëŸ¬ ì„¤ì •
      const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
      renderer.setSize(150, 150);
      renderer.setPixelRatio(window.devicePixelRatio);
      if (diceRef.current) {
        diceRef.current.appendChild(renderer.domElement);
      }

      // ì¡°ëª… ì¶”ê°€
      const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
      scene.add(ambientLight);
      
      const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
      directionalLight.position.set(1, 1, 1);
      scene.add(directionalLight);

      // í…ìŠ¤ì²˜ ë¡œë” ìƒì„±
      const textureLoader = new THREE.TextureLoader();
      
      // ê²°ê³¼ íƒ€ì…ì— ë”°ë¥¸ ë©´ ë§¤í•‘
      const resultIndex = DICE_FACES[type].faces.findIndex(face => face.type === result);
      
      // ì£¼ì‚¬ìœ„ ë©´ ìˆœì„œ: +x, -x, +y, -y, +z, -z
      // ê° ë©´ì— ë§ëŠ” í…ìŠ¤ì²˜ ë§¤í•‘
      const materials = [];
      
      // ì£¼ì‚¬ìœ„ íƒ€ì…ì— ë”°ë¥¸ ë©´ ì´ë¯¸ì§€ ì„¤ì •
      let brainImage, footstepsImage, shotgunImage;
      
      switch(type) {
        case 'RED':
          brainImage = redBrain;
          footstepsImage = redFootsteps;
          shotgunImage = redShotgun;
          break;
        case 'YELLOW':
          brainImage = yellowBrain;
          footstepsImage = yellowFootsteps;
          shotgunImage = yellowShotgun;
          break;
        case 'GREEN':
          brainImage = greenBrain;
          footstepsImage = greenFootsteps;
          shotgunImage = greenShotgun;
          break;
      }
      
      // ê²°ê³¼ì— ë”°ë¼ ìœ„ìª½ ë©´ ì„¤ì •
      let topFaceImage;
      switch(result) {
        case 'BRAIN':
          topFaceImage = brainImage;
          break;
        case 'FOOTSTEPS':
          topFaceImage = footstepsImage;
          break;
        case 'SHOTGUN':
          topFaceImage = shotgunImage;
          break;
      }
      
      // ê° ë©´ì— í…ìŠ¤ì²˜ ì ìš©
      // ìˆœì„œ: +x, -x, +y, -y, +z(ìœ„), -z(ì•„ë˜)
      const faceImages = [
        brainImage,       // +x
        footstepsImage,   // -x
        shotgunImage,     // +y
        brainImage,       // -y
        topFaceImage,     // +z (ìœ„ìª½ ë©´ - ê²°ê³¼ì— ë”°ë¼ ë³€ê²½)
        footstepsImage    // -z
      ];
      
      // ê° ë©´ì— í…ìŠ¤ì²˜ ì ìš©
      faceImages.forEach(image => {
        const texture = textureLoader.load(image);
        texture.colorSpace = THREE.SRGBColorSpace;
        materials.push(new THREE.MeshStandardMaterial({ 
          map: texture,
          roughness: 0.3,
          metalness: 0.2
        }));
      });
      
      // ì£¼ì‚¬ìœ„ ì§€ì˜¤ë©”íŠ¸ë¦¬ ìƒì„±
      const geometry = new THREE.BoxGeometry(1, 1, 1);
      const dice = new THREE.Mesh(geometry, materials);
      scene.add(dice);
      
      // ì´ˆê¸° íšŒì „ ì„¤ì •
      dice.rotation.set(
        Math.random() * Math.PI,
        Math.random() * Math.PI,
        Math.random() * Math.PI
      );
      
      // ì• ë‹ˆë©”ì´ì…˜ í•¨ìˆ˜
      const animate = () => {
        animationRef.current = requestAnimationFrame(animate);
        
        if (isRolling) {
          // êµ´ëŸ¬ê°€ëŠ” ì• ë‹ˆë©”ì´ì…˜
          dice.rotation.x += 0.1;
          dice.rotation.y += 0.15;
          dice.rotation.z += 0.05;
        } else {
          // ê²°ê³¼ì— ë”°ë¥¸ íšŒì „ ì„¤ì • - í•­ìƒ +z ë©´ì´ ìœ„ë¡œ ì˜¤ë„ë¡
          const targetX = 0;
          const targetY = 0;
          const targetZ = 0;
          
          // ë¶€ë“œëŸ¬ìš´ íšŒì „ ì• ë‹ˆë©”ì´ì…˜
          dice.rotation.x += (targetX - dice.rotation.x) * 0.1;
          dice.rotation.y += (targetY - dice.rotation.y) * 0.1;
          dice.rotation.z += (targetZ - dice.rotation.z) * 0.1;
        }
        
        renderer.render(scene, camera);
      };
      
      animate();
      
      // í´ë¦°ì—…
      return () => {
        cancelAnimationFrame(animationRef.current);
        if (diceRef.current && renderer.domElement) {
          diceRef.current.removeChild(renderer.domElement);
        }
        geometry.dispose();
        materials.forEach(material => material.dispose());
        renderer.dispose();
      };
    }, [isRolling, result, type]);
    
    return (
      <div className="dice-result-container">
        <div ref={diceRef} className="dice-3d-container"></div>
        <div className="dice-result-label">
          {result === 'BRAIN' && <span className="result-brain">ë‡Œ</span>}
          {result === 'SHOTGUN' && <span className="result-shotgun">ì´</span>}
          {result === 'FOOTSTEPS' && <span className="result-footsteps">ë°œìêµ­</span>}
        </div>
      </div>
    );
  }

  if (!isMyTurn) {
    return (
      <div className="zombie-dice">
        {renderScoreBoard()}
        <div className="text-center mb-3">
          <h3>{players[currentTurn].nickname}ë‹˜ì˜ ì°¨ë¡€ì…ë‹ˆë‹¤</h3>
        </div>
        
        {rolledResults.length > 0 && (
          <>
            <div className="current-status mb-3">
              <div className="d-flex justify-content-around">
                <div>íšë“í•œ ë‡Œ: {currentBrains}</div>
                <div>ìƒ·ê±´ íšŸìˆ˜: {currentShotguns}/3</div>
              </div>
            </div>
            
            <div className="dice-area mb-3">
              {rolledResults.map((result, index) => (
                <Dice 
                  key={index}
                  type={result.type}
                  result={result.result}
                  isRolling={isRolling}
                />
              ))}
            </div>
          </>
        )}
      </div>
    );
  }

  return (
    <div className="zombie-dice">
      {renderScoreBoard()}
      
      {winner ? (
        <div className="alert alert-success text-center">
          <h3>ğŸ† {winner.nickname}ë‹˜ì´ ìŠ¹ë¦¬í•˜ì…¨ìŠµë‹ˆë‹¤! ğŸ†</h3>
        </div>
      ) : (
        <>
          <div className="text-center mb-3">
            <h3 className="my-turn-indicator">ë‹¹ì‹ ì˜ ì°¨ë¡€ì…ë‹ˆë‹¤!</h3>
          </div>
          
          <div className="current-status mb-3">
            <div className="d-flex justify-content-around">
              <div>íšë“í•œ ë‡Œ: {currentBrains}</div>
              <div>ìƒ·ê±´ íšŸìˆ˜: {currentShotguns}/3</div>
            </div>
          </div>

          <div className="dice-area mb-3">
            {isRolling ? (
              <div className="text-center">
                <p>ì£¼ì‚¬ìœ„ êµ´ë¦¬ëŠ” ì¤‘...</p>
              </div>
            ) : (
              rolledResults.length > 0 ? (
                rolledResults.map((result, index) => (
                  <Dice 
                    key={index}
                    type={result.type}
                    result={result.result}
                    isRolling={isRolling}
                  />
                ))
              ) : (
                selectedDice.length > 0 && (
                  <button 
                    className="btn btn-primary"
                    onClick={handleRoll}
                    disabled={isRolling}
                  >
                    ì£¼ì‚¬ìœ„ êµ´ë¦¬ê¸°
                  </button>
                )
              )
            )}
          </div>

          {rolledResults.length > 0 && !winner && !isRolling && (
            <div className="controls">
              <button 
                className="btn btn-success me-2"
                onClick={handleContinue}
                disabled={currentShotguns >= 3}
              >
                ê³„ì†í•˜ê¸°
              </button>
              <button 
                className="btn btn-warning"
                onClick={handleStop}
              >
                ë©ˆì¶”ê¸°
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default ZombieDice;
